import React, { useMemo, useState, useRef, useLayoutEffect } from 'react';
import '../styles/trendChart.css';


const COLORS = { G93:'#34c759', G95:'#007bff', G97:'#a259c1', Diesel:'#fd7e14', Kero:'#6c757d' };
const FUEL_OPTIONS = [
  { key:'G93', field:'g93', label:'G93' },
  { key:'G95', field:'g95', label:'G95' },
  { key:'G97', field:'g97', label:'G97' },
  { key:'Diesel', field:'diesel', label:'Diesel' },
  { key:'Kero', field:'kero', label:'Kero' }
];

const toNum = v => (v === '-' || v == null ? null : Number(v));

function parseDateSafe(fecha){
  const token = String(fecha || '').split(' ')[0];
  if(!token) return { d:new Date(0), label:'' };
  if(/^\d{4}-\d{2}-\d{2}$/.test(token)){
    const [y,m,d] = token.split('-');
    return { d:new Date(token), label:`${d.padStart(2,'0')}-${m.padStart(2,'0')}` };
  }
  if(/^\d{2}-\d{2}-\d{4}$/.test(token)){
    const [d,m,y] = token.split('-').map(s=>s.padStart(2,'0'));
    return { d:new Date(`${y}-${m}-${d}`), label:`${d}-${m}` };
  }
  const d = new Date(token);
  const dd = String(d.getDate()).padStart(2,'0');
  const mm = String(d.getMonth()+1).padStart(2,'0');
  return { d, label:`${dd}-${mm}` };
}

export default function TrendChart({ dataRows = [], maxPoints = 7, height = 160, width = 300 }) {
  const [fuelKey, setFuelKey] = useState('G93');
  const [hover, setHover] = useState(null); // { idx, xSvg, ySvg, value, label, xClient, yClient }
  const wrapRef = useRef(null);
  const [w, setW] = useState(width);

  // Ajuste al ancho real del contenedor (no agranda, solo encoge si es menor)
  useLayoutEffect(() => {
    if (!wrapRef.current) return;
    const update = () => {
      const cw = wrapRef.current.clientWidth;
      setW(Math.min(width, Math.max(260, cw))); // mínimo 260, tope en prop width
    };
    update();
    const ro = new ResizeObserver(update);
    ro.observe(wrapRef.current);
    return () => ro.disconnect();
  }, [width]);

  const points = useMemo(() => {
    const norm = dataRows
      .map(r => {
        const { d, label } = parseDateSafe(r.fecha);
        return { date:d, label, G93:toNum(r.g93), G95:toNum(r.g95), G97:toNum(r.g97), Diesel:toNum(r.diesel), Kero:toNum(r.kero) };
      })
      .sort((a,b)=>a.date-b.date)
      .slice(-maxPoints)
      .reverse(); // más reciente a la izquierda
    return norm;
  }, [dataRows, maxPoints]);

  const labels = points.map(p=>p.label);
  const values = points.map(p=>p[fuelKey]);
  const present = values.filter(v=>v!=null);
  const aMin = present.length ? Math.min(...present) : 0;
  const aMax = present.length ? Math.max(...present) : 1;

  // Eje Y desde 0
  const yMin = 0;
  const pad = Math.max(1, Math.round((aMax - aMin) * 0.05));
  const yMax = aMax <= 0 ? 1 : aMax + pad;

  // Layout compacto
  const margin = { top: 8, right: 10, bottom: 24, left: 50 };
  const innerW = w - margin.left - margin.right;
  const innerH = height - margin.top - margin.bottom;

  const xPos = (i) => {
    if (labels.length <= 1) return margin.left + innerW / 2;
    return margin.left + (i * innerW) / (labels.length - 1);
  };
  const yPos = (v) => {
    if (v == null) return null;
    const span = Math.max(1, (yMax - yMin));
    const y = (v - yMin) / span;
    return margin.top + innerH - y * innerH;
  };

  // Barras y separación; nunca se pegan al borde
  const gap = 6;
  const effW = innerW - gap * Math.max(0, labels.length - 1);
  const colWidth = labels.length > 0 ? Math.max(12, Math.min(26, effW / Math.max(1, labels.length))) : 16;

  const gridY = 3;
  const yTicks = Array.from({ length: gridY + 1 }, (_, k) => yMin + (k * (yMax - yMin)) / Math.max(1, gridY));

  // Tooltip: se renderiza ABSOLUTO dentro del wrapper (no en pantalla completa), y sólo en barras
  const handleEnter = (i, e) => {
    const rect = wrapRef.current?.getBoundingClientRect();
    if (!rect) return;
    const value = values[i];
    if (value == null) return;
    const xSvg = xPos(i);
    const ySvg = yPos(value);
    setHover({
      idx: i,
      xSvg,
      ySvg,
      value,
      label: labels[i],
      xClient: e.clientX - rect.left,
      yClient: e.clientY - rect.top
    });
  };
  const handleMove = (i, e) => {
    const rect = wrapRef.current?.getBoundingClientRect();
    setHover(h => (h && h.idx === i && rect)
      ? { ...h, xClient: e.clientX - rect.left, yClient: e.clientY - rect.top }
      : h
    );
  };
  const handleLeave = () => setHover(null);

  return (
    <div ref={wrapRef} style={{ width: '100%', maxWidth: width, height, position: 'relative' }}>
      {/* Tooltip dentro del wrapper */}
      {hover && (
        <div
          style={{
            position: 'absolute',
            left: Math.min(Math.max(hover.xClient + 10, 0), w - 80),
            top: Math.min(Math.max(hover.yClient - 26, 0), height - 22),
            background: 'rgba(0,0,0,0.85)',
            color: '#fff',
            padding: '2px 6px',
            borderRadius: 4,
            fontSize: 11,
            pointerEvents: 'none'
          }}
        >
          {hover.label} • {Number(hover.value).toLocaleString('es-CL')}
        </div>
      )}

      {/* Header */}
      <div style={{ display:'flex', alignItems:'center', gap:8, marginBottom:4, paddingLeft:6 }}>
        <div style={{ width:8, height:8, background:COLORS[fuelKey], borderRadius:2 }} />
        <span style={{ fontSize:11, color:'#333' }}>Combustible:</span>
        <select
          value={fuelKey}
          onChange={(e)=>{ setHover(null); return setFuelKey(e.target.value); }}
          style={{ fontSize:11, padding:'2px 6px', borderRadius:4, border:'1px solid #d0d0d0', background:'#fff', outline:'none' }}
        >
          {FUEL_OPTIONS.map(f => <option key={f.key} value={f.key}>{f.label}</option>)}
        </select>
      </div>

      <svg width={w} height={height}>
        {/* Grid Y */}
        {yTicks.map((t, idx) => {
          const y = yPos(t);
          return (
            <g key={idx}>
              <line x1={margin.left} x2={margin.left + innerW} y1={y} y2={y} stroke="rgba(0,0,0,0.08)" />
              <text x={margin.left - 8} y={y} textAnchor="end" dominantBaseline="middle" fontSize="10">
                {Math.round(t).toLocaleString('es-CL')}
              </text>
            </g>
          );
        })}

        {/* Fechas DD-MM */}
        {labels.map((lab, i) => (
          <text key={i} x={xPos(i)} y={height - 6} textAnchor="middle" fontSize="10">{lab}</text>
        ))}

        {/* Barras con tooltip solo dentro de cada rect */}
        {values.map((v, i) => {
          const y = yPos(v);
          const xCenter = xPos(i);
          const x = xCenter - colWidth / 2 + (i * gap) / Math.max(1, labels.length);
          const barH = v == null ? 0 : (margin.top + innerH - y);
          return (
            <rect
              key={i}
              x={x}
              y={v == null ? (margin.top + innerH) : y}
              width={colWidth}
              height={barH}
              fill={COLORS[fuelKey]}
              rx={3}
              onMouseEnter={(e)=> handleEnter(i, e)}
              onMouseMove={(e)=> handleMove(i, e)}
              onMouseLeave={handleLeave}
            />
          );
        })}
      </svg>
    </div>
  );
}
