import React, { useState, useMemo, useEffect } from 'react';
import './styles/globals.css';
import './styles/layout.css';
import './styles/markers.css';
import MapComponent from './components/MapComponent';
import FilterPanel from './components/FilterPanel';
import AlertPanel from './components/AlertPanel';
import { REGION_COORDINATES, REGIONES_ORDENADAS } from './utils/constants';
import { useMapData } from './hooks/useMapData';
import { getDataBaseComp, getMercadoAlerta } from './services/apiService';

function App() {
  const [sidebarVisible, setSidebarVisible] = useState(false);
  const [alertPanelVisible, setAlertPanelVisible] = useState(false);
  const [alertCount, setAlertCount] = useState(0);
  const [selectedRegion, setSelectedRegion] = useState('Metropolitana de Santiago');
  const [filteredMarkers, setFilteredMarkers] = useState([]);
  const [filteredMarkersForMercado, setFilteredMarkersForMercado] = useState([]);
  const [baseCompData, setBaseCompData] = useState([]);
  
  // Nuevos estados para filtros r√°pidos en navbar
  const [quickFilterMarca, setQuickFilterMarca] = useState('');
  const [quickFilterTipo, setQuickFilterTipo] = useState('');
  
  const { markers } = useMapData();

  const regions = useMemo(() => {
    const regionesConDatos = new Set(markers.map(m => m.Region));
    return REGIONES_ORDENADAS.filter(region => regionesConDatos.has(region));
  }, [markers]);

  const regionMarkers = useMemo(() => {
    return markers.filter(m => m.Region === selectedRegion);
  }, [markers, selectedRegion]);

  // Marcas √∫nicas de la regi√≥n
  const marcasDisponibles = useMemo(() => {
    const marcas = new Set(regionMarkers.map(m => m.Marca).filter(Boolean));
    return ['Todas', ...Array.from(marcas).sort()];
  }, [regionMarkers]);

  // Aplicar filtros r√°pidos
  const markersConFiltrosRapidos = useMemo(() => {
    let filtered = regionMarkers;
    
    if (quickFilterMarca && quickFilterMarca !== 'Todas') {
      filtered = filtered.filter(m => m.Marca === quickFilterMarca);
    }
    
    if (quickFilterTipo && quickFilterTipo !== 'Todos') {
      filtered = filtered.filter(m => m.operacion === quickFilterTipo);
    }
    
    return filtered;
  }, [regionMarkers, quickFilterMarca, quickFilterTipo]);

  useEffect(() => {
    const loadIt = async () => {
      try {
        const data = await getDataBaseComp();
        const final = data?.bascomp || data?.base_comp || data?.data || [];
        setBaseCompData(final);
      } catch (err) {
        console.error('Error:', err);
      }
    };
    loadIt();
  }, []);

  useEffect(() => {
    const loadAlertCount = async () => {
      try {
        const alertas = await getMercadoAlerta();
        const getStationCount = (alerts) => {
          const estaciones = {};
          alerts.forEach(a => {
            estaciones[a.Nom_Eds || 'Sin nombre'] = true;
          });
          return Object.keys(estaciones).length;
        };
        const count = getStationCount(alertas);
        setAlertCount(count);
      } catch (err) {
        console.error('Error al cargar alertas:', err);
        setAlertCount(0);
      }
    };
    loadAlertCount();
    const interval = setInterval(loadAlertCount, 60000);
    return () => clearInterval(interval);
  }, []);

  const handleFiltersChange = (filtered, filteredForMercado) => {
    setFilteredMarkers(filtered);
    setFilteredMarkersForMercado(regionMarkers);
  };

  const toggleSidebar = () => {
    setSidebarVisible(!sidebarVisible);
    setTimeout(() => {
      if (window.leafletMap) {
        window.leafletMap.invalidateSize();
      }
    }, 300);
  };

  const toggleAlertPanel = () => {
    setAlertPanelVisible(!alertPanelVisible);
  };

  // Reset filtros r√°pidos al cambiar regi√≥n
  useEffect(() => {
    setQuickFilterMarca('');
    setQuickFilterTipo('');
  }, [selectedRegion]);

  const finalMarkers = filteredMarkers.length > 0 ? filteredMarkers : markersConFiltrosRapidos;

  return (
    <div className="app-container">
      <nav className="navbar">
        <div className="navbar-container">
          <div className="navbar-left">
            <h1 className="navbar-title">S.I.M.E</h1>
            <span className="navbar-subtitle">
              {selectedRegion} -  {finalMarkers.length} estaciones
            </span>
          </div>

          {/* FILTROS R√ÅPIDOS EN NAVBAR */}
          <div className="navbar-filters">
            <select 
              className="navbar-select"
              value={selectedRegion}
              onChange={(e) => setSelectedRegion(e.target.value)}
              title="Seleccionar regi√≥n"
            >
              {regions.map(region => (
                <option key={region} value={region}>
                  {region}
                </option>
              ))}
            </select>

            <select 
              className="navbar-select"
              value={quickFilterMarca}
              onChange={(e) => setQuickFilterMarca(e.target.value)}
              title="Filtrar por marca"
            >
              <option value="">Todas las marcas</option>
              {marcasDisponibles.filter(m => m !== 'Todas').map(marca => (
                <option key={marca} value={marca}>{marca}</option>
              ))}
            </select>

            <select 
              className="navbar-select"
              value={quickFilterTipo}
              onChange={(e) => setQuickFilterTipo(e.target.value)}
              title="Filtrar por tipo"
            >
              <option value="">Todos los tipos</option>
              <option value="Comisionista">Comisionista</option>
              <option value="CODO">CODO</option>
            </select>

            {(quickFilterMarca || quickFilterTipo) && (
              <button 
                className="navbar-btn-clear"
                onClick={() => {
                  setQuickFilterMarca('');
                  setQuickFilterTipo('');
                }}
                title="Limpiar filtros"
              >
                ‚úï
              </button>
            )}
          </div>
          
          <div className="navbar-right">
            <button 
              className="navbar-icon-btn"
              onClick={toggleSidebar}
              title="Filtros avanzados"
            >
              ‚öôÔ∏è
              {sidebarVisible && <span className="btn-indicator active"></span>}
            </button>

            <button 
              className="navbar-icon-btn"
              onClick={toggleAlertPanel}
              title="Alertas de mercado"
            >
              üîî
              {alertCount > 0 && (
                <span className="alert-badge pulse">{alertCount}</span>
              )}
            </button>

            <img 
              src={`${process.env.PUBLIC_URL}/iconos/aramco.jpg`} 
              alt="Aramco Logo" 
              className="navbar-logo"
              onError={(e) => e.target.style.display = 'none'}
            />
          </div>
        </div>
      </nav>

      <div className="main-container">
        {/* SIDEBAR FLOTANTE - SOLO FILTROS AVANZADOS */}
        <aside className={`sidebar-floating ${sidebarVisible ? 'visible' : ''}`}>
          <div className="sidebar-header">
            <h3 className="sidebar-title">Filtros Avanzados</h3>
            <button className="sidebar-close" onClick={toggleSidebar}>‚úï</button>
          </div>
          
          <FilterPanel 
            markers={markersConFiltrosRapidos}
            selectedRegion={selectedRegion}
            onFiltersChange={handleFiltersChange}
          />
        </aside>

        <MapComponent 
          selectedRegion={selectedRegion}
          regionCenter={REGION_COORDINATES[selectedRegion]}
          markers={finalMarkers}
          markersForMercado={regionMarkers}
          baseCompData={baseCompData}
          onToggleSidebar={toggleSidebar}
          sidebarVisible={sidebarVisible}
        />

        <AlertPanel 
          isVisible={alertPanelVisible}
          onClose={toggleAlertPanel}
        />
      </div>

      <div className="navbar-version">v2.1</div>
    </div>
  );
}

export default App;
