import React, { useState, useEffect } from 'react';
import { getMercadoAlerta, getHistoricoAlerta } from '../services/apiService';
import '../styles/AlertPanel.css';

const AlertPanel = ({ isVisible, onClose }) => {
  const [activeTab, setActiveTab] = useState('alertas');
  const [alertas, setAlertas] = useState([]);
  const [historico, setHistorico] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    if (isVisible) {
      loadData();
    }
  }, [isVisible]);

  const loadData = async () => {
    try {
      setLoading(true);
      const [alertasData, historicoData] = await Promise.all([
        getMercadoAlerta(),
        getHistoricoAlerta()
      ]);
      
      // ‚úÖ Validaci√≥n: asegurar que sean arrays
      setAlertas(Array.isArray(alertasData) ? alertasData : []);
      setHistorico(Array.isArray(historicoData) ? historicoData : []);
      
      console.log('‚úÖ Alertas cargadas:', alertasData);
      console.log('‚úÖ Hist√≥rico cargado:', historicoData);
    } catch (error) {
      console.error('‚ùå Error al cargar alertas:', error);
      setAlertas([]);
      setHistorico([]);
    } finally {
      setLoading(false);
    }
  };

  return (
    <aside className={`alert-panel ${!isVisible ? 'hidden' : ''}`}>
      {/* Header */}
      <div className="alert-panel-header">
        <h3 className="alert-panel-title">üìä Alertas de Mercado</h3>
        <button className="alert-panel-close" onClick={onClose}>‚úï</button>
      </div>

      {/* Tabs */}
      <div className="alert-tabs">
        <button 
          className={`alert-tab ${activeTab === 'alertas' ? 'active' : ''}`}
          onClick={() => setActiveTab('alertas')}
        >
          Alertas Activas ({alertas.length})
        </button>
        <button 
          className={`alert-tab ${activeTab === 'historico' ? 'active' : ''}`}
          onClick={() => setActiveTab('historico')}
        >
          Hist√≥rico ({historico.length})
        </button>
      </div>

      {/* Content */}
      <div className="alert-panel-content">
        {loading ? (
          <div className="alert-loading">Cargando...</div>
        ) : (
          <>
            {activeTab === 'alertas' ? (
              <AlertasActivas data={alertas} />
            ) : (
              <HistoricoAlertas data={historico} />
            )}
          </>
        )}
      </div>
    </aside>
  );
};

// Componente de Alertas Activas
const AlertasActivas = ({ data }) => {
  // ‚úÖ Validaci√≥n adicional
  if (!Array.isArray(data)) {
    console.error('‚ùå AlertasActivas: data no es un array', data);
    return <div className="alert-empty">‚ö†Ô∏è Error: Formato de datos inv√°lido</div>;
  }

  if (data.length === 0) {
    return <div className="alert-empty">‚úÖ No hay alertas activas</div>;
  }

  return (
    <div className="alerts-list">
      {data.map((alerta, idx) => {
        // ‚úÖ Manejo seguro de propiedades
        const estacion = alerta.Estacion || alerta['La Reina'] || 'Sin nombre';
        const marca = alerta.Marca_Competencia || alerta.marca_competencia || 'N/A';
        const diferencia = Number(alerta.Diferencia) || 0;
        const combustible = alerta.Combustible || alerta.combustible || 'N/A';
        const precio = alerta.Precio || alerta.precio || 0;
        const horario = alerta.Horario || alerta.horario || 'N/A';
        
        return (
          <div key={idx} className="alert-card">
            <div className="alert-card-header">
              <span className={`alert-icon ${diferencia < 0 ? 'down' : 'up'}`}>
                {diferencia < 0 ? 'üî¥' : 'üü¢'}
              </span>
              <div className="alert-card-info">
                <strong>{estacion}</strong>
                <span className="alert-marca">{marca}</span>
              </div>
              <span className={`alert-diff ${diferencia < 0 ? 'negative' : 'positive'}`}>
                {diferencia > 0 ? '+' : ''}{diferencia}
              </span>
            </div>
            <div className="alert-card-details">
              <span>üíß {combustible}</span>
              <span>üí∞ ${precio}</span>
              <span>üïê {horario}</span>
            </div>
          </div>
        );
      })}
    </div>
  );
};

// Componente de Hist√≥rico
const HistoricoAlertas = ({ data }) => {
  // ‚úÖ Validaci√≥n adicional
  if (!Array.isArray(data)) {
    console.error('‚ùå HistoricoAlertas: data no es un array', data);
    return <div className="alert-empty">‚ö†Ô∏è Error: Formato de datos inv√°lido</div>;
  }

  if (data.length === 0) {
    return <div className="alert-empty">üìã No hay hist√≥rico disponible</div>;
  }

  return (
    <div className="historico-wrapper">
      <table className="historico-table">
        <thead>
          <tr>
            <th>Estaci√≥n</th>
            <th>Marca</th>
            <th>Combustible</th>
            <th>Precio</th>
            <th>Dif</th>
            <th>Fecha</th>
          </tr>
        </thead>
        <tbody>
          {data.slice(0, 50).map((item, idx) => {
            // ‚úÖ Manejo seguro de propiedades
            const estacion = item.Estacion || item.estacion || 'N/A';
            const marca = item.Marca_competencia || item.marca_competencia || 'N/A';
            const combustible = item.Combustible || item.combustible || 'N/A';
            const precio = item.Precio || item.precio || 0;
            const diferencia = Number(item.Diferencia) || 0;
            const fecha = item.Fecha_Carga || item.fecha_carga || 'N/A';
            
            return (
              <tr key={idx}>
                <td>{estacion}</td>
                <td>{marca}</td>
                <td>{combustible}</td>
                <td>${precio}</td>
                <td className={diferencia < 0 ? 'negative' : 'positive'}>
                  {diferencia}
                </td>
                <td className="fecha-small">{fecha}</td>
              </tr>
            );
          })}
        </tbody>
      </table>
    </div>
  );
};

export default AlertPanel;
