import React, { useState, useEffect } from 'react';
import MapComponent from './components/MapComponent';
import RegionSelector from './components/RegionSelector';
import './styles/globals.css';

function App() {
  const [markers, setMarkers] = useState([]);
  const [selectedRegion, setSelectedRegion] = useState('Metropolitana');
  const [regions, setRegions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Cargar datos
  useEffect(() => {
    const loadData = async () => {
      try {
        setLoading(true);

        // Llamar a tu API
        const response = await fetch('/api/estaciones'); // Ajusta la URL seg√∫n tu API

        if (!response.ok) {
          throw new Error(`Error: ${response.status}`);
        }

        const data = await response.json();

        console.log('‚úÖ Datos cargados:', data.length, 'estaciones');

        setMarkers(data);

        // Extraer regiones √∫nicas
        const uniqueRegions = [...new Set(data.map(m => m.Region))].sort();
        setRegions(uniqueRegions);

        setLoading(false);
      } catch (err) {
        console.error('‚ùå Error cargando datos:', err);
        setError(err.message);
        setLoading(false);
      }
    };

    loadData();
  }, []);

  if (loading) {
    return (
      <div className="app-container">
        <div className="loading">
          <h2>‚è≥ Cargando datos...</h2>
          <p>Por favor espera</p>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="app-container">
        <div className="error">
          <h2>‚ùå Error</h2>
          <p>{error}</p>
          <button onClick={() => window.location.reload()}>Reintentar</button>
        </div>
      </div>
    );
  }

  return (
    <div className="app-container">
      {/* Panel superior */}
      <div className="header">
        <h1>üó∫Ô∏è Mercado de Gasolineras Chile</h1>
        <RegionSelector 
          regions={regions}
          selectedRegion={selectedRegion}
          onRegionChange={setSelectedRegion}
        />
      </div>

      {/* Contenido principal */}
      <div className="main-content">
        <MapComponent 
          markers={markers}
          selectedRegion={selectedRegion}
          regions={regions}
        />
      </div>
    </div>
  );
}

export default App;
