import React, { useState, useEffect, useRef } from 'react';
import { getNotasCompartidas, guardarNotasCompartidas } from '../services/apiService';
import '../styles/NotesModal.css';

const NotesModal = ({ isOpen, onClose }) => {
    const [notes, setNotes] = useState('');
    const [loading, setLoading] = useState(true);
    const saveTimeoutRef = useRef(null);

    // Load notes from server on mount
    useEffect(() => {
        if (isOpen) {
            loadNotes();
        }
    }, [isOpen]);

    const loadNotes = async () => {
        try {
            setLoading(true);
            const notasFromServer = await getNotasCompartidas();
            setNotes(notasFromServer || '');
        } catch (error) {
            console.error('Error cargando notas:', error);
            setNotes('');
        } finally {
            setLoading(false);
        }
    };

    // Auto-save notes with debounce
    useEffect(() => {
        if (loading) return; // Don't save while loading

        if (saveTimeoutRef.current) {
            clearTimeout(saveTimeoutRef.current);
        }

        saveTimeoutRef.current = setTimeout(async () => {
            try {
                await guardarNotasCompartidas(notes);
                console.log('üìù Notas guardadas');
            } catch (error) {
                console.error('Error guardando notas:', error);
            }
        }, 500);

        return () => {
            if (saveTimeoutRef.current) {
                clearTimeout(saveTimeoutRef.current);
            }
        };
    }, [notes, loading]);

    const handleClearAll = async () => {
        try {
            setNotes('');
            await guardarNotasCompartidas('');
            console.log('üìù Todas las notas borradas');
        } catch (error) {
            console.error('Error borrando notas:', error);
        }
    };

    if (!isOpen) return null;

    // Convertir notas en l√≠neas para mostrar con botones de borrar
    const lines = notes.split('\n');

    const handleDeleteLine = async (index) => {
        const newLines = lines.filter((_, i) => i !== index);
        const newNotes = newLines.join('\n');
        setNotes(newNotes);
    };

    return (
        <div className="notes-modal-overlay" onClick={onClose}>
            <div className="notes-modal-content" onClick={e => e.stopPropagation()}>
                <div className="notes-modal-header">
                    <h2>üìù Notas Compartidas</h2>
                    <button className="notes-close-btn" onClick={onClose}>‚úï</button>
                </div>
                <div className="notes-modal-body">
                    {loading ? (
                        <div className="notes-loading">Cargando notas...</div>
                    ) : (
                        <>
                            <textarea
                                className="notes-textarea"
                                placeholder="Escribe tus notas aqu√≠... Presiona Enter para crear una nueva l√≠nea. Se guardar√°n autom√°ticamente y ser√°n visibles para todos los usuarios."
                                value={notes}
                                onChange={(e) => setNotes(e.target.value)}
                                autoFocus
                            />

                            {/* Lista de l√≠neas con botones de borrar */}
                            {notes.trim() && (
                                <div className="notes-lines-list">
                                    <div className="notes-lines-header">L√≠neas guardadas:</div>
                                    {lines.map((line, index) => (
                                        line.trim() && (
                                            <div key={index} className="notes-line-item">
                                                <span className="notes-line-text">{line}</span>
                                                <button
                                                    className="notes-delete-line-btn"
                                                    onClick={() => handleDeleteLine(index)}
                                                    title="Borrar esta l√≠nea"
                                                >
                                                    ‚úï
                                                </button>
                                            </div>
                                        )
                                    ))}
                                </div>
                            )}
                        </>
                    )}
                </div>
                <div className="notes-modal-footer">
                    <span className="notes-autosave-indicator">
                        üíæ Guardado autom√°tico (compartido)
                    </span>
                    <button className="notes-clear-btn" onClick={handleClearAll}>
                        üóëÔ∏è Borrar todo
                    </button>
                </div>
            </div>
        </div>
    );
};

export default NotesModal;
