import React, { useMemo, useState } from 'react';

const FilterPanel = ({ markers, selectedRegion, onFiltersChange, selectedJefeZona, onJefeZonaChange }) => {
  const [selectedMarca, setSelectedMarca] = useState('');
  const [selectedComuna, setSelectedComuna] = useState('');
  const [selectedEds, setSelectedEds] = useState('');
  const [edsSearchTerm, setEdsSearchTerm] = useState('');

  // ============================================
  // OPCI√ìN 1: SIN FILTROS (mostrar solo regi√≥n)
  // ============================================
  const baseMarkersRegion = useMemo(() => {
    return markers.filter(m => m.Region === selectedRegion);
  }, [markers, selectedRegion]);

  // ============================================
  // OPCI√ìN 2: CON JEFE DE ZONA
  // ============================================
  const getCompetenciaDelJefe = useMemo(() => {
    if (!selectedJefeZona) return [];

    // Estaciones del jefe
    const jefeStations = markers.filter(m => 
      m.Region === selectedRegion && m.nombre === selectedJefeZona
    );

    // PBLs del jefe
    const pblsDelJefe = new Set(jefeStations.map(m => m.pbl));

    // TODOS los marcadores (propios + competencia) de esos PBLs
    const result = markers.filter(m => pblsDelJefe.has(m.pbl));

    return result;
  }, [markers, selectedRegion, selectedJefeZona]);

  // ============================================
  // OPCI√ìN 3: CON MARCA
  // ============================================
  const getCompetenciaDeMarca = useMemo(() => {
    if (!selectedMarca) return [];

    // Estaciones de la marca seleccionada en la regi√≥n
    const marcaStations = markers.filter(m => 
      m.Region === selectedRegion && m.Marca === selectedMarca
    );

    // PBLs de esa marca
    const pblsDeMarca = new Set(marcaStations.map(m => m.pbl));

    // TODOS los marcadores de esos PBLs (incluyendo competencia)
    const result = markers.filter(m => pblsDeMarca.has(m.pbl));

    return result;
  }, [markers, selectedRegion, selectedMarca]);

  // ============================================
  // OPCI√ìN 4: CON EDS
  // ============================================
  const getCompetenciaDeEds = useMemo(() => {
    if (!selectedEds) return [];

    // Estaci√≥n con ese EDS
    const edsStation = markers.find(m => m.eds === selectedEds);

    if (!edsStation) return [];

    // PBL de esa estaci√≥n
    const pblDelEds = edsStation.pbl;

    // TODOS los marcadores de ese PBL (incluyendo competencia)
    const result = markers.filter(m => m.pbl === pblDelEds);

    return result;
  }, [markers, selectedEds]);

  // ============================================
  // L√ìGICA: Determinar cu√°l filtro usar
  // ============================================
  const filteredMarkers = useMemo(() => {
    if (selectedJefeZona) {
      return getCompetenciaDelJefe;
    } else if (selectedMarca) {
      return getCompetenciaDeMarca;
    } else if (selectedEds) {
      return getCompetenciaDeEds;
    } else if (selectedComuna) {
      return baseMarkersRegion.filter(m => m.Comuna === selectedComuna);
    } else {
      return baseMarkersRegion;
    }
  }, [selectedJefeZona, selectedMarca, selectedEds, selectedComuna, 
      getCompetenciaDelJefe, getCompetenciaDeMarca, getCompetenciaDeEds, baseMarkersRegion]);

  // Llamar al callback
  React.useEffect(() => {
    onFiltersChange(filteredMarkers);
  }, [filteredMarkers, onFiltersChange]);

  // ============================================
  // OPCIONES DISPONIBLES SEG√öN REGI√ìN
  // ============================================
  const marcasEnRegion = useMemo(() => {
    return [...new Set(baseMarkersRegion.map(m => m.Marca))].sort();
  }, [baseMarkersRegion]);

  const jefesEnRegion = useMemo(() => {
    return [...new Set(
      baseMarkersRegion.filter(m => m.nombre).map(m => m.nombre)
    )].sort();
  }, [baseMarkersRegion]);

  const comunasEnRegion = useMemo(() => {
    return [...new Set(baseMarkersRegion.map(m => m.Comuna))].sort();
  }, [baseMarkersRegion]);

  const edsEnRegion = useMemo(() => {
    return [...new Set(baseMarkersRegion.map(m => m.eds).filter(e => e))].sort();
  }, [baseMarkersRegion]);

  const filteredEds = useMemo(() => {
    return edsEnRegion.filter(e => e.toLowerCase().includes(edsSearchTerm.toLowerCase()));
  }, [edsEnRegion, edsSearchTerm]);

  // ============================================
  // ESTAD√çSTICAS
  // ============================================
  const stats = useMemo(() => {
    const byBrand = {};
    filteredMarkers.forEach(m => {
      byBrand[m.Marca] = (byBrand[m.Marca] || 0) + 1;
    });
    return byBrand;
  }, [filteredMarkers]);

  const handleClear = () => {
    setSelectedMarca('');
    setSelectedComuna('');
    setSelectedEds('');
    setEdsSearchTerm('');
    onJefeZonaChange('');
  };

  return (
    <div className="filter-panel">
      <h3 className="filter-title">üìä Estad√≠sticas</h3>
      <div className="stats-mini">
        <div className="stat-item">
          <span className="stat-label">Estaciones</span>
          <span className="stat-value">{filteredMarkers.length}</span>
        </div>
        <div className="stat-item">
          <span className="stat-label">Marcas</span>
          <span className="stat-value">{Object.keys(stats).length}</span>
        </div>
      </div>

      <h3 className="filter-title">üè¢ Por Marca</h3>
      <div className="brands-mini">
        {Object.entries(stats)
          .sort((a, b) => b[1] - a[1])
          .map(([brand, count]) => (
            <div key={brand} className="brand-mini">
              <span>{brand}</span>
              <span className="count">{count}</span>
            </div>
          ))}
      </div>

      <h3 className="filter-title">üîç Filtros</h3>

      <div className="filter-item">
        <label>Jefe de Zona</label>
        <select value={selectedJefeZona} onChange={(e) => {
          onJefeZonaChange(e.target.value);
          setSelectedMarca('');
          setSelectedComuna('');
          setSelectedEds('');
          setEdsSearchTerm('');
        }}>
          <option value="">Todos</option>
          {jefesEnRegion.map(j => <option key={j} value={j}>{j}</option>)}
        </select>
      </div>

      <div className="filter-item">
        <label>Marca</label>
        <select value={selectedMarca} onChange={(e) => {
          setSelectedMarca(e.target.value);
          setSelectedComuna('');
          setSelectedEds('');
          setEdsSearchTerm('');
          onJefeZonaChange('');
        }}>
          <option value="">Todas</option>
          {marcasEnRegion.map(m => <option key={m} value={m}>{m}</option>)}
        </select>
      </div>

      <div className="filter-item">
        <label>Comuna</label>
        <select value={selectedComuna} onChange={(e) => {
          setSelectedComuna(e.target.value);
          setSelectedMarca('');
          setSelectedEds('');
          setEdsSearchTerm('');
          onJefeZonaChange('');
        }}>
          <option value="">Todas</option>
          {comunasEnRegion.map(c => <option key={c} value={c}>{c}</option>)}
        </select>
      </div>

      <div className="filter-item">
        <label>Buscar EDS</label>
        <input
          type="text"
          placeholder="Escribe para buscar..."
          value={edsSearchTerm}
          onChange={(e) => {
            setEdsSearchTerm(e.target.value);
            setSelectedEds('');
          }}
        />
        
        {edsSearchTerm && filteredEds.length > 0 && (
          <div className="eds-dropdown">
            {filteredEds.slice(0, 10).map(e => (
              <div key={e} className="eds-option" onClick={() => {
                setSelectedEds(e);
                setEdsSearchTerm('');
                setSelectedMarca('');
                setSelectedComuna('');
                onJefeZonaChange('');
              }}>
                {e}
              </div>
            ))}
          </div>
        )}

        {selectedEds && (
          <div className="selected-eds">
            <span>‚úì {selectedEds}</span>
            <button onClick={() => {
              setSelectedEds('');
              setEdsSearchTerm('');
            }}>‚úï</button>
          </div>
        )}
      </div>

      <button className="btn-clear" onClick={handleClear}>
        üîÑ Limpiar Filtros
      </button>
    </div>
  );
};

export default FilterPanel;
