import React, { useState, useEffect, useRef } from 'react';
import { MapContainer, TileLayer, useMap } from 'react-leaflet';
import IconMarkersLayer from './IconMarkersLayer';
import 'leaflet/dist/leaflet.css';

const TILE_LAYERS = {
  streets: {
    name: 'Calles',
    icon: 'üó∫Ô∏è',
    url: 'https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png',
    attribution: '¬© OpenStreetMap'
  },
  satellite: {
    name: 'Sat√©lite',
    icon: 'üõ∞Ô∏è',
    url: 'https://server.arcgisonline.com/ArcGIS/rest/services/World_Imagery/MapServer/tile/{z}/{y}/{x}',
    attribution: '¬© Esri'
  },
  hybrid: {
    name: 'H√≠brido',
    icon: 'üåç',
    url: 'https://server.arcgisonline.com/ArcGIS/rest/services/World_Imagery/MapServer/tile/{z}/{y}/{x}',
    attribution: '¬© Esri'
  }
};

function MapController({ regionCenter }) {
  const map = useMap();

  useEffect(() => {
    if (regionCenter && regionCenter.length === 2) {
      map.setView(regionCenter, 10);
    }
  }, [regionCenter, map]);

  useEffect(() => {
    window.leafletMap = map;
  }, [map]);

  useEffect(() => {
    const timer = setTimeout(() => {
      map.invalidateSize();
    }, 100);
    return () => clearTimeout(timer);
  }, [map]);

  return null;
}

function MapComponent({ 
  selectedRegion, 
  regionCenter, 
  markers, 
  markersForMercado, 
  baseCompData,
  onToggleSidebar,
  onToggleRightPanel,
  sidebarVisible,
  rightPanelVisible
}) {
  const [selectedLayer, setSelectedLayer] = useState('streets');
  const [searchQuery, setSearchQuery] = useState('');
  const [searchResults, setSearchResults] = useState([]);
  const [showResults, setShowResults] = useState(false);
  const [panelsMenuOpen, setPanelsMenuOpen] = useState(false);
  const [layersMenuOpen, setLayersMenuOpen] = useState(false);
  const searchTimeoutRef = useRef(null);

 const handleSearch = async (query) => {
  setSearchQuery(query);
  
  if (query.length < 3) {
    setSearchResults([]);
    setShowResults(false);
    return;
  }

  try {
    const response = await fetch(
      `https://nominatim.openstreetmap.org/search?format=json&q=${encodeURIComponent(query)}&countrycodes=cl&limit=8&addressdetails=1`
    );
    const data = await response.json();
    
    // Formatear resultados de forma espec√≠fica y limpia
    const formattedResults = data.map(item => {
      const addr = item.address || {};
      
      // PARTE 1: Direcci√≥n principal (nombre espec√≠fico)
      let mainAddress = '';
      
      // Prioridad: establecimiento/POI con direcci√≥n
      if (addr.amenity || addr.shop || addr.building) {
        const poiName = addr.amenity || addr.shop || addr.building;
        if (addr.road && addr.house_number) {
          mainAddress = `${poiName}, ${addr.house_number} ${addr.road}`;
        } else if (addr.road) {
          mainAddress = `${poiName}, ${addr.road}`;
        } else {
          mainAddress = poiName;
        }
      }
      // Si no hay POI, mostrar calle + n√∫mero
      else if (addr.road) {
        if (addr.house_number) {
          mainAddress = `${addr.house_number} ${addr.road}`;
        } else {
          mainAddress = addr.road;
        }
      }
      // Si no hay calle, usar barrio/suburbio
      else if (addr.neighbourhood || addr.suburb) {
        mainAddress = addr.neighbourhood || addr.suburb;
      }
      // Fallback: primera parte del display_name
      else {
        mainAddress = item.display_name.split(',')[0].trim();
      }
      
      // PARTE 2: Ubicaci√≥n secundaria (comuna, regi√≥n, pa√≠s)
      let location = [];
      
      // Comuna/Localidad
      if (addr.municipality || addr.city || addr.town || addr.village) {
        location.push(addr.municipality || addr.city || addr.town || addr.village);
      }
      // Subregi√≥n o condado (opcional)
      else if (addr.county && addr.county !== addr.city) {
        location.push(addr.county);
      }
      
      // Regi√≥n
      if (addr.state && addr.state !== 'Regi√≥n Metropolitana de Santiago') {
        // Acortar nombres de regi√≥n
        const regionShort = addr.state.replace('Regi√≥n', '').replace('de', '').trim();
        location.push(regionShort);
      }
      
      // Pa√≠s
      location.push('Chile');
      
      const secondaryLocation = location.join(', ');
      
      return {
        ...item,
        mainAddress: mainAddress,
        secondaryLocation: secondaryLocation,
        fullAddress: item.display_name
      };
    });
    
    setSearchResults(formattedResults);
    setShowResults(true);
  } catch (error) {
    console.error('Error buscando direcci√≥n:', error);
    setSearchResults([]);
  }
};


  const selectLocation = (result) => {
    const map = window.leafletMap;
    if (map) {
      const lat = parseFloat(result.lat);
      const lon = parseFloat(result.lon);
      map.setView([lat, lon], 17);
    }
    
    setSearchQuery(result.display_name);
    setShowResults(false);
  };

  return (
    <div className="map-wrapper">
      {/* MEN√ö PANELES (IZQUIERDA) */}
   {/* BUSCADOR DE DIRECCIONES - COMPACTO AL LADO DE LOS BOTONES */}
<div className="map-control-left">
  <div className="search-bar-inline">
    <input
      type="text"
      className="search-input-inline"
      placeholder="üîç Buscar direcci√≥n..."
      value={searchQuery}
      onChange={(e) => handleSearch(e.target.value)}
      onClick={(e) => e.stopPropagation()}
    />
    
    {searchQuery && (
      <button
        className="search-clear-btn"
        onClick={() => {
          setSearchQuery('');
          setSearchResults([]);
          setShowResults(false);
        }}
        title="Limpiar b√∫squeda"
      >
        ‚úï
      </button>
    )}
    
    {showResults && searchResults.length > 0 && (
      <div className="search-results-dropdown-inline">
        {searchResults.map((result, index) => (
          <div
            key={index}
            className="search-result-item"
            onMouseDown={(e) => e.preventDefault()}
            onClick={(e) => {
              e.stopPropagation();
              selectLocation(result);
            }}
          >
            üìç {result.display_name}
          </div>
        ))}
      </div>
    )}
  </div>
</div>



      {/* MEN√ö CAPAS (DERECHA) */}
      <div className="map-control-right">
        <button 
          className="control-toggle-btn"
          onClick={() => setLayersMenuOpen(!layersMenuOpen)}
          title="Capas del Mapa"
        >
          üó∫Ô∏è
        </button>

        {layersMenuOpen && (
          <div className="control-dropdown">
            <div className="control-section">
              <div className="control-section-title">Capas del Mapa</div>
              <div className="layer-buttons">
                {Object.entries(TILE_LAYERS).map(([key, layer]) => (
                  <button
                    key={key}
                    className={`layer-btn-compact ${selectedLayer === key ? 'active' : ''}`}
                    onClick={() => setSelectedLayer(key)}
                  >
                    {layer.icon} {layer.name}
                  </button>
                ))}
              </div>
            </div>
          </div>
        )}
      </div>

      <MapContainer
        center={regionCenter || [-33.4489, -70.6693]}
        zoom={10}
        className="map"
        zoomControl={true}
      >
        <TileLayer
          url={TILE_LAYERS[selectedLayer].url}
          attribution={TILE_LAYERS[selectedLayer].attribution}
          maxZoom={19}
        />
        
        {selectedLayer === 'hybrid' && (
          <TileLayer
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
            attribution=""
            opacity={0.4}
          />
        )}

        <MapController regionCenter={regionCenter} />
        
        <IconMarkersLayer
          markers={markers}
          markersForMercado={markersForMercado}
          selectedRegion={selectedRegion}
          baseCompData={baseCompData}
        />
      </MapContainer>
    </div>
  );
}

export default MapComponent;
