import React, { useState, useEffect, useRef } from 'react';
import { MapContainer, TileLayer, useMap } from 'react-leaflet';
import IconMarkersLayer from './IconMarkersLayer';
import 'leaflet/dist/leaflet.css';

const TILE_LAYERS = {
  streets: {
    name: 'Calles',
    url: 'https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png',
    attribution: '¬© OpenStreetMap'
  },
  satellite: {
    name: 'Sat√©lite',
    url: 'https://server.arcgisonline.com/ArcGIS/rest/services/World_Imagery/MapServer/tile/{z}/{y}/{x}',
    attribution: '¬© Esri'
  },
  hybrid: {
    name: 'H√≠brido',
    url: 'https://server.arcgisonline.com/ArcGIS/rest/services/World_Imagery/MapServer/tile/{z}/{y}/{x}',
    attribution: '¬© Esri'
  }
};

function MapController({ regionCenter }) {
  const map = useMap();

  useEffect(() => {
    if (regionCenter && regionCenter.length === 2) {
      map.setView(regionCenter, 10);
    }
  }, [regionCenter, map]);

  useEffect(() => {
    window.leafletMap = map;
  }, [map]);

  useEffect(() => {
    const timer = setTimeout(() => {
      map.invalidateSize();
    }, 100);
    return () => clearTimeout(timer);
  }, [map]);

  return null;
}

function MapComponent({ selectedRegion, regionCenter, markers, markersForMercado, baseCompData }) {
  const [selectedLayer, setSelectedLayer] = useState('streets');
  const [searchQuery, setSearchQuery] = useState('');
  const [searchResults, setSearchResults] = useState([]);
  const [showResults, setShowResults] = useState(false);
  const searchTimeoutRef = useRef(null);

  const toggleFullscreen = () => {
    const mapElement = document.querySelector('.map-wrapper');
    
    if (!document.fullscreenElement) {
      mapElement.requestFullscreen().catch(err => {
        console.error('Error fullscreen:', err);
      });
    } else {
      document.exitFullscreen();
    }
  };

  const handleSearch = (query) => {
    setSearchQuery(query);
    
    if (query.length < 3) {
      setSearchResults([]);
      setShowResults(false);
      return;
    }

    // Limpiar timeout anterior
    if (searchTimeoutRef.current) {
      clearTimeout(searchTimeoutRef.current);
    }

    // Esperar 500ms antes de buscar
    searchTimeoutRef.current = setTimeout(async () => {
      try {
        console.log('üîç Buscando:', query);
        
        const response = await fetch(
          `https://nominatim.openstreetmap.org/search?` +
          `format=json&` +
          `q=${encodeURIComponent(query)}` +
          `&countrycodes=cl&` +
          `limit=8&` +
          `addressdetails=1`,
          {
            headers: {
              'User-Agent': 'MapaEstaciones/1.0',
              'Accept-Language': 'es'
            }
          }
        );
        
        const data = await response.json();
        console.log('üìç Resultados:', data.length);
        
        setSearchResults(data);
        setShowResults(data.length > 0);
      } catch (error) {
        console.error('‚ùå Error en b√∫squeda:', error);
      }
    }, 500);
  };

  const selectLocation = (result) => {
    console.log('‚úÖ Seleccionado:', result);
    
    const map = window.leafletMap;
    if (map) {
      const lat = parseFloat(result.lat);
      const lon = parseFloat(result.lon);
      
      console.log('üìç Moviendo mapa a:', lat, lon);
      
      map.setView([lat, lon], 17);
    }
    
    setSearchQuery(result.display_name);
    setShowResults(false);
  };

  return (
    <div className="map-wrapper">
      {/* BUSCADOR */}
      <div className="map-search-container">
        <input
          type="text"
          className="map-search-input"
          placeholder="üîç Buscar direcci√≥n (ej: Bernardo O'Higgins 292, Talagante)..."
          value={searchQuery}
          onChange={(e) => handleSearch(e.target.value)}
          onFocus={() => searchResults.length > 0 && setShowResults(true)}
        />
        
        {showResults && searchResults.length > 0 && (
          <div 
            className="map-search-results"
            onMouseDown={(e) => e.preventDefault()}
          >
            {searchResults.map((result, index) => (
              <div
                key={index}
                className="map-search-result-item"
                onClick={() => selectLocation(result)}
              >
                üìç <strong>{result.display_name}</strong>
              </div>
            ))}
          </div>
        )}
      </div>

      {/* CAPAS */}
      <div className="map-layer-selector">
        {Object.entries(TILE_LAYERS).map(([key, layer]) => (
          <button
            key={key}
            className={`layer-btn ${selectedLayer === key ? 'active' : ''}`}
            onClick={() => setSelectedLayer(key)}
          >
            {layer.name}
          </button>
        ))}
      </div>

      {/* CONTROLES */}
      <div className="map-controls">
        <button className="map-btn" onClick={toggleFullscreen}>
          üñºÔ∏è Pantalla Completa
        </button>
      </div>

      <MapContainer
        center={regionCenter || [-33.4489, -70.6693]}
        zoom={10}
        className="map"
        zoomControl={true}
      >
        <TileLayer
          url={TILE_LAYERS[selectedLayer].url}
          attribution={TILE_LAYERS[selectedLayer].attribution}
          maxZoom={19}
        />
        
        {selectedLayer === 'hybrid' && (
          <TileLayer
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
            attribution=""
            opacity={0.4}
          />
        )}

        <MapController regionCenter={regionCenter} />
        
        <IconMarkersLayer
          markers={markers}
          markersForMercado={markersForMercado}
          selectedRegion={selectedRegion}
          baseCompData={baseCompData}
        />
      </MapContainer>
    </div>
  );
}

export default MapComponent;
