import React, { useState, useEffect, useMemo } from 'react';
import { getMercadoAlerta, getHistoricoAlerta, getDataBaseguerra } from '../services/apiService';
import '../styles/AlertPanel.css';

// Agrupar alertas por estaci√≥n
function groupAlertsByStation(alerts) {
  const grouped = {};
  alerts.forEach(alert => {
    const key = alert.Nom_Eds || 'Sin nombre';
    if (!grouped[key]) grouped[key] = [];
    grouped[key].push(alert);
  });
  return grouped;
}

// CONTADOR CORRECTO DE ESTACIONES √öNICAS
function getStationCount(alerts) {
  const estaciones = {};
  alerts.forEach(a => estaciones[a.Nom_Eds || 'Sin nombre'] = true);
  return Object.keys(estaciones).length;
}

// Funci√≥n para parsear fechas en formato "DD-MM-YYYY HH:mm"
function parseFecha(fechaStr) {
  const [datePart, timePart] = fechaStr.split(' ');
  const [day, month, year] = datePart.split('-').map(Number);
  const [hours, minutes] = timePart ? timePart.split(':').map(Number) : [0, 0];
  return new Date(year, month - 1, day, hours, minutes);
}

const ITEMS_PER_PAGE = 50;

const AlertPanel = ({ isVisible, onClose, alertasExternas }) => {
  const [activeTab, setActiveTab] = useState('alertas');
  const [alertas, setAlertas] = useState([]);
  const [historico, setHistorico] = useState([]);
  const [guerraData, setGuerraData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [page, setPage] = useState(1);

  // Estados para filtros agregados
  const [filterNombreEstacion, setFilterNombreEstacion] = useState('');
  const [filterSoloGuerra, setFilterSoloGuerra] = useState(false);

  useEffect(() => {
    if (alertasExternas) {
      setAlertas(alertasExternas);
      setLoading(false);
    } else if (isVisible) {
      loadData();
    }
  }, [isVisible, alertasExternas]);

  const loadData = async () => {
    try {
      setLoading(true);
      const [alertasData, historicoData, guerraDataResult] = await Promise.all([
        getMercadoAlerta(),
        getHistoricoAlerta(),
        getDataBaseguerra()
      ]);

      setAlertas(Array.isArray(alertasData) ? alertasData : []);
      setHistorico(Array.isArray(historicoData) ? historicoData : []);

      const guerraArr = Array.isArray(guerraDataResult) 
        ? guerraDataResult 
        : Array.isArray(guerraDataResult?.data) 
        ? guerraDataResult.data 
        : [];
      setGuerraData(guerraArr);
    } catch {
      setAlertas([]);
      setHistorico([]);
      setGuerraData([]);
    } finally {
      setLoading(false);
    }
  };

  const agrupadas = useMemo(() => groupAlertsByStation(alertas), [alertas]);
  const cantidadEstaciones = useMemo(() => getStationCount(alertas), [alertas]);

  const estacionesGuerra = useMemo(() => {
    if (!Array.isArray(guerraData)) return [];
    return guerraData
      .filter(item => item.Activa === 'Si')
      .sort((a, b) => {
        if (a.Guerra_Precio === 'Si' && b.Guerra_Precio !== 'Si') return -1;
        if (a.Guerra_Precio !== 'Si' && b.Guerra_Precio === 'Si') return 1;
        return (a.Nom_Eds || '').localeCompare(b.Nom_Eds || '');
      });
  }, [guerraData]);

  // Filtrado de hist√≥rico con los filtros nuevos
  const historicoFiltrado = useMemo(() => {
    let filtered = [...historico];

    if (filterNombreEstacion.trim() !== '') {
      const searchTerm = filterNombreEstacion.toLowerCase();
      filtered = filtered.filter(item => 
        (item.Nom_Eds || '').toLowerCase().includes(searchTerm)
      );
    }

    if (filterSoloGuerra) {
      const estacionesEnGuerra = new Set(
        guerraData
          .filter(g => g.Guerra_Precio === 'Si')
          .map(g => g.Nom_Eds)
      );
      filtered = filtered.filter(item => estacionesEnGuerra.has(item.Nom_Eds));
    }

    return filtered;
  }, [historico, filterNombreEstacion, filterSoloGuerra, guerraData]);

  const historicoSorted = useMemo(() => {
    return historicoFiltrado.sort((a, b) => parseFecha(b.Fecha) - parseFecha(a.Fecha));
  }, [historicoFiltrado]);

  const totalPages = Math.max(1, Math.ceil(historicoSorted.length / ITEMS_PER_PAGE));
  const historicoPageSlice = historicoSorted.slice(
    (page - 1) * ITEMS_PER_PAGE, 
    page * ITEMS_PER_PAGE
  );

  const handlePrevPage = () => setPage(p => Math.max(p - 1, 1));
  const handleNextPage = () => setPage(p => Math.min(p + 1, totalPages));

  // Reset p√°gina al cambiar filtros
  useEffect(() => {
    setPage(1);
  }, [filterNombreEstacion, filterSoloGuerra]);

  return (
    <div className={`alert-panel ${isVisible ? 'visible' : ''}`}>
      <div className="alert-panel-header">
        <h2>Panel de Alertas</h2>
        <button className="close-button" onClick={onClose}>‚úï</button>
      </div>

      <div className="alert-tabs">
        <button
          className={activeTab === 'alertas' ? 'active' : ''}
          onClick={() => setActiveTab('alertas')}
        >
          Alertas Activas ({cantidadEstaciones})
        </button>
        <button
          className={activeTab === 'historico' ? 'active' : ''}
          onClick={() => setActiveTab('historico')}
        >
          Hist√≥rico ({historicoSorted.length})
        </button>
        <button
          className={activeTab === 'guerra' ? 'active' : ''}
          onClick={() => setActiveTab('guerra')}
        >
          Guerra de Precios ({estacionesGuerra.filter(e => e.Guerra_Precio === 'Si').length})
        </button>
      </div>

      <div className="alert-content">
        {loading ? (
          <div className="loading">Cargando datos...</div>
        ) : (
          <>
            {activeTab === 'alertas' && <AlertasActivas agrupadas={agrupadas} />}
            {activeTab === 'historico' && (
              <HistoricoPrecios
                historico={historicoPageSlice}
                page={page}
                totalPages={totalPages}
                onPrevPage={handlePrevPage}
                onNextPage={handleNextPage}
                totalItems={historicoSorted.length}
                filterNombreEstacion={filterNombreEstacion}
                setFilterNombreEstacion={setFilterNombreEstacion}
                filterSoloGuerra={filterSoloGuerra}
                setFilterSoloGuerra={setFilterSoloGuerra}
              />
            )}
            {activeTab === 'guerra' && <EstacionesGuerra estaciones={estacionesGuerra} />}
          </>
        )}
      </div>
    </div>
  );
};

const AlertasActivas = ({ agrupadas }) => {
  const estaciones = Object.entries(agrupadas);
  
  if (estaciones.length === 0) 
    return <div className="empty-message">No hay alertas activas en este momento</div>;

  return (
    <div className="alertas-activas">
      {estaciones.map(([nombreEstacion, alertasEstacion]) => (
        <div key={nombreEstacion} className="estacion-card">
          <h3 className="estacion-nombre">{nombreEstacion}</h3>
          <table className="alertas-table">
            <thead>
              <tr>
                <th>Comb.</th>
                <th>Actual</th>
                <th>Anterior</th>
                <th>Dif.</th>
                <th>Hora</th>
                <th>Tipo</th>
              </tr>
            </thead>
            <tbody>
              {alertasEstacion.map((a, idx) => {
                const dif = a.Precio - a.PrecioAnterior;
                return (
                  <tr key={idx}>
                    <td>{a.Combustible}</td>
                    <td>${a.Precio}</td>
                    <td>${a.PrecioAnterior}</td>
                    <td className={dif > 0 ? 'precio-sube' : 'precio-baja'}>
                      {dif > 0 && "+"}{dif}
                    </td>
                    <td>{a.Horario}</td>
                    <td>{a.tipo_atencion}</td>
                  </tr>
                );
              })}
            </tbody>
          </table>
        </div>
      ))}
    </div>
  );
};

const HistoricoPrecios = ({ 
  historico, 
  page, 
  totalPages, 
  onPrevPage, 
  onNextPage, 
  totalItems,
  filterNombreEstacion,
  setFilterNombreEstacion,
  filterSoloGuerra,
  setFilterSoloGuerra
}) => {
  if (historico.length === 0) 
    return <div className="empty-message">No hay datos hist√≥ricos disponibles</div>;

  const formatFecha = (fechaStr) => {
    if (!fechaStr) return { dia: '-', hora: '-' };
    const [datePart, timePart] = fechaStr.split(' ');
    return { dia: datePart || '-', hora: timePart || '-' };
  };

  return (
    <div className="historico-precios">
      {/* Filtros agregados */}
      <div className="filtros-historico">
        <div className="filtro-nombre">
          <label htmlFor="filter-nombre">Buscar estaci√≥n:</label>
          <input
            id="filter-nombre"
            type="text"
            placeholder="Nombre de estaci√≥n..."
            value={filterNombreEstacion}
            onChange={(e) => setFilterNombreEstacion(e.target.value)}
          />
        </div>
        <div className="filtro-guerra">
          <label>
            <input
              type="checkbox"
              checked={filterSoloGuerra}
              onChange={(e) => setFilterSoloGuerra(e.target.checked)}
            />
            Solo estaciones en guerra
          </label>
        </div>
        <div className="filtro-resultados">
          Mostrando {historico.length} de {totalItems} registros
        </div>
      </div>

      <div className="table-container">
        <table className="historico-table">
          <thead>
            <tr>
              <th>Estaci√≥n</th>
              <th>Marca</th>
              <th>CNE</th>
              <th>Comb</th>
              <th>PBL</th>
              <th>P Actual</th>
              <th>P Anterior</th>
              <th>Ajuste</th>
              <th>Tipo</th>
              <th>Horario</th>
              <th>Fecha Actual</th>
              <th>Fecha Anterior</th>
              <th>Fecha Carga</th>
              <th>Marcador</th>
            </tr>
          </thead>
          <tbody>
            {historico.map((item, idx) => {
              const precioActual = parseFloat(item.Precio) || 0;
              const precioAnterior = parseFloat(item.PrecioAnterior) || 0;
              const diferencia = Math.abs(precioActual - precioAnterior);
              const cambioIcon = precioActual > precioAnterior ? '‚¨ÜÔ∏è' : 
                                 precioActual < precioAnterior ? '‚¨áÔ∏è' : '‚û°Ô∏è';
              const cambioSigno = precioActual > precioAnterior ? '+' : 
                                  precioActual < precioAnterior ? '-' : '';
              const tipoIcon = item.tipo_atencion === 'Autoservicio' ? 'üöó' : 'üë§';
              const marcadorPrincipal = item.marcador_principal === 'Si';

              const fechaActual = formatFecha(item.Fecha);
              const fechaAnterior = formatFecha(item.FechaAnterior);
              const fechaCarga = formatFecha(item.fecha_carga);

              return (
                <tr key={idx}>
                  <td>{item.Nom_Eds?.slice(0, 18) || 'S/N'}</td>
                  <td>{item.marca || '-'}</td>
                  <td>{item.cod_cne?.slice(-6) || '-'}</td>
                  <td>{item.Combustible || '-'}</td>
                  <td>{item.pbl || '-'}</td>
                  <td>${precioActual}</td>
                  <td>${precioAnterior}</td>
                  <td>
                    {cambioIcon} {cambioSigno}{diferencia}
                  </td>
                  <td>{tipoIcon}</td>
                  <td>{item.Horario || '-'}</td>
                  <td>{fechaActual.dia} {fechaActual.hora}</td>
                  <td>{fechaAnterior.dia} {fechaAnterior.hora}</td>
                  <td>{fechaCarga.dia} {fechaCarga.hora}</td>
                  <td>{marcadorPrincipal && 'SI'}</td>
                </tr>
              );
            })}
          </tbody>
        </table>
      </div>

      <div className="pagination">
        <button onClick={onPrevPage} disabled={page === 1}>
          ‚Üê Anterior
        </button>
        <span>P√°gina {page} de {totalPages}</span>
        <button onClick={onNextPage} disabled={page === totalPages}>
          Siguiente ‚Üí
        </button>
      </div>
    </div>
  );
};

const EstacionesGuerra = ({ estaciones }) => {
  if (estaciones.length === 0) 
    return <div className="empty-message">No hay estaciones registradas</div>;

  return (
    <div className="estaciones-guerra">
      <table className="guerra-table">
        <thead>
          <tr>
            <th>Estado</th>
            <th>Estaci√≥n</th>
            <th>Tipo</th>
            <th>CNE</th>
            <th>Poblaci√≥n</th>
            <th>Regi√≥n</th>
          </tr>
        </thead>
        <tbody>
          {estaciones.map((item, idx) => (
            <tr key={idx} className={item.Guerra_Precio === 'Si' ? 'en-guerra' : ''}>
              <td>{item.Guerra_Precio === 'Si' ? '‚öîÔ∏è Guerra' : '‚úÖ Normal'}</td>
              <td>{item.Nom_Eds || 'S/N'}</td>
              <td>{item.Tipo_EDS || '-'}</td>
              <td>{item.ID_Cne || '-'}</td>
              <td>{item.PBL || '-'}</td>
              <td>{item.region || '-'}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default AlertPanel;
