import L from 'leaflet';
import { ICON_MAP } from './constants';

export const getIconForMarca = (marca) => {
  return ICON_MAP[marca] || 'default.jpg';
};

// Colores por marca
const brandColors = {
  'Aramco': '#FF6B35',
  'Petrobras': '#FDB913',
  'Copec': '#E60000',
  'Shell': '#FFED00',
  'Blanco': '#FFFFFF',
  'Gulf': '#003DA5',
  'Petroprix': '#006FCC'
};

// Crear icono HTML con Canvas
export const createCustomIcon = (marca, publicUrl = process.env.PUBLIC_URL) => {
  const color = brandColors[marca] || '#999999';
  
  // Crear canvas
  const canvas = document.createElement('canvas');
  canvas.width = 50;
  canvas.height = 60;
  const ctx = canvas.getContext('2d');

  // Fondo blanco con borde
  ctx.fillStyle = color;
  ctx.beginPath();
  ctx.moveTo(25, 0);
  ctx.lineTo(50, 20);
  ctx.lineTo(50, 40);
  ctx.arc(25, 40, 15, 0, Math.PI, false);
  ctx.lineTo(0, 40);
  ctx.lineTo(0, 20);
  ctx.closePath();
  ctx.fill();

  // Borde
  ctx.strokeStyle = 'rgba(0,0,0,0.3)';
  ctx.lineWidth = 1.5;
  ctx.stroke();

  // Círculo interior blanco
  ctx.fillStyle = 'white';
  ctx.beginPath();
  ctx.arc(25, 28, 10, 0, Math.PI * 2);
  ctx.fill();

  // Logo o inicial
  ctx.fillStyle = color;
  ctx.font = 'bold 12px Arial';
  ctx.textAlign = 'center';
  ctx.textBaseline = 'middle';
  ctx.fillText(marca.substring(0, 1), 25, 28);

  // Sombra
  const dataURL = canvas.toDataURL();
  
  return L.icon({
    iconUrl: dataURL,
    iconSize: [40, 50],
    iconAnchor: [20, 50],
    popupAnchor: [0, -50],
    shadowUrl: require('leaflet/dist/images/marker-shadow.png'),
    shadowSize: [41, 41],
    shadowAnchor: [13, 41],
    className: 'custom-marker-icon'
  });
};

export const createBrandLogo = (marca, publicUrl = process.env.PUBLIC_URL) => {
  const iconUrl = getIconForMarca(marca);
  return `${publicUrl}/iconos/${iconUrl}`;
};
