import React, { useState } from 'react';
import { MapContainer, TileLayer } from 'react-leaflet';
import L from 'leaflet';
import IconMarkersLayer from './IconMarkersLayer';
import FilterPanel from './FilterPanel';
import DebugPriceTable from './DebugPriceTable'; // ✅ Importar
import '../styles/layout.css';

const MapComponent = ({ markers, markersForMercado, baseCompData }) => {
  const [selectedRegion, setSelectedRegion] = useState(null);
  const [selectedEds, setSelectedEds] = useState(null); // ✅ EDS seleccionado para debug
  const [selectedCompId, setSelectedCompId] = useState(null); // ✅ Comp ID para debug

  const handleFilterChange = (region, eds) => {
    setSelectedRegion(region);
    setSelectedEds(eds);
  };

  const handleCompetenciaClick = (compId) => {
    setSelectedCompId(compId);
  };

  return (
    <div className="map-layout">
      <div className="filter-panel-wrapper">
        <FilterPanel 
          markers={markers} 
          onFilterChange={handleFilterChange}
          onCompetenciaClick={handleCompetenciaClick}
        />
      </div>

      <div className="map-wrapper">
        <MapContainer
          center={[-33.8688, -51.5305]}
          zoom={12}
          style={{ height: '100%', width: '100%' }}
        >
          <TileLayer
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
            attribution='&copy; OpenStreetMap contributors'
          />
          <IconMarkersLayer
            markers={markers.filter(m => selectedRegion ? m.Region === selectedRegion : true)}
            markersForMercado={markersForMercado}
            selectedRegion={selectedRegion}
            baseCompData={baseCompData}
          />
        </MapContainer>
      </div>

      {/* ✅ COMPONENTE DE DEBUG */}
      {selectedEds && (
        <DebugPriceTable 
          markers={markers}
          baseCompData={baseCompData}
          selectedEds={selectedEds}
          selectedCompId={selectedCompId}
        />
      )}
    </div>
  );
};

export default MapComponent;
