import React, { useState, useEffect, useRef } from 'react';
import '../styles/NotesModal.css';

const NotesModal = ({ isOpen, onClose }) => {
    const [notes, setNotes] = useState('');
    const saveTimeoutRef = useRef(null);

    // Load notes from localStorage on mount
    useEffect(() => {
        const savedNotes = localStorage.getItem('mapNotes');
        if (savedNotes) {
            setNotes(savedNotes);
        }
    }, []);

    // Auto-save notes with debounce
    useEffect(() => {
        if (saveTimeoutRef.current) {
            clearTimeout(saveTimeoutRef.current);
        }

        saveTimeoutRef.current = setTimeout(() => {
            localStorage.setItem('mapNotes', notes);
        }, 500);

        return () => {
            if (saveTimeoutRef.current) {
                clearTimeout(saveTimeoutRef.current);
            }
        };
    }, [notes]);

    const handleClear = () => {
        if (window.confirm('¬øEst√°s seguro de que quieres borrar todas las notas?')) {
            setNotes('');
            localStorage.removeItem('mapNotes');
        }
    };

    if (!isOpen) return null;

    return (
        <div className="notes-modal-overlay" onClick={onClose}>
            <div className="notes-modal-content" onClick={e => e.stopPropagation()}>
                <div className="notes-modal-header">
                    <h2>üìù Notas</h2>
                    <button className="notes-close-btn" onClick={onClose}>‚úï</button>
                </div>
                <div className="notes-modal-body">
                    <textarea
                        className="notes-textarea"
                        placeholder="Escribe tus notas aqu√≠... Se guardar√°n autom√°ticamente."
                        value={notes}
                        onChange={(e) => setNotes(e.target.value)}
                        autoFocus
                    />
                </div>
                <div className="notes-modal-footer">
                    <span className="notes-autosave-indicator">
                        üíæ Guardado autom√°tico
                    </span>
                    <button className="notes-clear-btn" onClick={handleClear}>
                        üóëÔ∏è Borrar todo
                    </button>
                </div>
            </div>
        </div>
    );
};

export default NotesModal;
