import React, { useEffect, useRef, useState, useCallback } from 'react';
import { useMap } from 'react-leaflet';
import L from 'leaflet';
import { createCustomIcon, createBrandLogo } from '../utils/iconHelpers';
import { guardarCoordenadaCorregida } from '../services/apiService';

const IconMarkersLayer = ({ 
  markers,
  markersForMercado,
  selectedRegion,
  baseCompData 
}) => {
  const map = useMap();
  const markersRef = useRef({});
  const zoomUpdateRef = useRef(null);
  const [showingAssociated, setShowingAssociated] = useState(false);
  const [modoCorreccion, setModoCorreccion] = useState(null);

  // ‚úÖ FUNCIONES GLOBALES PARA CORRECCI√ìN DE COORDENADAS
  useEffect(() => {
    window.activarCorreccionCoordenadas = (pbl, eds) => {
      console.log('üéØ Modo correcci√≥n activado para PBL:', pbl);
      
      if (map) {
        map.getContainer().style.cursor = 'crosshair';
      }
      
      setModoCorreccion({ pbl, eds, lat: null, lng: null });
      
      const btnGuardar = document.getElementById(`guardar-coord-${pbl}`);
      if (btnGuardar) {
        btnGuardar.disabled = true;
        btnGuardar.style.opacity = '0.5';
      }
      
      const btnActivar = document.getElementById(`activar-coord-${pbl}`);
      if (btnActivar) {
        btnActivar.textContent = 'Click en mapa...';
        btnActivar.style.background = '#ffc107';
      }
    };

    window.guardarCorreccionCoordenadas = async (pbl, eds) => {
      if (!modoCorreccion || !modoCorreccion.lat || !modoCorreccion.lng) {
        alert('‚ö†Ô∏è Primero selecciona una ubicaci√≥n en el mapa');
        return;
      }

      // ‚úÖ Buscar datos completos del marcador
      const marker = markers.find(m => m.pbl === pbl);

      try {
        const coordenada = {
          id: marker?.id || pbl,
          pbl: pbl,
          eds: eds || marker?.eds || '',
          marca: marker?.Marca || '',
          comuna: marker?.Comuna || '',
          lat_corregida: modoCorreccion.lat,
          lon_corregida: modoCorreccion.lng
        };

        console.log('üíæ Guardando coordenada:', coordenada);

        await guardarCoordenadaCorregida(coordenada);
        
        console.log('‚úÖ Coordenada guardada:', coordenada);
        alert('‚úÖ Coordenada guardada correctamente');
        
        setModoCorreccion(null);
        if (map) {
          map.getContainer().style.cursor = '';
        }
        
        window.location.reload();
        
      } catch (error) {
        console.error('‚ùå Error al guardar:', error);
        alert('‚ùå Error al guardar coordenada: ' + error.message);
      }
    };

    return () => {
      delete window.activarCorreccionCoordenadas;
      delete window.guardarCorreccionCoordenadas;
    };
  }, [map, modoCorreccion, markers]);

  // ‚úÖ EVENTO: Click en el mapa para capturar coordenadas
  useEffect(() => {
    if (!map || !modoCorreccion) return;

    const handleMapClick = (e) => {
      const { lat, lng } = e.latlng;
      
      console.log('üìç Coordenadas seleccionadas:', { lat, lng });
      
      setModoCorreccion(prev => ({
        ...prev,
        lat: lat,
        lng: lng
      }));
      
      const latSpan = document.getElementById(`lat-actual-${modoCorreccion.pbl}`);
      const lngSpan = document.getElementById(`lon-actual-${modoCorreccion.pbl}`);
      
      if (latSpan) latSpan.textContent = lat.toFixed(6);
      if (lngSpan) lngSpan.textContent = lng.toFixed(6);
      
      const btnGuardar = document.getElementById(`guardar-coord-${modoCorreccion.pbl}`);
      if (btnGuardar) {
        btnGuardar.disabled = false;
        btnGuardar.style.opacity = '1';
      }
      
      const btnActivar = document.getElementById(`activar-coord-${modoCorreccion.pbl}`);
      if (btnActivar) {
        btnActivar.textContent = 'Coord. Seleccionada ‚úì';
        btnActivar.style.background = '#28a745';
      }
      
      map.getContainer().style.cursor = '';
    };

    map.on('click', handleMapClick);

    return () => {
      map.off('click', handleMapClick);
    };
  }, [map, modoCorreccion]);

  const clearMarkers = useCallback(() => {
    if (!map) return;
    Object.values(markersRef.current).forEach((marker) => {
      try {
        if (map.hasLayer(marker)) map.removeLayer(marker);
      } catch {}
    });
    markersRef.current = {};
  }, [map]);

  const makeDraggable = useCallback((popup) => {
    if (!map || !popup || !popup.getElement) return;
    const el = popup.getElement();
    if (!el) return;
    try {
      const wrapper = el.querySelector('.leaflet-popup-content-wrapper');
      if (!wrapper) return;
      const draggable = new L.Draggable(el, wrapper);
      draggable.enable();
    } catch {}
  }, [map]);

  const generarPopupContent = useCallback((marker, datosTabla, tablaPreciosHtml, variant = 'primary') => {
    const isSecondary = variant === 'secondary';
    
    const logoUrl = marker.logo 
      ? (marker.logo.startsWith('http') 
          ? marker.logo 
          : `https://api.bencinaenlinea.cl${marker.logo}`)
      : '';

    let content = `
      <div class="icon-markers-popup-content">
        <div class="popup-info-header" style="position: relative;">
          ${logoUrl ? `
            <img 
              src="${logoUrl}" 
              alt="Logo ${marker.Marca}" 
              class="popup-logo"
              onerror="this.style.display='none';"
            />
          ` : ''}
          
          <div class="popup-info-row">
            <span class="popup-label">PBL:</span>
            <span class="popup-value">${marker.pbl || '-'}</span>
          </div>
          <div class="popup-info-row">
            <span class="popup-label">Estaci√≥n:</span>
            <span class="popup-value">${marker.nombre || '-'}</span>
          </div>
          <div class="popup-info-row">
            <span class="popup-label">Jefe Zona:</span>
            <span class="popup-value">${marker.Marca || '-'}</span>
          </div>
          <div class="popup-info-row">
            <span class="popup-label">Operaci√≥n:</span>
            <span class="popup-value">Comisionista</span>
          </div>
        </div>

        <div class="popup-tabs-container">
          <div class="popup-tabs-header">
            <button class="popup-tab-button active" data-tab="precios">Precios</button>
            <button class="popup-tab-button" data-tab="coordenadas">Coordenadas</button>
            <button class="popup-tab-button" data-tab="informacion">Informaci√≥n</button>
          </div>

          <div class="popup-tabs-content">
            <!-- TAB 1: PRECIOS -->
            <div class="popup-tab-pane active" data-tab="precios">
              ${tablaPreciosHtml}
            </div>

            <!-- TAB 2: COORDENADAS -->
            <div class="popup-tab-pane" data-tab="coordenadas">
              <div class="popup-info-box">
                <p><strong>Lat corregida:</strong> <span id="lat-actual-${marker.pbl}">${marker.lat.toFixed(6)}</span></p>
                <p><strong>Lon corregida:</strong> <span id="lon-actual-${marker.pbl}">${marker.lng.toFixed(6)}</span></p>
                <hr style="margin: 8px 0; border: none; border-top: 1px solid #dee2e6;">
                <p><strong>Lat original:</strong> ${marker.lat_original ? marker.lat_original.toFixed(6) : '-'}</p>
                <p><strong>Lon original:</strong> ${marker.lng_original ? marker.lng_original.toFixed(6) : '-'}</p>
              </div>
              
              <div style="display: flex; gap: 8px; margin-top: 10px;">
                <button 
                  id="activar-coord-${marker.pbl}"
                  style="flex: 1; padding: 8px; background: #17a2b8; color: white; border: none; border-radius: 4px; cursor: pointer; font-size: 11px; font-weight: 600;"
                  onclick="window.activarCorreccionCoordenadas('${marker.pbl}', '${marker.eds}')"
                >
                  Activar Coord
                </button>
                
                <button 
                  id="guardar-coord-${marker.pbl}"
                  style="flex: 1; padding: 8px; background: #28a745; color: white; border: none; border-radius: 4px; cursor: pointer; font-size: 11px; font-weight: 600; opacity: 0.5;"
                  onclick="window.guardarCorreccionCoordenadas('${marker.pbl}', '${marker.eds}')"
                  disabled
                >
                  Guardar
                </button>
              </div>
            </div>

            <!-- TAB 3: INFORMACI√ìN -->
            <div class="popup-tab-pane" data-tab="informacion">
              <div class="popup-info-box">
                <p><strong>EDS:</strong> ${marker.eds || '-'}</p>
                <p><strong>Direcci√≥n:</strong> ${marker.direccion || '-'}</p>
                ${marker.Guerra_Precio === 'Si' ? `<p><strong style="color: #d9534f;">‚ö†Ô∏è Guerra de Precio Activa</strong></p>` : ''}
              </div>
            </div>
          </div>
        </div>
      </div>
    `;

    if (marker.pbl && (marker.Marca === 'Aramco' || marker.Marca === 'Petrobras')) {
      content += `<button class="popup-button-mercado ${isSecondary ? 'secondary' : ''}" onclick="window.showAssociatedIds && window.showAssociatedIds(${marker.pbl}, ${marker.lat}, ${marker.lng}, '${marker.id}')">Mercado</button>`;
    }

    return content;
  }, []);

  const bindPopupWithMercado = useCallback((leafletMarker, marker) => {
    const preciosHistoricos = marker.preciosHistoricos || [];
    
    let tablaPreciosHtml = '<div class="popup-table-wrapper"><table class="popup-table"><thead><tr>';
    tablaPreciosHtml += '<th>Fecha</th><th>G93</th><th>G95</th><th>G97</th><th>Diesel</th><th>Kero</th></tr></thead><tbody>';
    
    if (preciosHistoricos.length > 0) {
      preciosHistoricos.forEach(precio => {
        tablaPreciosHtml += `<tr>
          <td>${precio.Actualizacion || '-'}</td>
          <td>${precio.G93 || '-'}</td>
          <td>${precio.G95 || '-'}</td>
          <td>${precio.G97 || '-'}</td>
          <td>${precio.Diesel || '-'}</td>
          <td>${precio.Kerosene || '-'}</td>
        </tr>`;
      });
    } else {
      tablaPreciosHtml += '<tr><td colspan="6">No hay datos disponibles</td></tr>';
    }
    
    tablaPreciosHtml += '</tbody></table></div>';

    const popupHtml = generarPopupContent(marker, null, tablaPreciosHtml, 'primary');

    leafletMarker.bindPopup(popupHtml, { maxWidth: 500, className: 'custom-popup' });

    leafletMarker.on('popupopen', (e) => {
      const popup = e.popup;
      makeDraggable(popup);
      
      setTimeout(() => {
        const tabButtons = document.querySelectorAll('.popup-tab-button');
        const tabPanes = document.querySelectorAll('.popup-tab-pane');
        
        tabButtons.forEach(button => {
          button.addEventListener('click', () => {
            tabButtons.forEach(btn => btn.classList.remove('active'));
            tabPanes.forEach(pane => pane.classList.remove('active'));
            
            button.classList.add('active');
            const tab = button.getAttribute('data-tab');
            const pane = document.querySelector(`.popup-tab-pane[data-tab="${tab}"]`);
            if (pane) pane.classList.add('active');
          });
        });
      }, 100);
    });
  }, [generarPopupContent, makeDraggable]);

  useEffect(() => {
    if (!map || !markers) return;

    clearMarkers();

    markers.forEach((marker) => {
      try {
        const icon = createCustomIcon(marker);
        const leafletMarker = L.marker([marker.lat, marker.lng], { icon });
        bindPopupWithMercado(leafletMarker, marker);
        leafletMarker.addTo(map);
        markersRef.current[marker.id] = leafletMarker;
      } catch (error) {
        console.error('Error creating marker:', error);
      }
    });

    return () => clearMarkers();
  }, [map, markers, bindPopupWithMercado, clearMarkers]);

  useEffect(() => {
    window.showAssociatedIds = (pbl, lat, lng, mainId) => {
      if (!map || !markersForMercado) return;

      clearMarkers();
      setShowingAssociated(true);

      const filtered = markersForMercado.filter(m => m.pbl === pbl);

      if (filtered.length === 0) {
        console.warn('No associated markers found for PBL:', pbl);
        return;
      }

      filtered.forEach(marker => {
        try {
          const isMain = marker.id === mainId;
          const variant = isMain ? 'primary' : 'secondary';
          const icon = isMain ? createCustomIcon(marker) : createBrandLogo(marker.Marca, marker);

          const leafletMarker = L.marker([marker.lat, marker.lng], { icon });

          const preciosHistoricos = marker.preciosHistoricos || [];
          let tablaPreciosHtml = `<div class="popup-table-wrapper ${variant === 'secondary' ? 'secondary' : ''}"><table class="popup-table ${variant === 'secondary' ? 'secondary' : ''}"><thead><tr>`;
          tablaPreciosHtml += '<th>Fecha</th><th>G93</th><th>G95</th><th>G97</th><th>Diesel</th><th>Kero</th></tr></thead><tbody>';

          if (preciosHistoricos.length > 0) {
            preciosHistoricos.forEach(precio => {
              tablaPreciosHtml += `<tr>
                <td>${precio.Actualizacion || '-'}</td>
                <td>${precio.G93 || '-'}</td>
                <td>${precio.G95 || '-'}</td>
                <td>${precio.G97 || '-'}</td>
                <td>${precio.Diesel || '-'}</td>
                <td>${precio.Kerosene || '-'}</td>
              </tr>`;
            });
          } else {
            tablaPreciosHtml += '<tr><td colspan="6">No hay datos disponibles</td></tr>';
          }

          tablaPreciosHtml += '</tbody></table></div>';

          const popupHtml = generarPopupContent(marker, null, tablaPreciosHtml, variant);

          leafletMarker.bindPopup(popupHtml, { maxWidth: 500, className: 'custom-popup' });

          leafletMarker.on('popupopen', (e) => {
            const popup = e.popup;
            makeDraggable(popup);
            
            setTimeout(() => {
              const tabButtons = document.querySelectorAll('.popup-tab-button');
              const tabPanes = document.querySelectorAll('.popup-tab-pane');
              
              tabButtons.forEach(button => {
                button.addEventListener('click', () => {
                  tabButtons.forEach(btn => btn.classList.remove('active'));
                  tabPanes.forEach(pane => pane.classList.remove('active'));
                  
                  button.classList.add('active');
                  const tab = button.getAttribute('data-tab');
                  const pane = document.querySelector(`.popup-tab-pane[data-tab="${tab}"]`);
                  if (pane) pane.classList.add('active');
                });
              });
            }, 100);
          });

          leafletMarker.addTo(map);
          markersRef.current[marker.id] = leafletMarker;
        } catch (error) {
          console.error('Error creating associated marker:', error);
        }
      });

      if (lat && lng) {
        map.setView([lat, lng], 15);
      }
    };

    return () => {
      delete window.showAssociatedIds;
    };
  }, [map, markersForMercado, generarPopupContent, makeDraggable, clearMarkers]);

  return null;
};

export default IconMarkersLayer;
