import React, { useState, useEffect, useRef } from 'react';
import { MapContainer, TileLayer, useMap } from 'react-leaflet';
import IconMarkersLayer from './IconMarkersLayer';
import 'leaflet/dist/leaflet.css';

const TILE_LAYERS = {
  streets: {
    name: 'Calles',
    icon: 'üó∫Ô∏è',
    url: 'https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png',
    attribution: '¬© OpenStreetMap'
  },
  satellite: {
    name: 'Sat√©lite',
    icon: 'üõ∞Ô∏è',
    url: 'https://server.arcgisonline.com/ArcGIS/rest/services/World_Imagery/MapServer/tile/{z}/{y}/{x}',
    attribution: '¬© Esri'
  },
  hybrid: {
    name: 'H√≠brido',
    icon: 'üåç',
    url: 'https://server.arcgisonline.com/ArcGIS/rest/services/World_Imagery/MapServer/tile/{z}/{y}/{x}',
    attribution: '¬© Esri'
  }
};

function MapController({ regionCenter }) {
  const map = useMap();

  useEffect(() => {
    if (regionCenter && regionCenter.length === 2) {
      map.setView(regionCenter, 10);
    }
  }, [regionCenter, map]);

  useEffect(() => {
    window.leafletMap = map;
  }, [map]);

  useEffect(() => {
    const timer = setTimeout(() => {
      map.invalidateSize();
    }, 100);
    return () => clearTimeout(timer);
  }, [map]);

  return null;
}

function MapComponent({ 
  selectedRegion, 
  regionCenter, 
  markers, 
  markersForMercado, 
  baseCompData,
  onToggleSidebar,
  onToggleRightPanel,
  sidebarVisible,
  rightPanelVisible
}) {
  const [selectedLayer, setSelectedLayer] = useState('streets');
  const [searchQuery, setSearchQuery] = useState('');
  const [searchResults, setSearchResults] = useState([]);
  const [showResults, setShowResults] = useState(false);
  const [controlPanelOpen, setControlPanelOpen] = useState(false);
  const searchTimeoutRef = useRef(null);

  const handleSearch = (query) => {
    setSearchQuery(query);
    
    if (query.length < 3) {
      setSearchResults([]);
      setShowResults(false);
      return;
    }

    if (searchTimeoutRef.current) {
      clearTimeout(searchTimeoutRef.current);
    }

    searchTimeoutRef.current = setTimeout(async () => {
      try {
        const response = await fetch(
          `https://nominatim.openstreetmap.org/search?format=json&q=${encodeURIComponent(query)}&countrycodes=cl&limit=5&addressdetails=1`,
          {
            headers: {
              'User-Agent': 'MapaEstaciones/1.0',
              'Accept-Language': 'es'
            }
          }
        );
        
        const data = await response.json();
        setSearchResults(data);
        setShowResults(data.length > 0);
      } catch (error) {
        console.error('Error en b√∫squeda:', error);
      }
    }, 500);
  };

  const selectLocation = (result) => {
    const map = window.leafletMap;
    if (map) {
      const lat = parseFloat(result.lat);
      const lon = parseFloat(result.lon);
      map.setView([lat, lon], 17);
    }
    
    setSearchQuery(result.display_name);
    setShowResults(false);
  };

  return (
    <div className="map-wrapper">
      {/* PANEL DE CONTROL UNIFICADO */}
      <div className="map-controls-unified">
        <button 
          className="control-toggle-btn"
          onClick={() => setControlPanelOpen(!controlPanelOpen)}
          title="Controles"
        >
          ‚ò∞
        </button>

        {controlPanelOpen && (
          <div className="control-panel-dropdown">
            {/* BUSCADOR COMPACTO */}
            <div className="control-section">
              <input
                type="text"
                className="search-input-compact"
                placeholder="üîç Buscar direcci√≥n..."
                value={searchQuery}
                onChange={(e) => handleSearch(e.target.value)}
                onFocus={() => searchResults.length > 0 && setShowResults(true)}
              />
              
              {showResults && searchResults.length > 0 && (
                <div 
                  className="search-results-compact"
                  onMouseDown={(e) => e.preventDefault()}
                >
                  {searchResults.map((result, index) => (
                    <div
                      key={index}
                      className="search-result-item-compact"
                      onClick={() => selectLocation(result)}
                    >
                      üìç {result.display_name}
                    </div>
                  ))}
                </div>
              )}
            </div>

            {/* CAPAS */}
            <div className="control-section">
              <div className="control-section-title">Capas del Mapa</div>
              <div className="layer-buttons-compact">
                {Object.entries(TILE_LAYERS).map(([key, layer]) => (
                  <button
                    key={key}
                    className={`layer-btn-compact ${selectedLayer === key ? 'active' : ''}`}
                    onClick={() => setSelectedLayer(key)}
                    title={layer.name}
                  >
                    {layer.icon} {layer.name}
                  </button>
                ))}
              </div>
            </div>

            {/* PANELES */}
            <div className="control-section">
              <div className="control-section-title">Paneles</div>
              <button 
                className={`panel-toggle-btn ${sidebarVisible ? 'active' : ''}`}
                onClick={onToggleSidebar}
              >
                üîΩ {sidebarVisible ? 'Ocultar' : 'Mostrar'} Filtros
              </button>
              <button 
                className={`panel-toggle-btn ${rightPanelVisible ? 'active' : ''}`}
                onClick={onToggleRightPanel}
              >
                üìä {rightPanelVisible ? 'Ocultar' : 'Mostrar'} Datos
              </button>
            </div>
          </div>
        )}
      </div>

      <MapContainer
        center={regionCenter || [-33.4489, -70.6693]}
        zoom={10}
        className="map"
        zoomControl={true}
      >
        <TileLayer
          url={TILE_LAYERS[selectedLayer].url}
          attribution={TILE_LAYERS[selectedLayer].attribution}
          maxZoom={19}
        />
        
        {selectedLayer === 'hybrid' && (
          <TileLayer
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
            attribution=""
            opacity={0.4}
          />
        )}

        <MapController regionCenter={regionCenter} />
        
        <IconMarkersLayer
          markers={markers}
          markersForMercado={markersForMercado}
          selectedRegion={selectedRegion}
          baseCompData={baseCompData}
        />
      </MapContainer>
    </div>
  );
}

export default MapComponent;
