import { useState, useEffect } from 'react';

export const useMapData = () => {
  const [markers, setMarkers] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchData = async () => {
      try {
        // Simulamos 2000 marcadores
        const mockData = Array.from({ length: 2000 }, (_, i) => ({
          id: i + 1,
          lat: -33.8688 + (Math.random() - 0.5) * 2,
          lng: -70.9123 + (Math.random() - 0.5) * 2,
          Marca: ['Petrobras', 'Copec', 'Shell', 'Blanco'][Math.floor(Math.random() * 4)],
          nombre: `Estación ${i + 1}`,
          precio_g93: 1235 + Math.floor(Math.random() * 50),
          precio_g95: 1279 + Math.floor(Math.random() * 50),
          precio_diesel: 1324 + Math.floor(Math.random() * 50)
        }));
        
        setMarkers(mockData);
        setLoading(false);
      } catch (error) {
        console.error("Error loading data:", error);
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  return { markers, loading };
};
