import React, { useEffect, useRef, useState, useCallback } from 'react';
import { useMap } from 'react-leaflet';
import L from 'leaflet';
import { createCustomIcon, createBrandLogo } from '../utils/iconHelpers';

const IconMarkersLayer = ({ markers, markersForMercado = null, selectedRegion, baseCompData = [] }) => {
  const map = useMap();
  const markersRef = useRef({});
  const cacheRef = useRef({});
  const updateInProgressRef = useRef(false);
  const zoomUpdateRef = useRef(null);
  const [showingAssociated, setShowingAssociated] = useState(false);
  const [originalPopupMarker, setOriginalPopupMarker] = useState(null);
  const [activeMercadoPbl, setActiveMercadoPbl] = useState(null);
  const competenciaMarkersRef = useRef([]);
  const polylineRef = useRef([]);
  const [debugTable, setDebugTable] = useState(null);
  const [maxHeight, setMaxHeight] = useState(500); // ‚úÖ Altura configurable

  const makeDraggable = useCallback((popup) => {
    if (!map || !popup || !popup._container) return;
    try {
      const pos = map.latLngToLayerPoint(popup.getLatLng());
      L.DomUtil.setPosition(popup._wrapper.parentNode, pos);
      const draggable = new L.Draggable(popup._container, popup._wrapper);
      draggable.enable();
    } catch (error) {
      console.error('Error draggable:', error);
    }
  }, [map]);

  const clearCompetenciaMarkers = useCallback(() => {
    competenciaMarkersRef.current.forEach(marker => {
      try {
        if (map && map.hasLayer(marker)) {
          map.removeLayer(marker);
        }
      } catch (e) {
        console.error('Error limpiando competencia:', e);
      }
    });
    competenciaMarkersRef.current = [];
  }, [map]);

  const clearPolylines = useCallback(() => {
    polylineRef.current.forEach(line => {
      try {
        if (map && map.hasLayer(line)) {
          map.removeLayer(line);
        }
      } catch (e) {
        console.error('Error limpiando polyline:', e);
      }
    });
    polylineRef.current = [];
  }, [map]);

  const getButtonContent = useCallback((pbl) => {
    const isActive = activeMercadoPbl === pbl && showingAssociated;
    return isActive ? '‚ùå Cerrar Mercado' : 'üõí Abrir Mercado';
  }, [activeMercadoPbl, showingAssociated]);

  const updateMarkerButtons = useCallback(() => {
    Object.values(markersRef.current).forEach(marker => {
      if (marker._popup) {
        const latLng = marker.getLatLng();
        const markerData = markers.find(m => m.lat === latLng.lat && m.lng === latLng.lng);
        
        if (markerData && markerData.pbl && (markerData.Marca === 'Aramco' || markerData.Marca === 'Petrobras')) {
          const oldContent = marker._popup.getContent();
          if (typeof oldContent === 'string') {
            const updatedContent = oldContent.replace(
              /(<button class="associated-button"[^>]*>).*?(<\/button>)/g,
              `$1${getButtonContent(markerData.pbl)}$2`
            );
            marker._popup.setContent(updatedContent);
          }
        }
      }
    });
  }, [markers, getButtonContent]);

  // ‚úÖ Tabla con estilos mejorados y responsive
  const generarTablaPrecios = useCallback((datos, variant = 'primary') => {
    if (!datos || datos.length === 0) {
      return '<div style="padding: 12px; text-align: center; font-size: 12px; color: #999; font-style: italic;">Sin datos disponibles</div>';
    }

    const headerBg = variant === 'primary' 
      ? '#007bff'
      : '#28a745';
    const headerHover = variant === 'primary'
      ? '#0056b3'
      : '#1e7e34';
    const bodyBg = variant === 'primary' ? '#f8f9fa' : '#f0f9f4';
    const borderColor = variant === 'primary' ? '#dee2e6' : '#d4edda';
    const rowHover = variant === 'primary' ? '#e7f3ff' : '#e8f5e9';

    let html = `
      <div style="
        overflow-x: auto;
        border-radius: 6px;
        border: 1px solid ${borderColor};
        margin: 8px 0;
      ">
        <table style="
          width: 100%;
          border-collapse: collapse;
          font-size: 11px;
          background: white;
          margin: 0;
        ">
          <thead>
            <tr style="
              background: ${headerBg};
              color: white;
              position: sticky;
              top: 0;
              z-index: 10;
            ">
              <th style="padding: 10px 8px; border: 1px solid ${borderColor}; font-weight: 600; text-align: center; font-size: 10px;">Actualizaci√≥n</th>
              <th style="padding: 10px 8px; border: 1px solid ${borderColor}; font-weight: 600; text-align: center; font-size: 10px;">G93</th>
              <th style="padding: 10px 8px; border: 1px solid ${borderColor}; font-weight: 600; text-align: center; font-size: 10px;">G95</th>
              <th style="padding: 10px 8px; border: 1px solid ${borderColor}; font-weight: 600; text-align: center; font-size: 10px;">G97</th>
              <th style="padding: 10px 8px; border: 1px solid ${borderColor}; font-weight: 600; text-align: center; font-size: 10px;">Diesel</th>
              <th style="padding: 10px 8px; border: 1px solid ${borderColor}; font-weight: 600; text-align: center; font-size: 10px;">Kero</th>
            </tr>
          </thead>
          <tbody>
    `;

    datos.forEach((fila, idx) => {
      const bgcolor = idx % 2 === 0 ? bodyBg : '#ffffff';
      html += `
        <tr style="
          background: ${bgcolor};
          transition: background 0.2s;
        " onmouseover="this.style.background='${rowHover}'" onmouseout="this.style.background='${bgcolor}'">
          <td style="padding: 10px 8px; border: 1px solid ${borderColor}; text-align: center; font-size: 10px; color: #555;">${fila.fecha}</td>
          <td style="padding: 10px 8px; border: 1px solid ${borderColor}; text-align: center; font-weight: 500; color: #333;">${fila.g93 || '-'}</td>
          <td style="padding: 10px 8px; border: 1px solid ${borderColor}; text-align: center; font-weight: 500; color: #333;">${fila.g95 || '-'}</td>
          <td style="padding: 10px 8px; border: 1px solid ${borderColor}; text-align: center; font-weight: 500; color: #333;">${fila.g97 || '-'}</td>
          <td style="padding: 10px 8px; border: 1px solid ${borderColor}; text-align: center; font-weight: 500; color: #333;">${fila.diesel || '-'}</td>
          <td style="padding: 10px 8px; border: 1px solid ${borderColor}; text-align: center; font-weight: 500; color: #333;">${fila.kero || '-'}</td>
        </tr>
      `;
    });

    html += `
          </tbody>
        </table>
      </div>
    `;

    return html;
  }, []);

  // ‚úÖ Obtener TODOS los registros
  const obtenerHistoricoMarker = useCallback((markerId, dataArray) => {
    const datos = dataArray.filter(m => m.id === markerId);
    
    return datos
      .sort((a, b) => {
        const fechaA = a.fecha_aplicacion ? new Date(a.fecha_aplicacion) : new Date(0);
        const fechaB = b.fecha_aplicacion ? new Date(b.fecha_aplicacion) : new Date(0);
        return fechaB - fechaA;
      })
      .map(item => ({
        fecha: item.fecha_aplicacion || 'S/F',
        g93: item.precio_g93 || '-',
        g95: item.precio_g95 || '-',
        g97: item.precio_g97 || '-',
        diesel: item.precio_diesel || '-',
        kero: item.precio_kero || '-'
      }));
  }, []);

  const updateIconsInViewport = useCallback((allMarkers, clearFirst = true) => {
    if (!map || updateInProgressRef.current) return;

    updateInProgressRef.current = true;
    const bounds = map.getBounds();
    const visibleMarkers = allMarkers.filter(marker => {
      return bounds.contains([marker.lat, marker.lng]);
    });

    if (clearFirst) {
      Object.values(markersRef.current).forEach(marker => {
        try {
          if (map.hasLayer(marker)) {
            map.removeLayer(marker);
          }
        } catch (error) {
          console.error('Error removiendo:', error);
        }
      });
      markersRef.current = {};

      map.eachLayer((layer) => {
        if (layer instanceof L.Marker && !map.hasLayer(layer)) {
          try {
            map.removeLayer(layer);
          } catch (e) {}
        }
      });
    }

    if (visibleMarkers.length === 0) {
      updateInProgressRef.current = false;
      return;
    }

    const markersByBrand = {};
    visibleMarkers.forEach(marker => {
      if (!markersByBrand[marker.Marca]) {
        markersByBrand[marker.Marca] = [];
      }
      markersByBrand[marker.Marca].push(marker);
    });

    const brandOrder = ['Aramco', 'Petrobras', 'Copec', 'Shell', 'Blanco', 'Gulf', 'Petroprix'];
    let delayOffset = 0;
    const cacheKey = selectedRegion;
    let totalBrands = brandOrder.filter(b => markersByBrand[b]).length;
    let brandsProcessed = 0;
    
    brandOrder.forEach((brand) => {
      if (!markersByBrand[brand] || markersByBrand[brand].length === 0) return;

      const brandMarkers = markersByBrand[brand];
      
      setTimeout(() => {
        brandMarkers.forEach((marker) => {
          try {
            const markerId = `${cacheKey}-${marker.id}`;

            if (markersRef.current[markerId]) {
              return;
            }

            const lat = parseFloat(marker.lat);
            const lng = parseFloat(marker.lng);

            if (!isNaN(lat) && !isNaN(lng)) {
              const iconUrl = createCustomIcon(marker.Marca);
              const leafletMarker = L.marker([lat, lng], { icon: iconUrl }).addTo(map);

              const datosTabla = obtenerHistoricoMarker(marker.id, allMarkers);
              const tablaPreciosHtml = generarTablaPrecios(datosTabla, 'primary');

              leafletMarker.on('popupopen', () => {
                setDebugTable({
                  titulo: `${marker.Marca} - ${marker.nombre}`,
                  registros: datosTabla.length,
                  html: tablaPreciosHtml
                });
              });

              // ‚úÖ Popup con estilos mejorados
              let popupContent = `
                <div style="
                  font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
                  min-width: 320px;
                  color: #333;
                ">
                  <div style="
                    background: linear-gradient(135deg, #007bff 0%, #0056b3 100%);
                    color: white;
                    padding: 12px;
                    border-radius: 6px 6px 0 0;
                    margin: -12px -12px 12px -12px;
                    display: flex;
                    align-items: center;
                    gap: 10px;
                  ">
                    <img 
                      src="${createBrandLogo(marker.Marca)}"
                      alt="${marker.Marca}"
                      style="width: 35px; height: 35px; border-radius: 50%; object-fit: cover; border: 2px solid white;"
                      onerror="this.style.display='none'"
                    />
                    <div>
                      <h3 style="margin: 0; font-size: 14px; font-weight: 700;">${marker.nombre || marker.Marca}</h3>
                      <p style="margin: 2px 0 0 0; font-size: 11px; opacity: 0.9;">${marker.Marca}</p>
                    </div>
                  </div>

                  <div style="padding: 0;">
                    <div style="
                      background: #f8f9fa;
                      padding: 10px;
                      border-radius: 4px;
                      margin-bottom: 10px;
                    ">
                      <p style="margin: 5px 0; font-size: 11px;"><strong>PBL:</strong> ${marker.pbl || '-'}</p>
                      <p style="margin: 5px 0; font-size: 11px;"><strong>Regi√≥n:</strong> ${marker.Region || '-'}</p>
                      <p style="margin: 5px 0; font-size: 11px;"><strong>Comuna:</strong> ${marker.Comuna || '-'}</p>
                      <p style="margin: 5px 0; font-size: 11px;"><strong>EDS:</strong> ${marker.eds || '-'}</p>
                      ${marker.direccion ? `<p style="margin: 5px 0; font-size: 11px;"><strong>Direcci√≥n:</strong> ${marker.direccion}</p>` : ''}
                    </div>

                    <h4 style="
                      margin: 10px 0 8px 0;
                      font-size: 12px;
                      font-weight: 600;
                      color: #007bff;
                      border-bottom: 2px solid #007bff;
                      padding-bottom: 6px;
                    ">üìä Hist√≥rico (${datosTabla.length} registros)</h4>
                    
                    ${tablaPreciosHtml}

                    ${marker.Guerra_Precio === 'Si' ? `
                      <div style="
                        margin-top: 10px;
                        padding: 8px;
                        background: #fff3cd;
                        border: 1px solid #ffc107;
                        border-radius: 4px;
                        color: #856404;
                        font-weight: 600;
                        font-size: 11px;
                        text-align: center;
                      ">
                        ‚ö†Ô∏è Guerra de Precio Activa
                      </div>
                    ` : ''}
                  </div>
                </div>
              `;

              if (marker.pbl && (marker.Marca === 'Aramco' || marker.Marca === 'Petrobras')) {
                popupContent += `<button class="associated-button" onclick="window.showAssociatedIds && window.showAssociatedIds(${marker.pbl}, ${marker.lat}, ${marker.lng}, '${marker.id}')" style="
                  width: 100%;
                  padding: 10px;
                  margin-top: 10px;
                  background: #007bff;
                  color: white;
                  border: none;
                  border-radius: 4px;
                  cursor: pointer;
                  font-weight: 600;
                  font-size: 12px;
                  transition: background 0.2s;
                " onmouseover="this.style.background='#0056b3'" onmouseout="this.style.background='#007bff'">üõí Abrir Mercado</button>`;
              }

              const popup = L.popup({
                autoClose: false,
                closeOnClick: false,
                keepInView: true,
                autoPan: false,
                maxWidth: 380,
                className: 'custom-popup'
              }).setContent(popupContent);

              leafletMarker.bindPopup(popup);

              leafletMarker.on('popupopen', () => {
                makeDraggable(popup);
              });

              leafletMarker.on('click', () => {
                leafletMarker.openPopup();
              });

              markersRef.current[markerId] = leafletMarker;

              cacheRef.current[markerId] = {
                lat: marker.lat,
                lng: marker.lng,
                marca: marker.Marca,
                nombre: marker.nombre,
                region: marker.Region
              };
            }
          } catch (error) {
            console.error(`Error:`, error);
          }
        });

        brandsProcessed++;
        localStorage.setItem('markersCache', JSON.stringify(cacheRef.current));

        if (brandsProcessed === totalBrands) {
          updateInProgressRef.current = false;
        }

      }, delayOffset);

      delayOffset += 60;
    });
  }, [map, makeDraggable, selectedRegion, generarTablaPrecios, obtenerHistoricoMarker]);

  const showAssociatedIds = useCallback((pbl, lat, lng, originalMarkerId) => {
    const searchArray = markersForMercado && markersForMercado.length > 0 ? markersForMercado : markers;
    
    if (showingAssociated && activeMercadoPbl === pbl) {
      clearCompetenciaMarkers();
      clearPolylines();
      
      Object.values(markersRef.current).forEach(marker => {
        try {
          if (map.hasLayer(marker)) {
            map.removeLayer(marker);
          }
        } catch (error) {
          console.error('Error removiendo:', error);
        }
      });
      markersRef.current = {};
      
      setShowingAssociated(false);
      setActiveMercadoPbl(null);
      setOriginalPopupMarker(null);
      
      const markersToShow = markersForMercado && markersForMercado.length > 0 ? markersForMercado : markers;
      
      if (map && markersToShow && markersToShow.length > 0) {
        updateIconsInViewport(markersToShow, true);
      }
      
      return;
    }

    clearCompetenciaMarkers();
    clearPolylines();

    const associatedData = baseCompData.filter(item => 
      String(item.pbl).trim() === String(pbl).trim()
    );

    if (associatedData.length === 0) {
      return;
    }

    const ids = associatedData.map(item => item.id);

    if (map) {
      let openPopup = null;
      map.eachLayer((layer) => {
        if (layer instanceof L.Marker && layer._popup && layer._popup.isOpen()) {
          openPopup = layer._popup;
        }
      });

      map.eachLayer((layer) => {
        if (layer instanceof L.Marker) {
          const mLatLng = layer.getLatLng();
          
          const shouldKeep = ids.some(id => {
            const marker = searchArray.find(m => m.id === id);
            return marker && marker.lat === mLatLng.lat && marker.lng === mLatLng.lng;
          }) || (mLatLng.lat === lat && mLatLng.lng === lng);

          if (!shouldKeep) {
            map.removeLayer(layer);
          }
        }
      });

      Object.values(markersRef.current).forEach(marker => {
        const mLatLng = marker.getLatLng();
        const shouldKeep = ids.some(id => {
          const m = searchArray.find(x => x.id === id);
          return m && m.lat === mLatLng.lat && m.lng === mLatLng.lng;
        }) || (mLatLng.lat === lat && mLatLng.lng === lng);

        if (!shouldKeep) {
          try {
            if (map.hasLayer(marker)) {
              map.removeLayer(marker);
            }
          } catch (error) {
            console.error('Error:', error);
          }
        }
      });

      const associatedMarkers = searchArray.filter(item => ids.includes(item.id));
      associatedMarkers.forEach(marker => {
        const markerLat = parseFloat(marker.lat);
        const markerLng = parseFloat(marker.lng);

        if (!isNaN(markerLat) && !isNaN(markerLng)) {
          let exists = false;
          map.eachLayer((layer) => {
            if (layer instanceof L.Marker) {
              const mLatLng = layer.getLatLng();
              if (mLatLng.lat === markerLat && mLatLng.lng === markerLng) {
                exists = true;
              }
            }
          });

          if (!exists) {
            const iconUrl = createCustomIcon(marker.Marca);
            const leafletMarker = L.marker([markerLat, markerLng], { icon: iconUrl }).addTo(map);

            competenciaMarkersRef.current.push(leafletMarker);

            const datosTabla = obtenerHistoricoMarker(marker.id, baseCompData);
            const tablaPreciosHtml = generarTablaPrecios(datosTabla, 'secondary');

            leafletMarker.on('popupopen', () => {
              setDebugTable({
                titulo: `${marker.Marca} - ${marker.nombre}`,
                registros: datosTabla.length,
                html: tablaPreciosHtml
              });
            });

            let popupContent = `
              <div style="
                font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
                min-width: 320px;
                color: #333;
              ">
                <div style="
                  background: linear-gradient(135deg, #28a745 0%, #1e7e34 100%);
                  color: white;
                  padding: 12px;
                  border-radius: 6px 6px 0 0;
                  margin: -12px -12px 12px -12px;
                  display: flex;
                  align-items: center;
                  gap: 10px;
                ">
                  <img 
                    src="${createBrandLogo(marker.Marca)}"
                    alt="${marker.Marca}"
                    style="width: 35px; height: 35px; border-radius: 50%; object-fit: cover; border: 2px solid white;"
                    onerror="this.style.display='none'"
                  />
                  <div>
                    <h3 style="margin: 0; font-size: 14px; font-weight: 700;">${marker.nombre || marker.Marca}</h3>
                    <p style="margin: 2px 0 0 0; font-size: 11px; opacity: 0.9;">${marker.Marca}</p>
                  </div>
                </div>

                <div style="padding: 0;">
                  <div style="
                    background: #f8f9fa;
                    padding: 10px;
                    border-radius: 4px;
                    margin-bottom: 10px;
                  ">
                    <p style="margin: 5px 0; font-size: 11px;"><strong>PBL:</strong> ${marker.pbl || '-'}</p>
                    <p style="margin: 5px 0; font-size: 11px;"><strong>Regi√≥n:</strong> ${marker.Region || '-'}</p>
                    <p style="margin: 5px 0; font-size: 11px;"><strong>Comuna:</strong> ${marker.Comuna || '-'}</p>
                    ${marker.direccion ? `<p style="margin: 5px 0; font-size: 11px;"><strong>Direcci√≥n:</strong> ${marker.direccion}</p>` : ''}
                  </div>

                  <h4 style="
                    margin: 10px 0 8px 0;
                    font-size: 12px;
                    font-weight: 600;
                    color: #28a745;
                    border-bottom: 2px solid #28a745;
                    padding-bottom: 6px;
                  ">üìä Precios Competencia (${datosTabla.length} registros)</h4>
                  
                  ${tablaPreciosHtml}

                  ${marker.Guerra_Precio === 'Si' ? `
                    <div style="
                      margin-top: 10px;
                      padding: 8px;
                      background: #fff3cd;
                      border: 1px solid #ffc107;
                      border-radius: 4px;
                      color: #856404;
                      font-weight: 600;
                      font-size: 11px;
                      text-align: center;
                    ">
                      ‚ö†Ô∏è Guerra de Precio Activa
                    </div>
                  ` : ''}
                </div>
              </div>
            `;

            if (marker.pbl && (marker.Marca === 'Aramco' || marker.Marca === 'Petrobras')) {
              popupContent += `<button class="associated-button" onclick="window.showAssociatedIds && window.showAssociatedIds(${marker.pbl}, ${marker.lat}, ${marker.lng}, '${marker.id}')" style="
                width: 100%;
                padding: 10px;
                margin-top: 10px;
                background: #28a745;
                color: white;
                border: none;
                border-radius: 4px;
                cursor: pointer;
                font-weight: 600;
                font-size: 12px;
                transition: background 0.2s;
              " onmouseover="this.style.background='#1e7e34'" onmouseout="this.style.background='#28a745'">üõí Abrir Mercado</button>`;
            }

            const popup = L.popup({
              autoClose: false,
              closeOnClick: false,
              keepInView: true,
              autoPan: false,
              maxWidth: 380,
              className: 'custom-popup'
            }).setContent(popupContent);

            leafletMarker.bindPopup(popup);
            leafletMarker.on('popupopen', () => makeDraggable(popup));
            leafletMarker.on('click', () => leafletMarker.openPopup());
          }
        }
      });

      const mainMarkersData = associatedData.filter(m => m.Marcador_Principal === 'Si');
      mainMarkersData.forEach(mainMarker => {
        const markerWithCoords = searchArray.find(m => m.id === mainMarker.id);
        
        if (markerWithCoords) {
          const mainLat = parseFloat(markerWithCoords.lat);
          const mainLng = parseFloat(markerWithCoords.lng);

          if (!isNaN(mainLat) && !isNaN(mainLng)) {
            const currentLat = parseFloat(lat);
            const currentLng = parseFloat(lng);
            
            const polyline = L.polyline(
              [
                [currentLat, currentLng],
                [mainLat, mainLng]
              ],
              {
                color: '#FF6B6B',
                weight: 3,
                opacity: 0.8,
                dashArray: '5, 5'
              }
            ).addTo(map);

            polylineRef.current.push(polyline);
          }
        }
      });

      if (openPopup) {
        setTimeout(() => {
          try {
            openPopup._map = map;
            openPopup.update();
          } catch (e) {
            console.log('Popup actualizado');
          }
        }, 50);
      }

      setShowingAssociated(true);
      setActiveMercadoPbl(pbl);
    }
  }, [baseCompData, markers, markersForMercado, map, makeDraggable, showingAssociated, activeMercadoPbl, updateIconsInViewport, clearCompetenciaMarkers, clearPolylines, generarTablaPrecios, obtenerHistoricoMarker]);

  useEffect(() => {
    window.showAssociatedIds = showAssociatedIds;
  }, [showAssociatedIds]);

  useEffect(() => {
    updateMarkerButtons();
  }, [showingAssociated, activeMercadoPbl, updateMarkerButtons]);

  useEffect(() => {
    if (!map || !markers || markers.length === 0) return;

    clearCompetenciaMarkers();
    clearPolylines();
    setShowingAssociated(false);
    setActiveMercadoPbl(null);
    setOriginalPopupMarker(null);
    
    updateIconsInViewport(markers, true);

  }, [map, markers, selectedRegion, updateIconsInViewport, clearCompetenciaMarkers, clearPolylines]);

  useEffect(() => {
    if (!map) return;

    const handleZoomEnd = () => {
      if (zoomUpdateRef.current) clearTimeout(zoomUpdateRef.current);
      
      zoomUpdateRef.current = setTimeout(() => {
        if (showingAssociated) {
          console.log('üîç Zoom en modo competencia - manteniendo filtro');
        } else if (markers && markers.length > 0) {
          updateIconsInViewport(markers, false);
        }
      }, 400);
    };

    map.on('zoomend', handleZoomEnd);

    return () => {
      map.off('zoomend', handleZoomEnd);
    };
  }, [map, markers, updateIconsInViewport, showingAssociated]);

  // ‚úÖ Tabla debug con estilos mejorados y altura configurable
  return debugTable ? (
    <div style={{
      position: 'fixed',
      bottom: 20,
      right: 20,
      width: '600px',
      maxHeight: `${maxHeight}px`,
      background: 'white',
      borderRadius: '8px',
      boxShadow: '0 6px 20px rgba(0, 0, 0, 0.2)',
      border: '2px solid #007bff',
      zIndex: 1000,
      padding: '0',
      overflowY: 'auto',
      display: 'flex',
      flexDirection: 'column',
      fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif"
    }}>
      <div style={{
        background: 'linear-gradient(135deg, #007bff 0%, #0056b3 100%)',
        color: 'white',
        padding: '12px 15px',
        borderRadius: '6px 6px 0 0',
        fontWeight: '600',
        fontSize: '13px',
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center',
        flexShrink: 0
      }}>
        <span>üìä {debugTable.titulo} ({debugTable.registros} registros)</span>
        <button 
          onClick={() => setDebugTable(null)}
          style={{
            background: 'rgba(255,255,255,0.2)',
            border: 'none',
            color: 'white',
            cursor: 'pointer',
            fontSize: '16px',
            padding: '0 5px',
            borderRadius: '3px'
          }}
        >
          ‚úï
        </button>
      </div>
      <div style={{
        padding: '12px',
        overflowY: 'auto',
        flex: 1
      }}>
        <div dangerouslySetInnerHTML={{ __html: debugTable.html }} />
      </div>
      <div style={{
        padding: '8px 12px',
        borderTop: '1px solid #e0e0e0',
        fontSize: '11px',
        color: '#666',
        flexShrink: 0
      }}>
        <input 
          type="range" 
          min="300" 
          max="800" 
          value={maxHeight}
          onChange={(e) => setMaxHeight(parseInt(e.target.value))}
          style={{ width: '100%', cursor: 'pointer' }}
        />
        <small>Altura: {maxHeight}px</small>
      </div>
    </div>
  ) : null;
};

export default IconMarkersLayer;
