import React, { useEffect, useRef } from 'react';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import { MarkerClusterGroup } from '@react-leaflet/cluster';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';
import 'leaflet.markercluster/dist/MarkerCluster.css';
import 'leaflet.markercluster/dist/MarkerCluster.Default.css';
import { useMapData } from '../hooks/useMapData';

// Fix para iconos de Leaflet
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: require('leaflet/dist/images/marker-icon-2x.png'),
  iconUrl: require('leaflet/dist/images/marker-icon.png'),
  shadowUrl: require('leaflet/dist/images/marker-shadow.png'),
});

const MapComponent = () => {
  const { markers, loading } = useMapData();
  const mapRef = useRef(null);

  if (loading) {
    return (
      <div style={{
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        height: '100%',
        fontSize: '16px',
        color: '#666'
      }}>
         Cargando {markers.length} marcadores...
      </div>
    );
  }

  return (
    <MapContainer
      center={[-33.8688, -70.9123]}
      zoom={12}
      style={{ width: '100%', height: '100%' }}
      ref={mapRef}
      zoomControl={false}
    >
      <TileLayer
        attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a>'
        url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
      />

      <MarkerClusterGroup
        chunkedLoading
        chunkInterval={200}
        chunkDelay={50}
        maxClusterRadius={80}
        disableClusteringAtZoom={16}
        iconCreateFunction={(cluster) => {
          const childCount = cluster.getChildCount();
          let className = 'marker-cluster';
          
          if (childCount < 10) {
            className += ' marker-cluster-small';
          } else if (childCount < 50) {
            className += ' marker-cluster-medium';
          } else {
            className += ' marker-cluster-large';
          }

          return L.divIcon({
            html: `<div><span>${childCount}</span></div>`,
            className: className,
            iconSize: L.point(40, 40),
          });
        }}
      >
        {markers.map((marker) => (
          <Marker
            key={marker.id}
            position={[marker.lat, marker.lng]}
            icon={createMarkerIcon(marker.Marca)}
          >
            <Popup maxWidth={300} minWidth={250}>
              <div className="popup-container">
                <div className="popup-header">
                  <img 
                    src={getBrandLogo(marker.Marca)} 
                    alt={marker.Marca}
                    style={{ width: '30px', height: '30px', borderRadius: '50%' }}
                  />
                  <h3>{marker.nombre}</h3>
                </div>
                <div className="popup-content">
                  <div className="popup-data">
                    <strong>Marca:</strong> {marker.Marca}<br />
                    <strong>Latitud:</strong> {marker.lat.toFixed(4)}<br />
                    <strong>Longitud:</strong> {marker.lng.toFixed(4)}<br />
                  </div>
                  <hr style={{ margin: '10px 0', border: 'none', borderTop: '1px solid #e0e0e0' }} />
                  <div className="popup-data">
                    <strong>Precios:</strong><br />
                    G93: <strong>${marker.precio_g93}</strong><br />
                    G95: <strong>${marker.precio_g95}</strong><br />
                    Diesel: <strong>${marker.precio_diesel}</strong>
                  </div>
                </div>
              </div>
            </Popup>
          </Marker>
        ))}
      </MarkerClusterGroup>
    </MapContainer>
  );
};

// Funci贸n para crear iconos seg煤n marca
const createMarkerIcon = (marca) => {
  const colors = {
    'Petrobras': '#FFDA00',
    'Copec': '#FF6B00',
    'Shell': '#FFD700',
    'Blanco': '#FFFFFF',
    'default': '#00B8A9'
  };

  const color = colors[marca] || colors.default;

  return L.icon({
    iconUrl: `data:image/svg+xml;base64,${btoa(`
      <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="${color}" stroke="white" stroke-width="1">
        <path d="M12 0C7.6 0 4 3.6 4 8c0 6 8 16 8 16s8-10 8-16c0-4.4-3.6-8-8-8z"/>
      </svg>
    `)}`,
    iconSize: [30, 30],
    iconAnchor: [15, 30],
    popupAnchor: [0, -30],
    shadowUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-shadow.png',
    shadowSize: [41, 41],
    shadowAnchor: [13, 41]
  });
};

// Funci贸n para obtener logo de marca
const getBrandLogo = (marca) => {
  const logos = {
    'Petrobras': 'https://via.placeholder.com/50?text=Petrobras',
    'Copec': 'https://via.placeholder.com/50?text=Copec',
    'Shell': 'https://via.placeholder.com/50?text=Shell',
    'Blanco': 'https://via.placeholder.com/50?text=Blanco',
    'default': 'https://via.placeholder.com/50?text=Station'
  };
  return logos[marca] || logos.default;
};

export default MapComponent;
