import React, { useState, useEffect } from 'react';
import '../styles/customPopup.css';

const FilterPanel = ({ markers, onFilterChange, onCompetenciaClick }) => {
  const [regiones, setRegiones] = useState([]);
  const [selectedRegion, setSelectedRegion] = useState(null);
  const [edsList, setEdsList] = useState([]);
  const [selectedEds, setSelectedEds] = useState(null);

  // ‚úÖ Obtener regiones √∫nicas
  useEffect(() => {
    if (markers && markers.length > 0) {
      const uniqueRegions = [...new Set(markers.map(m => m.Region))].filter(Boolean).sort();
      setRegiones(uniqueRegions);
    }
  }, [markers]);

  // ‚úÖ Obtener EDS cuando se selecciona regi√≥n
  useEffect(() => {
    if (selectedRegion && markers && markers.length > 0) {
      const filteredByRegion = markers.filter(m => m.Region === selectedRegion);
      const uniqueEds = [...new Set(filteredByRegion.map(m => m.eds))].filter(Boolean).sort();
      setEdsList(uniqueEds);
      setSelectedEds(null);
    } else {
      setEdsList([]);
    }
  }, [selectedRegion, markers]);

  // ‚úÖ Manejar cambio de regi√≥n
  const handleRegionChange = (e) => {
    const region = e.target.value;
    setSelectedRegion(region || null);
    setSelectedEds(null);
    
    if (region) {
      const filtered = markers.filter(m => m.Region === region);
      onFilterChange(region, null, filtered);
    } else {
      onFilterChange(null, null, markers);
    }
  };

  // ‚úÖ Manejar cambio de EDS
  const handleEdsChange = (e) => {
    const eds = e.target.value;
    setSelectedEds(eds || null);
    
    if (selectedRegion && eds) {
      const filtered = markers.filter(m => m.Region === selectedRegion && m.eds === eds);
      onFilterChange(selectedRegion, eds, filtered);
    } else if (selectedRegion) {
      const filtered = markers.filter(m => m.Region === selectedRegion);
      onFilterChange(selectedRegion, null, filtered);
    }
  };

  return (
    <div className="filter-panel">
      <div className="filter-group">
        <label htmlFor="region-select">üåç Regi√≥n</label>
        <select 
          id="region-select"
          value={selectedRegion || ''} 
          onChange={handleRegionChange}
        >
          <option value="">-- Seleccionar Regi√≥n --</option>
          {regiones.map((region, idx) => (
            <option key={idx} value={region}>
              {region}
            </option>
          ))}
        </select>
      </div>

      {selectedRegion && (
        <div className="filter-group">
          <label htmlFor="eds-select">üè¢ EDS</label>
          <select 
            id="eds-select"
            value={selectedEds || ''} 
            onChange={handleEdsChange}
          >
            <option value="">-- Todas las EDS --</option>
            {edsList.map((eds, idx) => (
              <option key={idx} value={eds}>
                {eds}
              </option>
            ))}
          </select>
        </div>
      )}

      {selectedEds && (
        <div className="filter-info">
          <p>‚úÖ Filtro activo:</p>
          <p><strong>Regi√≥n:</strong> {selectedRegion}</p>
          <p><strong>EDS:</strong> {selectedEds}</p>
        </div>
      )}
    </div>
  );
};

export default FilterPanel;
