import React, { useState, useEffect, useRef } from 'react';
import { MapContainer, TileLayer, Marker, Popup, useMap } from 'react-leaflet';
import IconMarkersLayer from './IconMarkersLayer';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';

const TILE_LAYERS = {
  streets: {
    name: 'Calles',
    icon: 'üó∫Ô∏è',
    url: 'https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png',
    attribution: '¬© OpenStreetMap'
  },
  satellite: {
    name: 'Sat√©lite',
    icon: 'üõ∞Ô∏è',
    url: 'https://server.arcgisonline.com/ArcGIS/rest/services/World_Imagery/MapServer/tile/{z}/{y}/{x}',
    attribution: '¬© Esri'
  },
  hybrid: {
    name: 'H√≠brido',
    icon: 'üåç',
    url: 'https://server.arcgisonline.com/ArcGIS/rest/services/World_Imagery/MapServer/tile/{z}/{y}/{x}',
    attribution: '¬© Esri'
  }
};

function MapController({ regionCenter }) {
  const map = useMap();

  useEffect(() => {
    if (regionCenter && regionCenter.length === 2) {
      map.setView(regionCenter, 10);
    }
  }, [regionCenter, map]);

  useEffect(() => {
    window.leafletMap = map;
  }, [map]);

  useEffect(() => {
    const timer = setTimeout(() => {
      map.invalidateSize();
    }, 100);
    return () => clearTimeout(timer);
  }, [map]);

  return null;
}

function MapComponent({ 
  selectedRegion, 
  regionCenter, 
  markers, 
  markersForMercado, 
  baseCompData,
  onToggleSidebar,
  onToggleRightPanel,
  sidebarVisible,
  rightPanelVisible
}) {
  const [selectedLayer, setSelectedLayer] = useState('streets');
  const [searchQuery, setSearchQuery] = useState('');
  const [searchResults, setSearchResults] = useState([]);
  const [showResults, setShowResults] = useState(false);
  const [searchMarker, setSearchMarker] = useState(null);
  const [panelsMenuOpen, setPanelsMenuOpen] = useState(false);
  const [layersMenuOpen, setLayersMenuOpen] = useState(false);
  const searchTimeoutRef = useRef(null);

  const handleSearch = (query) => {
    setSearchQuery(query);
    
    if (query.length < 3) {
      setSearchResults([]);
      setShowResults(false);
      return;
    }

    if (searchTimeoutRef.current) {
      clearTimeout(searchTimeoutRef.current);
    }

    searchTimeoutRef.current = setTimeout(async () => {
      try {
        const response = await fetch(
          `https://nominatim.openstreetmap.org/search?format=json&q=${encodeURIComponent(query)}&countrycodes=cl&limit=8&addressdetails=1`,
          {
            headers: {
              'User-Agent': 'MapaEstaciones/1.0',
              'Accept-Language': 'es'
            }
          }
        );
        
        const data = await response.json();
        setSearchResults(data);
        setShowResults(data.length > 0);
      } catch (error) {
        console.error('Error en b√∫squeda:', error);
      }
    }, 500);
  };

  const selectLocation = (result) => {
    const lat = parseFloat(result.lat);
    const lon = parseFloat(result.lon);
    
    if (window.leafletMap) {
      window.leafletMap.setView([lat, lon], 17);
      
      // Guardar marcador con direcci√≥n formateada
      const shortAddress = result.display_name.split(',').slice(0, 2).join(',');
      
      setSearchMarker({
        position: [lat, lon],
        address: shortAddress,
        fullAddress: result.display_name
      });
    }
    
    setShowResults(false);
    setSearchQuery(result.display_name.split(',')[0]); // Solo primera parte
  };

  return (
    <div className="map-wrapper">
      {/* BUSCADOR DE DIRECCIONES */}
      <div className="map-control-left">
        <div className="search-bar-inline">
          <input
            type="text"
            className="search-input-inline"
            placeholder="üîç Buscar direcci√≥n..."
            value={searchQuery}
            onChange={(e) => handleSearch(e.target.value)}
            onClick={(e) => e.stopPropagation()}
          />
          
          {searchQuery && (
            <button
              className="search-clear-btn"
              onClick={() => {
                setSearchQuery('');
                setSearchResults([]);
                setShowResults(false);
                setSearchMarker(null);
              }}
              title="Limpiar b√∫squeda"
            >
              ‚úï
            </button>
          )}
          
          {showResults && searchResults.length > 0 && (
            <div className="search-results-dropdown-inline">
              {searchResults.map((result, index) => (
                <div
                  key={index}
                  className="search-result-item-styled"
                  onMouseDown={(e) => e.preventDefault()}
                  onClick={(e) => {
                    e.stopPropagation();
                    selectLocation(result);
                  }}
                >
                  <span className="result-icon">üìç</span>
                  <div className="result-content">
                    <div className="result-name">
                      {result.display_name.split(',')[0]}
                    </div>
                    <div className="result-details">
                      {result.display_name.split(',').slice(1, 3).join(',')}
                    </div>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      </div>

      {/* MEN√ö CAPAS (DERECHA) */}
      <div className="map-control-right">
        <button 
          className="control-toggle-btn"
          onClick={() => setLayersMenuOpen(!layersMenuOpen)}
          title="Capas del Mapa"
        >
          üó∫Ô∏è
        </button>

        {layersMenuOpen && (
          <div className="control-dropdown">
            <div className="control-section">
              <div className="control-section-title">Capas del Mapa</div>
              <div className="layer-buttons">
                {Object.entries(TILE_LAYERS).map(([key, layer]) => (
                  <button
                    key={key}
                    className={`layer-btn-compact ${selectedLayer === key ? 'active' : ''}`}
                    onClick={() => setSelectedLayer(key)}
                  >
                    {layer.icon} {layer.name}
                  </button>
                ))}
              </div>
            </div>
          </div>
        )}
      </div>

      <MapContainer
        center={regionCenter || [-33.4489, -70.6693]}
        zoom={10}
        className="map"
        zoomControl={true}
      >
        <TileLayer
          url={TILE_LAYERS[selectedLayer].url}
          attribution={TILE_LAYERS[selectedLayer].attribution}
          maxZoom={19}
        />
        
        {selectedLayer === 'hybrid' && (
          <TileLayer
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
            attribution=""
            opacity={0.4}
          />
        )}

        <MapController regionCenter={regionCenter} />
        
        <IconMarkersLayer
          markers={markers}
          markersForMercado={markersForMercado}
          selectedRegion={selectedRegion}
          baseCompData={baseCompData}
        />

        {/* MARCADOR DE B√öSQUEDA */}
        {searchMarker && (
          <Marker 
            position={searchMarker.position}
            icon={L.icon({
              iconUrl: 'https://raw.githubusercontent.com/pointhi/leaflet-color-markers/master/img/marker-icon-2x-red.png',
              shadowUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-shadow.png',
              iconSize: [25, 41],
              iconAnchor: [12, 41],
              popupAnchor: [1, -34],
              shadowSize: [41, 41]
            })}
          >
            <Popup>
              <div style={{ padding: '8px', minWidth: '200px' }}>
                <strong style={{ 
                  color: '#02d6a8', 
                  fontSize: '13px', 
                  display: 'block', 
                  marginBottom: '8px',
                  fontWeight: 700
                }}>
                  üìç Ubicaci√≥n buscada
                </strong>
                <p style={{ 
                  margin: 0, 
                  fontSize: '12px', 
                  color: '#495057',
                  lineHeight: 1.5
                }}>
                  {searchMarker.address}
                </p>
              </div>
            </Popup>
          </Marker>
        )}
      </MapContainer>
    </div>
  );
}

export default MapComponent;
