import React from 'react';
import { MapContainer, TileLayer } from 'react-leaflet';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';
import IconMarkersLayer from './IconMarkersLayer';

delete L.Icon.Default.prototype._getIconUrl;

L.Icon.Default.mergeOptions({
  iconRetinaUrl: require('leaflet/dist/images/marker-icon-2x.png'),
  iconUrl: require('leaflet/dist/images/marker-icon.png'),
  shadowUrl: require('leaflet/dist/images/marker-shadow.png'),
});

const MapComponent = ({ selectedRegion, regionCenter, markers = [], markersForMercado = null, baseCompData = [] }) => {
  const mapCenter = regionCenter || [-33.4474107, -70.5518946];

  return (
    <MapContainer center={mapCenter} zoom={11} style={{ height: '100vh', width: '100%' }}>
      <TileLayer
        url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
        attribution='&copy; OpenStreetMap contributors'
      />
      {markers && markers.length > 0 && (
        <IconMarkersLayer 
          markers={markers}
          markersForMercado={markersForMercado}
          selectedRegion={selectedRegion}
          baseCompData={baseCompData}
        />
      )}
    </MapContainer>
  );
};

export default MapComponent;
