    const idsSet = new Set(ids);

    if (map) {
      let openPopup = null;
      map.eachLayer((layer) => {
        if (layer instanceof L.Marker && layer._popup && layer._popup.isOpen()) {
          openPopup = layer._popup;
        }
      });

      // Cambiar opacity
      map.eachLayer((layer) => {
        if (layer instanceof L.Marker) {
          const mLatLng = layer.getLatLng();
          
          let shouldKeep = false;
          
          for (let id of idsSet) {
            const marker = markers.find(m => m.id === id);
            if (marker && 
                Math.abs(marker.lat - mLatLng.lat) < 0.00001 && 
                Math.abs(marker.lng - mLatLng.lng) < 0.00001) {
              shouldKeep = true;
              break;
            }
          }
          
          if (!shouldKeep) {
            shouldKeep = (Math.abs(mLatLng.lat - lat) < 0.00001 && 
                         Math.abs(mLatLng.lng - lng) < 0.00001);
          }

          layer.setOpacity(shouldKeep ? 1 : 0);
        }
      });

      // Dibujar l√≠neas desde principal a competencia
      associatedData.forEach((compMarker) => {
        const lineId = `${lat},${lng}-${compMarker.id}`;
        
        const polyline = L.polyline(
          [[lat, lng], [compMarker.lat, compMarker.lng]],
          {
            color: '#FF6B35',
            weight: 2,
            opacity: 0.7,
            dashArray: '5, 5'
          }
        ).addTo(map);

        linesRef.current[lineId] = polyline;
      });

      if (openPopup) {
        setTimeout(() => {
          try {
            openPopup._map = map;
            openPopup.update();
          } catch (e) {}
        }, 50);
      }

      setShowingAssociated(true);
    }
  }, [baseCompData, markers, map, showingAssociated]);

  useEffect(() => {
    window.showAssociatedIds = showAssociatedIds;
  }, [showAssociatedIds]);

  useEffect(() => {
    if (!map || !markers || markers.length === 0) return;

    console.log(`‚úÖ Actualizando ${markers.length} marcadores`);
    updateIconsInViewport(markers, true);

  }, [map, markers, selectedRegion, updateIconsInViewport]);

  useEffect(() => {
    if (!map) return;

    const handleZoomEnd = () => {
      if (zoomUpdateRef.current) clearTimeout(zoomUpdateRef.current);
      
      zoomUpdateRef.current = setTimeout(() => {
        if (showingAssociated) {
          console.log('üîç Zoom en modo competencia - manteniendo filtro');
        } else if (markers && markers.length > 0) {
          updateIconsInViewport(markers, false);
        }
      }, 400);
    };

    map.on('zoomend', handleZoomEnd);

    return () => {
      map.off('zoomend', handleZoomEnd);
    };
  }, [map, markers, updateIconsInViewport, showingAssociated]);

  return null;
};

export default IconMarkersLayer;
