// COMPONENTE: IconMarkersLayer
// Gestiona marcadores, l√≠neas de competencia y vista del mercado
// ============================================

const IconMarkersLayer = ({ markers, selectedRegion, baseCompData = [] }) => {
  const map = useMap();
  
  // ============================================
  // REFERENCIAS Y ESTADO
