    });
  }, [map, makeDraggable, selectedRegion]);

  // ============================================
  // FUNCI√ìN: Mostrar asociados / Mercado
  // ============================================
  
  const showAssociatedIds = useCallback((pbl, lat, lng, originalMarkerId) => {
    console.log(`üîç PBL seleccionado: ${pbl}`);
    
