      String(item.pbl).trim() === String(pbl).trim()
    );

    // Filtrar solo las con Marcador_Principal='si' para dibujar l√≠neas
    const marcadoresConLinea = associatedData.filter(item =>
      String(item.Marcador_Principal).toLowerCase().trim() === 'si'
    );
    
    console.log(`‚úÖ Competencia encontrada:`, associatedData.length);

    if (associatedData.length === 0) {
      console.warn(`‚ö†Ô∏è Sin competencia para PBL: ${pbl}`);
      return;
    }

    const ids = associatedData.map(item => item.id);
    const idsSet = new Set(ids);

    if (map) {
      let openPopup = null;
      map.eachLayer((layer) => {
        if (layer instanceof L.Marker && layer._popup && layer._popup.isOpen()) {
          openPopup = layer._popup;
        }
      });

      // ============================================
      // FILTRAR MARCADORES POR OPACITY
      // ============================================
      
      map.eachLayer((layer) => {
        if (layer instanceof L.Marker) {
          const mLatLng = layer.getLatLng();
          
          let shouldKeep = false;
          
          // Verificar si est√° en competencia
          for (let id of idsSet) {
            const marker = markers.find(m => m.id === id);
            if (marker && 
                Math.abs(marker.lat - mLatLng.lat) < 0.00001 && 
                Math.abs(marker.lng - mLatLng.lng) < 0.00001) {
              shouldKeep = true;
              break;
            }
          }
          
          // Si no es competencia, verificar si es original
          if (!shouldKeep) {
            shouldKeep = (Math.abs(mLatLng.lat - lat) < 0.00001 && 
                         Math.abs(mLatLng.lng - lng) < 0.00001);
          }

          layer.setOpacity(shouldKeep ? 1 : 0);
        }
      });

      // ============================================
      // DIBUJAR L√çNEAS DE COMPETENCIA
      // ============================================
      
      marcadoresConLinea.forEach((compMarker) => {
        const markerData = markers.find(m => m.id === compMarker.id);
        
        if (markerData) {
          const compLat = parseFloat(markerData.lat);
          const compLng = parseFloat(markerData.lng);

          if (!isNaN(compLat) && !isNaN(compLng) && !isNaN(lat) && !isNaN(lng)) {
            const lineId = `${lat},${lng}-${compMarker.id}`;
            
            // Limpiar l√≠nea anterior si existe
            if (linesRef.current[lineId]) {
              try {
                map.removeLayer(linesRef.current[lineId]);
              } catch (e) {}
            }
            
            // Crear polyline
                      // Crear polyline desde el CENTRO del marcador
            const polyline = L.polyline(
              [[lat, lng], [compLat, compLng]],
              {
                color: '#0e0e0eff',
                weight: 3,
                opacity: 0.9,
                dashArray: '8, 4',
                lineCap: 'round',
                lineJoin: 'round',
                className: 'competition-polyline',
                offset: 0
              }
            ).bringToFront().addTo(map);


            linesRef.current[lineId] = polyline;
            console.log(`‚úÖ L√≠nea dibujada:`, lineId);
          }
        }
      });

      // Mantener popup abierto
      if (openPopup) {
        setTimeout(() => {
          try {
            openPopup._map = map;
            openPopup.update();
          } catch (e) {}
        }, 50);
      }

      setShowingAssociated(true);
    }
  }, [baseCompData, markers, map, showingAssociated]);

  // ============================================
  // EXPONER FUNCI√ìN A GLOBAL
  // ============================================
  
  useEffect(() => {
    window.showAssociatedIds = showAssociatedIds;
  }, [showAssociatedIds]);

  // ============================================
  // CARGAR MARCADORES CUANDO CAMBIA LA REGI√ìN
  // ============================================
  
  useEffect(() => {
    if (!map || !markers || markers.length === 0) return;

    console.log(`‚úÖ Actualizando ${markers.length} marcadores`);
    updateIconsInViewport(markers, true);

  }, [map, markers, selectedRegion, updateIconsInViewport]);

  // ============================================
  // ACTUALIZAR AL CAMBIAR ZOOM
  // ============================================
  
  useEffect(() => {
    if (!map) return;

    const handleZoomEnd = () => {
      if (zoomUpdateRef.current) clearTimeout(zoomUpdateRef.current);
      
      zoomUpdateRef.current = setTimeout(() => {
        if (showingAssociated) {
          console.log('üîç Zoom en modo competencia - manteniendo filtro');
        } else if (markers && markers.length > 0) {
          updateIconsInViewport(markers, false);
        }
      }, 400);
    };

    map.on('zoomend', handleZoomEnd);

    return () => {
      map.off('zoomend', handleZoomEnd);
    };
  }, [map, markers, updateIconsInViewport, showingAssociated]);

  return null;
};

export default IconMarkersLayer;
