import React, { useState } from 'react';
import { MapContainer, TileLayer, ZoomControl } from 'react-leaflet';
import L from 'leaflet';
import IconMarkersLayer from './IconMarkersLayer';
import '../styles/layout.css';

delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: require('leaflet/dist/images/marker-icon-2x.png'),
  iconUrl: require('leaflet/dist/images/marker-icon.png'),
  shadowUrl: require('leaflet/dist/images/marker-shadow.png'),
});

const MapComponent = ({ markers, selectedRegion, regions }) => {
  // Estados de filtros
  const [selectedComuna, setSelectedComuna] = useState('');
  const [selectedMarca, setSelectedMarca] = useState('');
  const [selectedJefeZona, setSelectedJefeZona] = useState('');
  const [selectedEds, setSelectedEds] = useState('');
  const [edsSearchTerm, setEdsSearchTerm] = useState('');

  // Obtener comunas √∫nicas de la regi√≥n seleccionada
  const comunas = markers && markers.length > 0
    ? [...new Set(markers
        .filter(m => m.Region === selectedRegion)
        .map(m => m.Comuna))]
        .sort()
    : [];

  // Obtener marcas √∫nicas
  const marcas = markers && markers.length > 0
    ? [...new Set(markers.map(m => m.Marca))].sort()
    : [];

  // Obtener jefes de zona (nombre) de la regi√≥n seleccionada
  const jefesZona = markers && markers.length > 0
    ? [...new Set(markers
        .filter(m => m.Region === selectedRegion && m.nombre)
        .map(m => m.nombre))]
        .sort()
    : [];

  // Obtener todas las EDS (filtradas por regi√≥n y otros filtros activos)
  const allEds = markers && markers.length > 0
    ? [...new Set(markers
        .filter(m => {
          const regionMatch = !selectedRegion || m.Region === selectedRegion;
          const comunaMatch = !selectedComuna || m.Comuna === selectedComuna;
          const marcaMatch = !selectedMarca || m.Marca === selectedMarca;
          const jefeMatch = !selectedJefeZona || m.nombre === selectedJefeZona;
          return regionMatch && comunaMatch && marcaMatch && jefeMatch;
        })
        .map(m => m.eds)
        .filter(eds => eds))]
        .sort()
    : [];

  // EDS filtradas por t√©rmino de b√∫squeda
  const filteredEds = allEds.filter(eds =>
    eds.toLowerCase().includes(edsSearchTerm.toLowerCase())
  );

  // Filtrar marcadores seg√∫n los filtros activos
  const filteredMarkers = markers && markers.length > 0
    ? markers.filter(m => {
        const regionMatch = !selectedRegion || m.Region === selectedRegion;
        const comunaMatch = !selectedComuna || m.Comuna === selectedComuna;
        const marcaMatch = !selectedMarca || m.Marca === selectedMarca;
        const jefeMatch = !selectedJefeZona || m.nombre === selectedJefeZona;
        const edsMatch = !selectedEds || m.eds === selectedEds;
        return regionMatch && comunaMatch && marcaMatch && jefeMatch && edsMatch;
      })
    : [];

  // Calcular estad√≠sticas
  const estadisticas = {
    estaciones: filteredMarkers.length,
    marcas: [...new Set(filteredMarkers.map(m => m.Marca))].length,
    comunas: [...new Set(filteredMarkers.map(m => m.Comuna))].length,
  };

  const markersByBrand = {};
  filteredMarkers.forEach(m => {
    markersByBrand[m.Marca] = (markersByBrand[m.Marca] || 0) + 1;
  });

  return (
    <div className="map-wrapper">
      {/* Panel izquierdo - Filtros */}
      <div className="info-panel">
        <div className="panel-header">
          <h2>üìç Gasolineras</h2>
          <p>{selectedRegion}</p>
        </div>

        <div className="panel-content">
          {/* Estad√≠sticas */}
          <div className="info-section">
            <h3>üìä Estad√≠sticas</h3>
            <div className="stats-grid">
              <div className="stat-card">
                <span className="stat-label">Estaciones</span>
                <span className="stat-value">{estadisticas.estaciones}</span>
              </div>
              <div className="stat-card">
                <span className="stat-label">Marcas</span>
                <span className="stat-value">{estadisticas.marcas}</span>
              </div>
              <div className="stat-card">
                <span className="stat-label">Comunas</span>
                <span className="stat-value">{estadisticas.comunas}</span>
              </div>
            </div>
          </div>

          {/* Por Marca */}
          <div className="info-section">
            <h3>üè¢ Por Marca</h3>
            <div className="brands-list">
              {Object.entries(markersByBrand)
                .sort((a, b) => b[1] - a[1])
                .map(([brand, count]) => (
                  <div key={brand} className="brand-item">
                    <span className="brand-name">{brand}</span>
                    <span className="brand-count">{count}</span>
                  </div>
                ))}
            </div>
          </div>

          {/* Filtros */}
          <div className="info-section">
            <h3>üîç Filtros</h3>

            {/* Comuna */}
            <div className="filter-group">
              <label htmlFor="comuna-filter">Comuna</label>
              <select
                id="comuna-filter"
                value={selectedComuna}
                onChange={(e) => {
                  setSelectedComuna(e.target.value);
                  setSelectedEds('');
                  setEdsSearchTerm('');
                }}
                className="filter-select"
              >
                <option value="">Todas</option>
                {comunas.map(comuna => (
                  <option key={comuna} value={comuna}>{comuna}</option>
                ))}
              </select>
            </div>

            {/* Marca */}
            <div className="filter-group">
              <label htmlFor="marca-filter">Marca</label>
              <select
                id="marca-filter"
                value={selectedMarca}
                onChange={(e) => setSelectedMarca(e.target.value)}
                className="filter-select"
              >
                <option value="">Todas</option>
                {marcas.map(marca => (
                  <option key={marca} value={marca}>{marca}</option>
                ))}
              </select>
            </div>

            {/* Jefe de Zona */}
            <div className="filter-group">
              <label htmlFor="jefe-filter">Jefe de Zona</label>
              <select
                id="jefe-filter"
                value={selectedJefeZona}
                onChange={(e) => {
                  setSelectedJefeZona(e.target.value);
                  setSelectedEds('');
                  setEdsSearchTerm('');
                }}
                className="filter-select"
              >
                <option value="">Todos</option>
                {jefesZona.map(jefe => (
                  <option key={jefe} value={jefe}>{jefe}</option>
                ))}
              </select>
            </div>

            {/* Buscador de EDS */}
            <div className="filter-group">
              <label htmlFor="eds-search">Buscar EDS</label>
              <input
                id="eds-search"
                type="text"
                placeholder="Escribe para buscar..."
                value={edsSearchTerm}
                onChange={(e) => {
                  setEdsSearchTerm(e.target.value);
                  setSelectedEds('');
                }}
                className="filter-input"
              />

              {/* Dropdown de EDS filtradas */}
              {edsSearchTerm && filteredEds.length > 0 && (
                <div className="eds-dropdown">
                  {filteredEds.map(eds => (
                    <div
                      key={eds}
                      className="eds-option"
                      onClick={() => {
                        setSelectedEds(eds);
                        setEdsSearchTerm('');
                      }}
                    >
                      {eds}
                    </div>
                  ))}
                </div>
              )}

              {/* EDS Seleccionada */}
              {selectedEds && (
                <div className="selected-eds">
                  <span>‚úì {selectedEds}</span>
                  <button
                    className="clear-eds"
                    onClick={() => {
                      setSelectedEds('');
                      setEdsSearchTerm('');
                    }}
                  >
                    ‚úï
                  </button>
                </div>
              )}
            </div>

            {/* Bot√≥n Limpiar Filtros */}
            <button
              className="btn-clear-filters"
              onClick={() => {
                setSelectedComuna('');
                setSelectedMarca('');
                setSelectedJefeZona('');
                setSelectedEds('');
                setEdsSearchTerm('');
              }}
            >
              üîÑ Limpiar Filtros
            </button>
          </div>
        </div>
      </div>

      {/* Mapa */}
      <div className="map-container">
        <MapContainer
          center={[-33.8688, -70.9123]}
          zoom={10}
          style={{ height: '100%', width: '100%' }}
          zoomControl={false}
        >
          <TileLayer
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
            attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a>'
          />
          <ZoomControl position="bottomright" />

          {filteredMarkers && filteredMarkers.length > 0 && (
            <IconMarkersLayer
              markers={filteredMarkers}
              selectedRegion={selectedRegion}
            />
          )}
        </MapContainer>
      </div>
    </div>
  );
};

export default MapComponent;
