import { useState, useEffect } from 'react';
import {   getCoordenadasCorregidas  // âœ… IMPORTAR
} from '../services/apiService';

const useMapData = () => {
  const [estaciones, setEstaciones] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const cargarDatos = async () => {
      try {
        setLoading(true);
        
        // âœ… 1. Cargar coordenadas corregidas PRIMERO
        const coordCorregidas = await getCoordenadasCorregidas();
        console.log('ðŸ“ Coordenadas corregidas cargadas:', coordCorregidas.length);
        
        // 2. Cargar estaciones
        const data = await obtenerEstaciones();
        const preciosHistoricos = await obtenerPreciosHistoricos();

        const estacionesConPreciosHistoricos = data.map(estacion => {
          const precios = preciosHistoricos
            .filter(p => p.pbl === estacion.pbl)
            .sort((a, b) => new Date(b.fecha_aplicacion) - new Date(a.fecha_aplicacion));
          
          // âœ… 3. Buscar coordenada corregida
          const coordCorregida = coordCorregidas.find(
            c => c.pbl === estacion.pbl || c.eds === estacion.eds
          );
          
          // âœ… 4. Usar corregida si existe, sino usar original
          const lat = parseFloat(coordCorregida?.lat_corregida || estacion.lat);
          const lng = parseFloat(coordCorregida?.lon_corregida || estacion.lon);

          return {
            id: estacion.id || estacion.pbl,
            pbl: estacion.pbl,
            eds: estacion.eds,
            lat: isNaN(lat) ? -33.8688 : lat,
            lng: isNaN(lng) ? -70.9123 : lng,
            lat_original: parseFloat(estacion.lat),  // âœ… GUARDAR ORIGINAL
            lng_original: parseFloat(estacion.lon),  // âœ… GUARDAR ORIGINAL
            Marca: estacion.Marca || 'Unknown',
            nombre: estacion.nombre || estacion.eds || `EstaciÃ³n ${estacion.id}`,
            precio_g93: estacion.G93 || 0,
            precio_g95: estacion.G95 || 0,
            precio_g97: estacion.G97 || 0,
            precio_diesel: estacion.Diesel || 0,
            precio_kero: estacion.Kerosene || 0,
            direccion: estacion.direccion || '',
            logo: estacion.logo || '',
            Region: estacion.Region || '',
            Comuna: estacion.Comuna || '',
            Guerra_Precio: estacion.Guerra_Precio || 'No',
            Actualizacion: estacion.Actualizacion || '',
            fecha_aplicacion: estacion.fecha_aplicacion || '',
            preciosHistoricos: precios
          };
        });

        setEstaciones(estacionesConPreciosHistoricos);
      } catch (err) {
        console.error('Error al cargar los datos:', err);
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    cargarDatos();
  }, []);

  return { estaciones, loading, error };
};

export default useMapData;
