import React, { useEffect, useRef, useState, useCallback } from 'react';
import { useMap } from 'react-leaflet';
import L from 'leaflet';
import { createCustomIcon, createBrandLogo } from '../utils/iconHelpers';

const IconMarkersLayer = ({ markers, selectedRegion }) => {
  const map = useMap();
  const markersRef = useRef({});
  const [showingAssociated, setShowingAssociated] = useState(false);
  const [baseCompData, setBaseCompData] = useState([]);

  // Cargar base_comp
  useEffect(() => {
    const loadBaseComp = async () => {
      try {
        const response = await fetch(`${process.env.REACT_APP_API_URL}/base_comp`);
        const data = await response.json();
        console.log('✅ Base comp cargada:', data);
        setBaseCompData(data.base_comp || data || []);
      } catch (error) {
        console.error('❌ Error:', error);
        setBaseCompData([]);
      }
    };
    loadBaseComp();
  }, []);

  const makeDraggable = useCallback((popup) => {
    if (!map || !popup || !popup._container) return;
    try {
      const pos = map.latLngToLayerPoint(popup.getLatLng());
      L.DomUtil.setPosition(popup._wrapper.parentNode, pos);
      const draggable = new L.Draggable(popup._container, popup._wrapper);
      draggable.enable();
    } catch (error) {
      console.error('Error draggable:', error);
    }
  }, [map]);

  // Función showAssociatedIds
  const showAssociatedIds = useCallback((pbl, lat, lng) => {
    if (showingAssociated) {
      if (map) {
        map.eachLayer((layer) => {
          if (layer instanceof L.Marker) {
            map.removeLayer(layer);
          }
        });
        addMarkers(markers, map);
      }
      setShowingAssociated(false);
    } else {
      const associatedData = baseCompData.filter(item => 
        String(item.pbl).trim() === String(pbl).trim()
      );
      const ids = associatedData.map(item => item.id);

      if (map) {
        map.eachLayer((layer) => {
          if (layer instanceof L.Marker) {
            const markerLatLng = layer.getLatLng();
            if (markerLatLng.lat !== lat || markerLatLng.lng !== lng) {
              map.removeLayer(layer);
            }
          }
        });

        const associatedMarkers = markers.filter(item => ids.includes(item.id));
        addMarkers(associatedMarkers, map);
      }
      setShowingAssociated(true);
    }
  }, [baseCompData, markers, map, showingAssociated]);

  const addMarkers = useCallback((data, mapInstance) => {
    data.forEach(markerData => {
      const lat = parseFloat(markerData.lat);
      const lng = parseFloat(markerData.lng);

      if (!isNaN(lat) && !isNaN(lng)) {
        const iconUrl = createCustomIcon(markerData.Marca);
        const marker = L.marker([lat, lng], { icon: iconUrl }).addTo(mapInstance);

        // Construir contenido del popup - SEPARADO EN PARTES
        let popupContent = `
          <div class="popup-container">
            <div class="popup-header">
              <img 
                src="${createBrandLogo(markerData.Marca)}"
                alt="${markerData.Marca}"
                style="width: 30px; height: 30px; border-radius: 50%; object-fit: cover; margin-right: 8px;"
                onerror="this.style.display='none'"
              />
              <h3 style="margin: 0;">${markerData.nombre || markerData.Marca}</h3>
            </div>
            <div class="popup-content">
              <div class="popup-data">
                <strong>Marca:</strong> ${markerData.Marca}<br />
                <strong>PBL:</strong> ${markerData.pbl || '-'}<br />
                <strong>Region:</strong> ${markerData.Region}<br />
                <strong>Comuna:</strong> ${markerData.Comuna}<br />
                ${markerData.direccion ? `<strong>Dirección:</strong> ${markerData.direccion}<br />` : ''}
                ${markerData.eds ? `<strong>EDS:</strong> ${markerData.eds}<br />` : ''}
              </div>
              <hr style="margin: 10px 0; border: none; border-top: 1px solid #e0e0e0" />
              <div class="popup-data">
                <strong>Precios:</strong><br />
                G93: ${markerData.precio_g93 ? `$${markerData.precio_g93}` : 'N/A'}<br />
                G95: ${markerData.precio_g95 ? `$${markerData.precio_g95}` : 'N/A'}<br />
                Diesel: ${markerData.precio_diesel ? `$${markerData.precio_diesel}` : 'N/A'}<br />
              </div>
              ${markerData.Guerra_Precio === 'Si' ? `
                <hr style="margin: 10px 0; border: none; border-top: 1px solid #e0e0e0" />
                <div class="popup-data" style="color: #d32f2f; font-weight: bold;">
                  ⚠️ Guerra de Precio Activa
                </div>
              ` : ''}
            </div>
          </div>
        `;

        // Agregar botón SOLO si tiene PBL y es Aramco o Petrobras
        if (markerData.pbl && (markerData.Marca === 'Aramco' || markerData.Marca === 'Petrobras')) {
          popupContent += `<button class="associated-button" onclick="window.showAssociatedIds && window.showAssociatedIds(${markerData.pbl}, ${lat}, ${lng})">Mercado</button>`;
        }

        const popup = L.popup({
          autoClose: false,
          closeOnClick: false,
          keepInView: true,
          autoPan: false
        }).setContent(popupContent);

        marker.bindPopup(popup);

        marker.on('popupopen', () => {
          makeDraggable(popup);
        });

        marker.on('click', () => {
          marker.openPopup();
        });
      }
    });
  }, [makeDraggable]);

  // Exponer función globalmente
  useEffect(() => {
    window.showAssociatedIds = showAssociatedIds;
  }, [showAssociatedIds]);

  useEffect(() => {
    if (!map || !markers || markers.length === 0) return;

    console.log(`✅ Actualizando ${markers.length} marcadores`);
    
    // Limpiar
    map.eachLayer((layer) => {
      if (layer instanceof L.Marker) {
        map.removeLayer(layer);
      }
    });

    // Agregar nuevos
    addMarkers(markers, map);

  }, [markers, selectedRegion, map, addMarkers]);

  return null;
};

export default IconMarkersLayer;
