import React, { useMemo, useState } from 'react';

const COLORS = {
  G93: '#34c759',
  G95: '#007bff',
  G97: '#a259c1',
  Diesel: '#fd7e14',
  Kero: '#6c757d'
};

const FUEL_OPTIONS = [
  { key: 'G93', field: 'g93', label: 'G93' },
  { key: 'G95', field: 'g95', label: 'G95' },
  { key: 'G97', field: 'g97', label: 'G97' },
  { key: 'Diesel', field: 'diesel', label: 'Diesel' },
  { key: 'Kero', field: 'kero', label: 'Kero' }
];

const toNum = (v) => (v === '-' || v == null ? null : Number(v));
const fmtLabel = (dateStr) => {
  const onlyDate = (dateStr || '').split(' ')[0] || '';
  const parts = onlyDate.split('-'); // YYYY-MM-DD
  if (parts.length === 3) {
    const [, m, d] = parts;
    return `${d}-${m}`;
  }
  return onlyDate;
};

export default function TrendChart({ dataRows = [], maxPoints = 7, height = 170 }) {
  const [fuelKey, setFuelKey] = useState('G93'); // default 93

  const { labels, data, minY, maxY } = useMemo(() => {
    const sorted = [...dataRows]
      .sort((a, b) => {
        const da = new Date((a.fecha || '').split(' ')[0] || a.fecha);
        const db = new Date((b.fecha || '').split(' ')[0] || b.fecha);
        return da - db;
      })
      .slice(-maxPoints);

    const labels = sorted.map(r => fmtLabel(r.fecha));
    // La serie activa la calcula el render según fuelKey; aquí preparamos todas las columnas
    const valuesByFuel = {
      G93: sorted.map(r => toNum(r.g93)),
      G95: sorted.map(r => toNum(r.g95)),
      G97: sorted.map(r => toNum(r.g97)),
      Diesel: sorted.map(r => toNum(r.diesel)),
      Kero: sorted.map(r => toNum(r.kero))
    };

    // Para el rango Y consideramos solo la serie activa en render; pero proveemos min/max globales razonables
    const allVals = Object.values(valuesByFuel).flat().filter(v => v != null);
    const minY = allVals.length ? Math.min(...allVals) : 0;
    const maxY = allVals.length ? Math.max(...allVals) : 1;

    return { labels, data: valuesByFuel, minY, maxY };
  }, [dataRows, maxPoints]);

  const width = 320;
  const margin = { top: 6, right: 8, bottom: 26, left: 46 };
  const innerW = width - margin.left - margin.right;
  const innerH = height - margin.top - margin.bottom;

  // Serie activa según filtro
  const active = FUEL_OPTIONS.find(f => f.key === fuelKey) || FUEL_OPTIONS[0];
  const series = (data[active.key] || []).slice();
  const presentVals = series.filter(v => v != null);
  const aMin = presentVals.length ? Math.min(...presentVals) : 0;
  const aMax = presentVals.length ? Math.max(...presentVals) : 1;
  const yMin = aMin;
  const yMax = aMax === aMin ? aMin + 1 : aMax;

  const xPos = (i) => {
    if (labels.length <= 1) return margin.left + innerW / 2;
    return margin.left + (i * innerW) / (labels.length - 1);
  };
  const colWidth = labels.length > 1 ? Math.max(6, innerW / (labels.length * 1.8)) : 12;
  const yPos = (v) => {
    if (v == null) return null;
    const span = Math.max(1, (yMax - yMin));
    const y = (v - yMin) / span;
    return margin.top + innerH - y * innerH;
  };

  const gridY = 3;
  const yTicks = Array.from({ length: gridY + 1 }, (_, k) => yMin + (k * (yMax - yMin)) / Math.max(1, gridY));

  return (
    <div style={{ minWidth: 320, height }}>
      {/* Header compacto con filtro */}
      <div style={{ display: 'flex', alignItems: 'center', gap: 8, marginBottom: 4, paddingLeft: 6 }}>
        <div style={{ width: 8, height: 8, background: COLORS[active.key], borderRadius: 2 }} />
        <span style={{ fontSize: 11, color: '#333' }}>Combustible:</span>
        <select
          value={fuelKey}
          onChange={(e) => setFuelKey(e.target.value)}
          style={{
            fontSize: 11, padding: '2px 6px', borderRadius: 4, border: '1px solid #d0d0d0',
            outline: 'none', background: '#fff'
          }}
        >
          {FUEL_OPTIONS.map(f => (
            <option key={f.key} value={f.key}>{f.label}</option>
          ))}
        </select>
      </div>

      <svg width={width} height={height}>
        {/* Grid Y */}
        {yTicks.map((t, idx) => {
          const y = yPos(t);
          return (
            <g key={idx}>
              <line x1={margin.left} x2={margin.left + innerW} y1={y} y2={y} stroke="rgba(0,0,0,0.08)" />
              <text x={margin.left - 6} y={y} textAnchor="end" dominantBaseline="middle" fontSize="10">
                {Math.round(t).toLocaleString('es-CL')}
              </text>
            </g>
          );
        })}

        {/* Eje X etiquetas fecha DD-MM */}
        {labels.map((lab, i) => (
          <text key={i} x={xPos(i)} y={height - 6} textAnchor="middle" fontSize="10">{lab}</text>
        ))}

        {/* Barras compactas una serie */}
        {series.map((v, i) => {
          const y = yPos(v);
          if (y == null) return null;
          const xCenter = xPos(i);
          const x = xCenter - colWidth / 2;
          const barH = margin.top + innerH - y;
          return (
            <rect
              key={i}
              x={x}
              y={y}
              width={colWidth}
              height={barH}
              fill={COLORS[active.key]}
              rx="2"
            />
          );
        })}
      </svg>
    </div>
  );
}
