import React, { useEffect, useRef, useState, useCallback } from 'react';
import { useMap } from 'react-leaflet';
import L from 'leaflet';
import { createCustomIcon, createBrandLogo } from '../utils/iconHelpers';

const IconMarkersLayer = ({ markers, selectedRegion, baseCompData = [], selectedJefeZona = '' }) => {
  const map = useMap();
  const markersRef = useRef({});
  const cacheRef = useRef({});
  const linesRef = useRef({});
  const updateInProgressRef = useRef(false);
  const zoomUpdateRef = useRef(null);
  
  const [showingAssociated, setShowingAssociated] = useState(false);
  const [originalPopupMarker, setOriginalPopupMarker] = useState(null);

  const makeDraggable = useCallback((popup) => {
    if (!map || !popup || !popup._container) return;
    try {
      const pos = map.latLngToLayerPoint(popup.getLatLng());
      L.DomUtil.setPosition(popup._wrapper.parentNode, pos);
      const draggable = new L.Draggable(popup._container, popup._wrapper);
      draggable.enable();
    } catch (error) {
      console.error('Error draggable:', error);
    }
  }, [map]);

  const createPopupContent = (marker) => {
    return `
      <div class="popup-container">
        <div class="popup-header">
          <img 
            src="${createBrandLogo(marker.Marca)}"
            alt="${marker.Marca}"
            style="width: 30px; height: 30px; border-radius: 50%; object-fit: cover; margin-right: 8px;"
            onerror="this.style.display='none'"
          />
          <h3 style="margin: 0;">${marker.nombre || marker.Marca}</h3>
        </div>
        <div class="popup-content">
          <div class="popup-data">
            <strong>Marca:</strong> ${marker.Marca}<br />
            <strong>PBL:</strong> ${marker.pbl || '-'}<br />
            <strong>Region:</strong> ${marker.Region}<br />
            <strong>Comuna:</strong> ${marker.Comuna}<br />
            ${marker.direccion ? `<strong>Direcci√≥n:</strong> ${marker.direccion}<br />` : ''}
            ${marker.eds ? `<strong>EDS:</strong> ${marker.eds}<br />` : ''}
          </div>
          <hr style="margin: 10px 0; border: none; border-top: 1px solid #e0e0e0" />
          <div class="popup-data">
            <strong>Precios:</strong><br />
            G93: ${marker.precio_g93 ? `$${marker.precio_g93}` : 'N/A'}<br />
            G95: ${marker.precio_g95 ? `$${marker.precio_g95}` : 'N/A'}<br />
            Diesel: ${marker.precio_diesel ? `$${marker.precio_diesel}` : 'N/A'}<br />
          </div>
          ${marker.Guerra_Precio === 'Si' ? `
            <hr style="margin: 10px 0; border: none; border-top: 1px solid #e0e0e0" />
            <div class="popup-data" style="color: #d32f2f; font-weight: bold;">
              ‚ö†Ô∏è Guerra de Precio Activa
            </div>
          ` : ''}
        </div>
      </div>
    `;
  };

  const addMercadoButton = (marker, popupContent) => {
    if (marker.pbl && (marker.Marca === 'Aramco' || marker.Marca === 'Petrobras')) {
      const lat = parseFloat(marker.lat);
      const lng = parseFloat(marker.lng);
      return popupContent + `<button class="associated-button" onclick="window.showAssociatedIds && window.showAssociatedIds(${marker.pbl}, ${lat}, ${lng}, '${marker.id}')">Mercado</button>`;
    }
    return popupContent;
  };

  const addMarkerToMap = (marker, mapInstance) => {
    const lat = parseFloat(marker.lat);
    const lng = parseFloat(marker.lng);

    if (!isNaN(lat) && !isNaN(lng)) {
      const iconUrl = createCustomIcon(marker.Marca);
      const leafletMarker = L.marker([lat, lng], { icon: iconUrl }).addTo(mapInstance);

      let popupContent = createPopupContent(marker);
      popupContent = addMercadoButton(marker, popupContent);

      const popup = L.popup({
        autoClose: false,
        closeOnClick: false,
        keepInView: true,
        autoPan: false
      }).setContent(popupContent);

      leafletMarker.bindPopup(popup);
      leafletMarker.on('popupopen', () => makeDraggable(popup));
      leafletMarker.on('click', () => leafletMarker.openPopup());
      
      return leafletMarker;
    }
    return null;
  };

  const updateIconsInViewport = useCallback((allMarkers, clearFirst = true) => {
    if (!map || updateInProgressRef.current) return;

    updateInProgressRef.current = true;
    const bounds = map.getBounds();
    const visibleMarkers = allMarkers.filter(marker => {
      return bounds.contains([marker.lat, marker.lng]);
    });

    console.log(`üéØ Actualizando ${visibleMarkers.length} iconos de ${allMarkers.length} totales...`);

    if (clearFirst) {
      Object.values(markersRef.current).forEach(marker => {
        try {
          if (map.hasLayer(marker)) {
            map.removeLayer(marker);
          }
        } catch (error) {
          console.error('Error removiendo:', error);
        }
      });
      markersRef.current = {};
    }

    if (visibleMarkers.length === 0) {
      updateInProgressRef.current = false;
      return;
    }

    const markersByBrand = {};
    visibleMarkers.forEach(marker => {
      if (!markersByBrand[marker.Marca]) {
        markersByBrand[marker.Marca] = [];
      }
      markersByBrand[marker.Marca].push(marker);
    });

    const brandOrder = ['Aramco', 'Petrobras', 'Copec', 'Shell', 'Blanco', 'Gulf', 'Petroprix'];
    let delayOffset = 0;
    const cacheKey = selectedRegion;
    let totalBrands = brandOrder.filter(b => markersByBrand[b]).length;
    let brandsProcessed = 0;
    
    brandOrder.forEach((brand) => {
      if (!markersByBrand[brand] || markersByBrand[brand].length === 0) return;

      const brandMarkers = markersByBrand[brand];
      
      setTimeout(() => {
        console.log(`üìç Procesando ${brandMarkers.length} de ${brand}...`);

        brandMarkers.forEach((marker) => {
          try {
            const markerId = `${cacheKey}-${marker.id}`;

            if (markersRef.current[markerId]) {
              return;
            }

            const leafletMarker = addMarkerToMap(marker, map);
            if (leafletMarker) {
              markersRef.current[markerId] = leafletMarker;

              cacheRef.current[markerId] = {
                lat: marker.lat,
                lng: marker.lng,
                marca: marker.Marca,
                nombre: marker.nombre,
                region: marker.Region
              };
            }
          } catch (error) {
            console.error(`Error:`, error);
          }
        });

        brandsProcessed++;
        localStorage.setItem('markersCache', JSON.stringify(cacheRef.current));

        if (brandsProcessed === totalBrands) {
          updateInProgressRef.current = false;
        }

      }, delayOffset);

      delayOffset += 60;
    });
  }, [map, makeDraggable, selectedRegion]);

  // ============================================
  // L√ìGICA PARA JEFE DE ZONA - MOSTRAR TODO SIN VIEWPORT
  // ============================================
  
  const showJefeZonaCompetencia = useCallback(() => {
    console.log(`üîç Mostrando competencia para jefe: ${selectedJefeZona}`);
    
    if (!map || !selectedJefeZona) return;

    // Obtener PBLs del jefe desde markers
    const jefeMarkers = markers.filter(m => 
      m.Region === selectedRegion && m.nombre === selectedJefeZona
    );
    
    const pblsDelJefe = new Set(jefeMarkers.map(m => m.pbl));
    console.log(`üìä PBLs del jefe:`, Array.from(pblsDelJefe));

    // Obtener TODAS las estaciones de esos PBLs (markers + baseCompData)
    const competenciaDelJefe = markers.filter(item => 
      pblsDelJefe.has(item.pbl)
    );
    
    console.log(`‚úÖ Competencia encontrada: ${competenciaDelJefe.length}`);

    if (competenciaDelJefe.length === 0) {
      console.warn(`‚ö†Ô∏è Sin competencia para estos PBLs`);
      return;
    }

    // Limpiar marcadores anteriores
    Object.values(markersRef.current).forEach(marker => {
      try {
        if (map.hasLayer(marker)) {
          map.removeLayer(marker);
        }
      } catch (error) {}
    });
    markersRef.current = {};

    console.log(`üé® Mostrando ${competenciaDelJefe.length} markers totales`);
    
    // Agregar marcadores SIN filtro de viewport
    competenciaDelJefe.forEach((marker) => {
      const markerId = `${selectedRegion}-${marker.id}`;
      
      if (markersRef.current[markerId]) {
        return;
      }

      const leafletMarker = addMarkerToMap(marker, map);
      if (leafletMarker) {
        markersRef.current[markerId] = leafletMarker;
      }
    });

    console.log(`‚úÖ ${Object.keys(markersRef.current).length} marcadores agregados al mapa`);
  }, [selectedJefeZona, markers, map, selectedRegion, addMarkerToMap]);

  // ============================================
  // EFECTO: Cuando cambia jefe de zona
  // ============================================

  useEffect(() => {
    if (selectedJefeZona) {
      showJefeZonaCompetencia();
    } else {
      // Volver a mostrar solo markers filtrados
      if (markers && markers.length > 0) {
        updateIconsInViewport(markers, true);
      }
    }
  }, [selectedJefeZona, showJefeZonaCompetencia, markers, updateIconsInViewport]);

  const showAssociatedIds = useCallback((pbl, lat, lng, originalMarkerId) => {
    console.log(`üîç PBL seleccionado: ${pbl}`);
    
    if (showingAssociated) {
      if (map) {
        map.eachLayer((layer) => {
          if (layer instanceof L.Marker) {
            layer.setOpacity(1);
          }
        });
      }
      
      Object.entries(linesRef.current).forEach(([key, polyline]) => {
        try {
          if (polyline && map.hasLayer(polyline)) {
            map.removeLayer(polyline);
          }
        } catch (e) {}
      });
      linesRef.current = {};

      Object.values(markersRef.current).forEach(marker => {
        try {
          if (!map.hasLayer(marker)) {
            map.addLayer(marker);
          }
          marker.setOpacity(1);
        } catch (error) {}
      });

      setShowingAssociated(false);
      setOriginalPopupMarker(null);
      return;
    }

    const associatedData = baseCompData.filter(item => 
      String(item.pbl).trim() === String(pbl).trim()
    );

    const marcadoresConLinea = associatedData.filter(item =>
      String(item.Marcador_Principal).toLowerCase().trim() === 'si'
    );
    
    console.log(`‚úÖ Competencia encontrada:`, associatedData.length);

    if (associatedData.length === 0) {
      console.warn(`‚ö†Ô∏è Sin competencia para PBL: ${pbl}`);
      return;
    }

    const ids = associatedData.map(item => item.id);
    const idsSet = new Set(ids);

    if (map) {
      let openPopup = null;
      map.eachLayer((layer) => {
        if (layer instanceof L.Marker && layer._popup && layer._popup.isOpen()) {
          openPopup = layer._popup;
        }
      });

      map.eachLayer((layer) => {
        if (layer instanceof L.Marker) {
          const mLatLng = layer.getLatLng();
          
          let shouldKeep = false;
          
          for (let id of idsSet) {
            const compItem = baseCompData.find(item => item.id === id);
            const markerItem = markers.find(m => m.id === id);
            const searchItem = compItem || markerItem;
            
            if (searchItem && 
                Math.abs(parseFloat(searchItem.lat) - mLatLng.lat) < 0.00001 && 
                Math.abs(parseFloat(searchItem.lng) - mLatLng.lng) < 0.00001) {
              shouldKeep = true;
              break;
            }
          }
          
          if (!shouldKeep) {
            shouldKeep = (Math.abs(mLatLng.lat - lat) < 0.00001 && 
                         Math.abs(mLatLng.lng - lng) < 0.00001);
          }

          layer.setOpacity(shouldKeep ? 1 : 0);
        }
      });

      marcadoresConLinea.forEach((compMarker) => {
        const markerData = baseCompData.find(m => m.id === compMarker.id);
        
        if (markerData) {
          const compLat = parseFloat(markerData.lat);
          const compLng = parseFloat(markerData.lng);

          if (!isNaN(compLat) && !isNaN(compLng) && !isNaN(lat) && !isNaN(lng)) {
            const lineId = `${lat},${lng}-${compMarker.id}`;
            
            if (linesRef.current[lineId]) {
              try {
                map.removeLayer(linesRef.current[lineId]);
              } catch (e) {}
            }
            
            const polyline = L.polyline(
              [[lat, lng], [compLat, compLng]],
              {
                color: '#0e0e0eff',
                weight: 3,
                opacity: 0.9,
                dashArray: '8, 4',
                lineCap: 'round',
                lineJoin: 'round',
                className: 'competition-polyline',
                offset: 0
              }
            ).bringToFront().addTo(map);

            linesRef.current[lineId] = polyline;
            console.log(`‚úÖ L√≠nea dibujada:`, lineId);
          }
        }
      });

      if (openPopup) {
        setTimeout(() => {
          try {
            openPopup._map = map;
            openPopup.update();
          } catch (e) {}
        }, 50);
      }

      setShowingAssociated(true);
    }
  }, [baseCompData, markers, map, showingAssociated]);

  useEffect(() => {
    window.showAssociatedIds = showAssociatedIds;
  }, [showAssociatedIds]);

  useEffect(() => {
    if (!map || !markers || markers.length === 0) return;

    if (!selectedJefeZona) {
      console.log(`‚úÖ Actualizando ${markers.length} marcadores`);
      updateIconsInViewport(markers, true);
    }

  }, [map, markers, selectedRegion, updateIconsInViewport, selectedJefeZona]);

  useEffect(() => {
    if (!map) return;

    const handleZoomEnd = () => {
      if (zoomUpdateRef.current) clearTimeout(zoomUpdateRef.current);
      
      zoomUpdateRef.current = setTimeout(() => {
        if (showingAssociated) {
          console.log('üîç Zoom en modo competencia - manteniendo filtro');
        } else if (markers && markers.length > 0 && !selectedJefeZona) {
          updateIconsInViewport(markers, false);
        }
      }, 400);
    };

    map.on('zoomend', handleZoomEnd);

    return () => {
      map.off('zoomend', handleZoomEnd);
    };
  }, [map, markers, updateIconsInViewport, showingAssociated, selectedJefeZona]);

  return null;
};

export default IconMarkersLayer;
