import React, { useEffect, useRef, useState } from 'react';
import { useMap } from 'react-leaflet';
import L from 'leaflet';
import { createCustomIcon, createBrandLogo } from '../utils/iconHelpers';

const IconMarkersLayer = ({ markers, selectedRegion }) => {
  const map = useMap();
  const markersRef = useRef({});
  const cacheRef = useRef({});
  const loadingRef = useRef({});
  const zoomUpdateRef = useRef(null);
  const panUpdateRef = useRef(null);
  const [visibleCount, setVisibleCount] = useState(0);

  // Cargar cache
  useEffect(() => {
    const cachedMarkers = localStorage.getItem('markersCache');
    if (cachedMarkers) {
      try {
        cacheRef.current = JSON.parse(cachedMarkers);
        console.log('‚úÖ Cache cargado');
      } catch (error) {
        console.error('Error cache:', error);
      }
    }
  }, []);

  // Renderizar solo marcadores visibles en el viewport
  const renderMarkersInViewport = (allMarkers) => {
    if (!map) return;

    // Obtener bounds del viewport actual
    const bounds = map.getBounds();

    // Filtrar solo marcadores dentro del viewport
    const visibleMarkers = allMarkers.filter(marker => {
      return bounds.contains([marker.lat, marker.lng]);
    });

    console.log(`üéØ ${visibleMarkers.length} marcadores visibles en viewport`);
    setVisibleCount(visibleMarkers.length);

    // Limpiar antiguos
    Object.values(markersRef.current).forEach(marker => {
      if (map.hasLayer(marker)) {
        map.removeLayer(marker);
      }
    });
    markersRef.current = {};

    if (visibleMarkers.length === 0) {
      console.log('‚ö†Ô∏è Sin marcadores visibles en este √°rea');
      return;
    }

    // Agrupar por marca
    const markersByBrand = {};
    visibleMarkers.forEach(marker => {
      if (!markersByBrand[marker.Marca]) {
        markersByBrand[marker.Marca] = [];
      }
      markersByBrand[marker.Marca].push(marker);
    });

    const brandOrder = ['Aramco', 'Petrobras', 'Copec', 'Shell', 'Blanco', 'Gulf', 'Petroprix'];
    let delayOffset = 0;
    const cacheKey = selectedRegion;
    
    brandOrder.forEach((brand) => {
      if (!markersByBrand[brand] || markersByBrand[brand].length === 0) return;

      const brandMarkers = markersByBrand[brand];
      
      setTimeout(() => {
        console.log(`üìç Agregando ${brandMarkers.length} de ${brand}...`);

        brandMarkers.forEach((marker) => {
          try {
            const markerId = `${cacheKey}-${marker.id}`;

            const leafletMarker = L.marker([marker.lat, marker.lng], {
              icon: createCustomIcon(marker.Marca),
              title: marker.nombre
            });

            // Popup solo al click
            leafletMarker.on('click', function() {
              const popupContent = `
                <div class="popup-container">
                  <div class="popup-header">
                    <img 
                      src="${createBrandLogo(marker.Marca)}"
                      alt="${marker.Marca}"
                      style="width: 30px; height: 30px; border-radius: 50%; object-fit: cover; margin-right: 8px;"
                      onerror="this.style.display='none'"
                    />
                    <h3 style="margin: 0;">${marker.nombre}</h3>
                  </div>
                  <div class="popup-content">
                    <div class="popup-data">
                      <strong>Marca:</strong> ${marker.Marca}<br />
                      <strong>Region:</strong> ${marker.Region}<br />
                      <strong>Comuna:</strong> ${marker.Comuna}<br />
                      ${marker.direccion ? `<strong>Direcci√≥n:</strong> ${marker.direccion}<br />` : ''}
                      ${marker.eds ? `<strong>EDS:</strong> ${marker.eds}<br />` : ''}
                    </div>
                    <hr style="margin: 10px 0; border: none; border-top: 1px solid #e0e0e0" />
                    <div class="popup-data">
                      <strong>Precios:</strong><br />
                      G93: ${marker.precio_g93 ? `$${marker.precio_g93}` : 'N/A'}<br />
                      G95: ${marker.precio_g95 ? `$${marker.precio_g95}` : 'N/A'}<br />
                      Diesel: ${marker.precio_diesel ? `$${marker.precio_diesel}` : 'N/A'}<br />
                    </div>
                    ${marker.Guerra_Precio === 'Si' ? `
                      <hr style="margin: 10px 0; border: none; border-top: 1px solid #e0e0e0" />
                      <div class="popup-data" style="color: #d32f2f; font-weight: bold;">
                        ‚ö†Ô∏è Guerra de Precio Activa
                      </div>
                    ` : ''}
                  </div>
                </div>
              `;

              leafletMarker.setPopupContent(popupContent);
              leafletMarker.openPopup();
            });

            leafletMarker.addTo(map);
            markersRef.current[markerId] = leafletMarker;

            cacheRef.current[markerId] = {
              lat: marker.lat,
              lng: marker.lng,
              marca: marker.Marca,
              nombre: marker.nombre,
              region: marker.Region
            };
          } catch (error) {
            console.error(`Error:`, error);
          }
        });

        localStorage.setItem('markersCache', JSON.stringify(cacheRef.current));

      }, delayOffset);

      delayOffset += 100;
    });
  };

  // Renderizar inicial cuando cambia regi√≥n
  useEffect(() => {
    if (!map || markers.length === 0) return;

    const filteredMarkers = markers.filter(m => m.Region === selectedRegion);

    if (loadingRef.current[selectedRegion]) {
      return;
    }

    loadingRef.current[selectedRegion] = true;

    console.log(`üìä Iniciando carga de ${filteredMarkers.length} de ${selectedRegion}...`);

    // Renderizar solo visibles
    renderMarkersInViewport(filteredMarkers);

  }, [map, markers, selectedRegion]);

  // Actualizar marcadores cuando hace zoom/pan
  useEffect(() => {
    if (!map) return;

    const handleZoomEnd = () => {
      if (zoomUpdateRef.current) clearTimeout(zoomUpdateRef.current);
      
      zoomUpdateRef.current = setTimeout(() => {
        const filteredMarkers = markers.filter(m => m.Region === selectedRegion);
        renderMarkersInViewport(filteredMarkers);
      }, 300); // Esperar 300ms despu√©s de terminar zoom
    };

    const handleDragEnd = () => {
      if (panUpdateRef.current) clearTimeout(panUpdateRef.current);
      
      panUpdateRef.current = setTimeout(() => {
        const filteredMarkers = markers.filter(m => m.Region === selectedRegion);
        renderMarkersInViewport(filteredMarkers);
      }, 200);
    };

    map.on('zoomend', handleZoomEnd);
    map.on('dragend', handleDragEnd);

    return () => {
      map.off('zoomend', handleZoomEnd);
      map.off('dragend', handleDragEnd);
      if (zoomUpdateRef.current) clearTimeout(zoomUpdateRef.current);
      if (panUpdateRef.current) clearTimeout(panUpdateRef.current);
    };
  }, [map, markers, selectedRegion]);

  return null;
};

export default IconMarkersLayer;
