import React from 'react';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';
import { useMapData } from '../hooks/useMapData';

// Fix para iconos de Leaflet
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: require('leaflet/dist/images/marker-icon-2x.png'),
  iconUrl: require('leaflet/dist/images/marker-icon.png'),
  shadowUrl: require('leaflet/dist/images/marker-shadow.png'),
});

const MapComponent = () => {
  const { markers, loading } = useMapData();

  if (loading) {
    return (
      <div style={{
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        height: '100%',
        fontSize: '16px',
        color: '#666'
      }}>
        üìç Cargando {markers.length} marcadores...
      </div>
    );
  }

  return (
    <MapContainer
      center={[-33.8688, -70.9123]}
      zoom={12}
      style={{ width: '100%', height: '100%' }}
      zoomControl={false}
    >
      <TileLayer
        attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a>'
        url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
      />
      
      {markers.map((marker) => (
        <Marker
          key={marker.id}
          position={[marker.lat, marker.lng]}
        >
                   <Popup maxWidth={280}>
            <div className="popup-container">
              <div className="popup-header">
                <h3>{marker.nombre}</h3>
              </div>
              <div className="popup-content">
                <div className="popup-data">
                  <strong>Marca:</strong> {marker.Marca}<br />
                  <strong>ID:</strong> {marker.id}<br />
                  {marker.direccion && <><strong>Direcci√≥n:</strong> {marker.direccion}<br /></>}
                </div>
                <hr style={{ margin: '10px 0', border: 'none', borderTop: '1px solid #e0e0e0' }} />
                <div className="popup-data">
                  <strong>Precios:</strong><br />
                  G93: <strong>${marker.precio_g93}</strong><br />
                  G95: <strong>${marker.precio_g95}</strong><br />
                  Diesel: <strong>${marker.precio_diesel}</strong>
                </div>
              </div>
            </div>
          </Popup>

        </Marker>
      ))}
    </MapContainer>
  );
};

export default MapComponent;
