// TestSQL.jsx
import React, { useState } from 'react';

function TestSQL() {
    const [resultado, setResultado] = useState(null);

    const probar = async () => {
        try {
            const res = await fetch('http://localhost/test-sql.php');
            const data = await res.json();
            setResultado(data);
        } catch (err) {
            setResultado({ success: false, error: err.message });
        }
    };

    return (
        <div style={{ padding: '20px' }}>
            <button onClick={probar} style={{ padding: '15px 30px', fontSize: '16px' }}>
                üß™ Probar Conexi√≥n SQL
            </button>

            {resultado && (
                <div style={{
                    marginTop: '20px',
                    padding: '15px',
                    backgroundColor: resultado.success ? '#d4edda' : '#f8d7da',
                    border: `2px solid ${resultado.success ? 'green' : 'red'}`,
                    borderRadius: '5px'
                }}>
                    <h3>{resultado.success ? '‚úÖ √âXITO' : '‚ùå ERROR'}</h3>
                    <p>{resultado.mensaje || resultado.error}</p>

                    {resultado.datos && (
                        <pre style={{ backgroundColor: '#f5f5f5', padding: '10px' }}>
                            {JSON.stringify(resultado.datos, null, 2)}
                        </pre>
                    )}
                </div>
            )}
        </div>
    );
}

export default TestSQL;
