import React, { useEffect, useRef, useState, useCallback } from 'react';
import { useMap } from 'react-leaflet';
import L from 'leaflet';
import { createCustomIcon, createBrandLogo } from '../utils/iconHelpers';

const IconMarkersLayer = ({ markers, selectedRegion, baseCompData = [] }) => {
  const map = useMap();
  const markersRef = useRef({});
  const cacheRef = useRef({});
  const updateInProgressRef = useRef(false);
  const zoomUpdateRef = useRef(null);
  const [showingAssociated, setShowingAssociated] = useState(false);
  const [originalPopupMarker, setOriginalPopupMarker] = useState(null);

  const makeDraggable = useCallback((popup) => {
    if (!map || !popup || !popup._container) return;
    try {
      const pos = map.latLngToLayerPoint(popup.getLatLng());
      L.DomUtil.setPosition(popup._wrapper.parentNode, pos);
      const draggable = new L.Draggable(popup._container, popup._wrapper);
      draggable.enable();
    } catch (error) {
      console.error('Error draggable:', error);
    }
  }, [map]);

  const addMarkers = useCallback((data, mapInstance) => {
    data.forEach(marker => {
      const lat = parseFloat(marker.lat);
      const lng = parseFloat(marker.lng);

      if (!isNaN(lat) && !isNaN(lng)) {
        const iconUrl = createCustomIcon(marker.Marca);
        const leafletMarker = L.marker([lat, lng], { icon: iconUrl }).addTo(mapInstance);

        let popupContent = `
          <div class="popup-content">
            <strong>${marker.nombre}</strong><br/>
            Marca: ${marker.Marca}<br/>
            EDS: ${marker.eds}<br/>
            Comuna: ${marker.Comuna}<br/>
            Región: ${marker.Region}
            <button class="btn-mercado" data-marker-id="${marker.id}">Ver Mercado</button>
          </div>
        `;

        const popup = L.popup({ maxWidth: 300 }).setContent(popupContent);
        leafletMarker.bindPopup(popup);

        leafletMarker.on('click', () => {
          mapInstance.setView([lat, lng], mapInstance.getZoom());
          setTimeout(() => {
            makeDraggable(popup);
            const btn = document.querySelector(`[data-marker-id="${marker.id}"]`);
            if (btn) {
              btn.addEventListener('click', (e) => {
                e.stopPropagation();
                handleShowMercado(marker);
              });
            }
          }, 100);
        });

        markersRef.current[marker.id] = {
          marker: leafletMarker,
          data: marker,
          popup: popup,
        };
      }
    });
  }, [makeDraggable]);

  const handleShowMercado = useCallback((marker) => {
    // LÓGICA CORREGIDA: Mostrar mercado del EDS + TODA la competencia en la misma Comuna
    const markersEnComunaDelEds = markers.filter(m => m.Comuna === marker.Comuna);

    Object.values(markersRef.current).forEach(markerRef => {
      markerRef.marker.setOpacity(0.3);
    });

    markersEnComunaDelEds.forEach(m => {
      const key = m.id;
      if (markersRef.current[key]) {
        markersRef.current[key].marker.setOpacity(1);
      }
    });

    setShowingAssociated(true);
    setOriginalPopupMarker(marker);

    console.log(`Mostrando mercado para: ${marker.nombre} en Comuna: ${marker.Comuna}`);
    console.log(`Total de estaciones en esta comuna: ${markersEnComunaDelEds.length}`);
  }, [markers]);

  const handleBackFromMercado = useCallback(() => {
    Object.values(markersRef.current).forEach(markerRef => {
      markerRef.marker.setOpacity(1);
    });
    setShowingAssociated(false);
    setOriginalPopupMarker(null);
  }, []);

  useEffect(() => {
    if (!map) return;

    const clearMarkers = () => {
      Object.values(markersRef.current).forEach(markerRef => {
        map.removeLayer(markerRef.marker);
      });
      markersRef.current = {};
    };

    clearMarkers();
    addMarkers(markers, map);

    return () => {
      clearMarkers();
    };
  }, [markers, map, addMarkers]);

  useEffect(() => {
    const handleBackClick = () => {
      if (showingAssociated) {
        handleBackFromMercado();
      }
    };

    document.addEventListener('custom-back-mercado', handleBackClick);
    return () => {
      document.removeEventListener('custom-back-mercado', handleBackClick);
    };
  }, [showingAssociated, handleBackFromMercado]);

  return null;
};

export default IconMarkersLayer;
