import React, { useEffect, useRef } from 'react';
import { useMap } from 'react-leaflet';
import L from 'leaflet';
import { createCustomIcon } from '../utils/iconHelpers';

const IconMarkersLayer = ({ markers, selectedRegion, onMarkerClick }) => {
  const map = useMap();
  const markersRef = useRef({});
  const cacheRef = useRef({});
  const loadingRef = useRef({});
  const zoomUpdateRef = useRef(null);
  const updateInProgressRef = useRef(false);

  // Cargar cache
  useEffect(() => {
    const cachedMarkers = localStorage.getItem('markersCache');
    if (cachedMarkers) {
      try {
        cacheRef.current = JSON.parse(cachedMarkers);
        console.log('âœ… Cache cargado');
      } catch (error) {
        console.error('Error cache:', error);
      }
    }
  }, []);

  // Renderizar iconos
  const updateIconsInViewport = (allMarkers, clearFirst = true) => {
    if (!map || updateInProgressRef.current) return;

    updateInProgressRef.current = true;
    const bounds = map.getBounds();
    const visibleMarkers = allMarkers.filter(marker => {
      return bounds.contains([marker.lat, marker.lng]);
    });

    console.log(`ðŸŽ¯ Actualizando ${visibleMarkers.length} iconos...`);

    if (clearFirst) {
      Object.values(markersRef.current).forEach(marker => {
        try {
          if (map.hasLayer(marker)) {
            map.removeLayer(marker);
          }
        } catch (error) {
          console.error('Error removiendo:', error);
        }
      });
      markersRef.current = {};
    }

    if (visibleMarkers.length === 0) {
      updateInProgressRef.current = false;
      return;
    }

    // Agrupar por marca
    const markersByBrand = {};
    visibleMarkers.forEach(marker => {
      if (!markersByBrand[marker.Marca]) {
        markersByBrand[marker.Marca] = [];
      }
      markersByBrand[marker.Marca].push(marker);
    });

    const brandOrder = ['Aramco', 'Petrobras', 'Copec', 'Shell', 'Blanco', 'Gulf', 'Petroprix'];
    let delayOffset = 0;
    const cacheKey = selectedRegion;
    let totalBrands = brandOrder.filter(b => markersByBrand[b]).length;
    let brandsProcessed = 0;
    
    brandOrder.forEach((brand) => {
      if (!markersByBrand[brand] || markersByBrand[brand].length === 0) return;

      const brandMarkers = markersByBrand[brand];
      
      setTimeout(() => {
        console.log(`ðŸ“ Procesando ${brandMarkers.length} de ${brand}...`);

        brandMarkers.forEach((marker) => {
          try {
            const markerId = `${cacheKey}-${marker.id}`;

            if (markersRef.current[markerId]) {
              return;
            }

            const leafletMarker = L.marker([marker.lat, marker.lng], {
              icon: createCustomIcon(marker.Marca),
              title: marker.nombre
            });

            // Click: abrir popup custom
            leafletMarker.on('click', function() {
              onMarkerClick(marker);
            });

            leafletMarker.addTo(map);
            markersRef.current[markerId] = leafletMarker;

            cacheRef.current[markerId] = {
              lat: marker.lat,
              lng: marker.lng,
              marca: marker.Marca,
              nombre: marker.nombre,
              region: marker.Region
            };
          } catch (error) {
            console.error(`Error:`, error);
          }
        });

        brandsProcessed++;
        localStorage.setItem('markersCache', JSON.stringify(cacheRef.current));

        if (brandsProcessed === totalBrands) {
          updateInProgressRef.current = false;
        }

      }, delayOffset);

      delayOffset += 60;
    });
  };

  // Iniciar carga
  useEffect(() => {
    if (!map || markers.length === 0) return;

    const filteredMarkers = markers.filter(m => m.Region === selectedRegion);

    if (loadingRef.current[selectedRegion]) {
      return;
    }

    loadingRef.current[selectedRegion] = true;
    updateIconsInViewport(filteredMarkers, true);

  }, [map, markers, selectedRegion, onMarkerClick]);

  // Debounce zoom
  useEffect(() => {
    if (!map) return;

    const handleZoomEnd = () => {
      if (zoomUpdateRef.current) clearTimeout(zoomUpdateRef.current);
      
      zoomUpdateRef.current = setTimeout(() => {
        const filteredMarkers = markers.filter(m => m.Region === selectedRegion);
        updateIconsInViewport(filteredMarkers, false);
      }, 400);
    };

    map.on('zoomend', handleZoomEnd);

    return () => {
      map.off('zoomend', handleZoomEnd);
    };
  }, [map, markers, selectedRegion]);

  return null;
};

export default IconMarkersLayer;
