import React from 'react';
import { MapContainer, TileLayer } from 'react-leaflet';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';
import { useMapData } from '../hooks/useMapData';
import CustomPopup from './CustomPopup';

import IconMarkersLayer from './IconMarkersLayer';


delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: require('leaflet/dist/images/marker-icon-2x.png'),
  iconUrl: require('leaflet/dist/images/marker-icon.png'),
  shadowUrl: require('leaflet/dist/images/marker-shadow.png'),
});

const MapComponent = ({ selectedRegion, regionCenter }) => {
  const { markers, loading } = useMapData();

  if (loading) {
    return (
      <div style={{
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        justifyContent: 'center',
        height: '100%',
        fontSize: '16px',
        color: '#666',
        gap: '20px',
        background: 'linear-gradient(135deg, #f5f5f5 0%, #fff 100%)'
      }}>
        <div style={{ fontSize: '40px' }}>üìç</div>
        <div>Cargando todos los marcadores...</div>
        <div style={{
          width: '200px',
          height: '4px',
          background: '#e0e0e0',
          borderRadius: '2px',
          overflow: 'hidden'
        }}>
          <div style={{
            height: '100%',
            background: '#00b8a9',
            borderRadius: '2px',
            animation: 'loading 1.5s infinite',
            width: '30%'
          }}></div>
        </div>
        <style>{`
          @keyframes loading {
            0% { width: 0%; }
            50% { width: 100%; }
            100% { width: 0%; }
          }
        `}</style>
      </div>
    );
  }

  const mapCenter = regionCenter || [-33.4474107, -70.5518946];

  return (
    <MapContainer
      center={mapCenter}
      zoom={9}
      style={{ width: '100%', height: '100%' }}
      zoomControl={false}
      key={selectedRegion}
    >
      <TileLayer
        attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a>'
        url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
        maxZoom={18}
      />
      
      <IconMarkersLayer markers={markers} selectedRegion={selectedRegion} />


    </MapContainer>
  );
};

export default MapComponent;
