import React, { useMemo, useState, useRef } from 'react';

const COLORS = {
  G93: '#34c759',
  G95: '#007bff',
  G97: '#a259c1',
  Diesel: '#fd7e14',
  Kero: '#6c757d'
};

const FUEL_OPTIONS = [
  { key: 'G93', field: 'g93', label: 'G93' },
  { key: 'G95', field: 'g95', label: 'G95' },
  { key: 'G97', field: 'g97', label: 'G97' },
  { key: 'Diesel', field: 'diesel', label: 'Diesel' },
  { key: 'Kero', field: 'kero', label: 'Kero' }
];

const toNum = (v) => (v === '-' || v == null ? null : Number(v));

// Normaliza fecha y devuelve Date seguro + etiqueta DD-MM
function parseDateSafe(fecha) {
  if (!fecha) return { d: new Date(0), label: '' };
  const token = String(fecha).split(' ')[0];
  let d;
  if (/^\d{4}-\d{2}-\d{2}$/.test(token)) {
    d = new Date(token);
    const [, m, day] = token.split('-');
    return { d, label: `${day.padStart(2,'0')}-${m.padStart(2,'0')}` };
  }
  if (/^\d{2}-\d{2}-\d{4}$/.test(token)) {
    const [day, m, y] = token.split('-').map(s => s.padStart(2,'0'));
    d = new Date(`${y}-${m}-${day}`);
    return { d, label: `${day}-${m}` };
  }
  d = new Date(token);
  const dd = String(d.getDate()).padStart(2,'0');
  const mm = String(d.getMonth()+1).padStart(2,'0');
  return { d, label: `${dd}-${mm}` };
}

export default function TrendChart({ dataRows = [], maxPoints = 7, height = 170 }) {
  const [fuelKey, setFuelKey] = useState('G93');
  const svgRef = useRef(null);

  // 1) Ordena por fecha asc y toma las ÚLTIMAS 7; luego invierte para mostrar de más reciente a más antiguo
  const points = useMemo(() => {
    const norm = dataRows.map(r => {
      const { d, label } = parseDateSafe(r.fecha);
      return {
        date: d,
        label,
        G93: toNum(r.g93),
        G95: toNum(r.g95),
        G97: toNum(r.g97),
        Diesel: toNum(r.diesel),
        Kero: toNum(r.kero)
      };
    }).sort((a, b) => a.date - b.date).slice(-maxPoints).reverse();
    return norm;
  }, [dataRows, maxPoints]);

  const labels = points.map(p => p.label);

  // Serie activa
  const values = points.map(p => p[fuelKey]);
  const present = values.filter(v => v != null);
  const aMin = present.length ? Math.min(...present) : 0;
  const aMax = present.length ? Math.max(...present) : 1;
  const yMin = aMin;
  const yMax = aMax === aMin ? aMin + 1 : aMax;

  // Layout
  const width = 320;
  const margin = { top: 6, right: 8, bottom: 28, left: 46 };
  const innerW = width - margin.left - margin.right;
  const innerH = height - margin.top - margin.bottom;

  const xPos = (i) => {
    if (labels.length <= 1) return margin.left + innerW / 2;
    return margin.left + (i * innerW) / (labels.length - 1);
  };
  const yPos = (v) => {
    if (v == null) return null;
    const span = Math.max(1, (yMax - yMin));
    const y = (v - yMin) / span;
    return margin.top + innerH - y * innerH;
  };

  // Barra compacta y separaciones
  const colWidth = labels.length > 1 ? Math.max(12, Math.min(28, innerW / (labels.length * 1.6))) : 18;

  // Ticks Y
  const gridY = 3;
  const yTicks = Array.from({ length: gridY + 1 }, (_, k) => yMin + (k * (yMax - yMin)) / Math.max(1, gridY));

  // Tooltip controlado solo cuando el cursor está sobre una barra
  const tipId = 'trend-tip';
  const hlId = 'trend-hl';

  const showTipAt = (evt, label, value, xCenter) => {
    const tip = document.getElementById(tipId);
    const hl = document.getElementById(hlId);
    if (!tip || !hl) return;
    tip.style.display = 'block';
    tip.textContent = `${label} • ${Number(value).toLocaleString('es-CL')}`;
    tip.style.left = `${evt.clientX + 8}px`;
    tip.style.top = `${evt.clientY - 18}px`;
    hl.setAttribute('x1', xCenter);
    hl.setAttribute('x2', xCenter);
    hl.setAttribute('y1', margin.top);
    hl.setAttribute('y2', margin.top + innerH);
    hl.style.display = 'block';
  };

  const hideTip = () => {
    const tip = document.getElementById(tipId);
    const hl = document.getElementById(hlId);
    if (tip) tip.style.display = 'none';
    if (hl) hl.style.display = 'none';
  };

  return (
    <div style={{ minWidth: 320, height, position: 'relative' }}>
      {/* Tooltip absoluto */}
      <div
        id={tipId}
        style={{
          position: 'fixed',
          zIndex: 9999,
          display: 'none',
          background: 'rgba(0,0,0,0.8)',
          color: '#fff',
          padding: '2px 6px',
          borderRadius: 4,
          fontSize: 11,
          pointerEvents: 'none'
        }}
      />

      {/* Header con filtro */}
      <div style={{ display: 'flex', alignItems: 'center', gap: 8, marginBottom: 4, paddingLeft: 6 }}>
        <div style={{ width: 8, height: 8, background: COLORS[fuelKey], borderRadius: 2 }} />
        <span style={{ fontSize: 11, color: '#333' }}>Combustible:</span>
        <select
          value={fuelKey}
          onChange={(e) => { hideTip(); return setFuelKey(e.target.value); }}
          style={{ fontSize: 11, padding: '2px 6px', borderRadius: 4, border: '1px solid #d0d0d0', outline: 'none', background: '#fff' }}
        >
          {FUEL_OPTIONS.map(f => (
            <option key={f.key} value={f.key}>{f.label}</option>
          ))}
        </select>
      </div>

      <svg ref={svgRef} width={width} height={height}>
        {/* Línea vertical highlight */}
        <line id={hlId} x1="0" y1="0" x2="0" y2="0" stroke="rgba(0,0,0,0.08)" strokeWidth="1" style={{ display: 'none' }} />

        {/* Grid Y */}
        {yTicks.map((t, idx) => {
          const y = yPos(t);
          return (
            <g key={idx}>
              <line x1={margin.left} x2={margin.left + innerW} y1={y} y2={y} stroke="rgba(0,0,0,0.08)" />
              <text x={margin.left - 6} y={y} textAnchor="end" dominantBaseline="middle" fontSize="10">
                {Math.round(t).toLocaleString('es-CL')}
              </text>
            </g>
          );
        })}

        {/* Etiquetas X (última a la izquierda porque invertimos para mostrar “más reciente primero”) */}
        {labels.map((lab, i) => (
          <text key={i} x={xPos(i)} y={height - 6} textAnchor="middle" fontSize="10">{lab}</text>
        ))}

        {/* Barras con mouseenter/leave por barra (tooltip sólo sobre barra) */}
        {values.map((v, i) => {
          const y = yPos(v);
          if (y == null) return (
            <rect key={i} x={xPos(i) - colWidth / 2} y={margin.top + innerH} width={colWidth} height={0} fill="transparent" />
          );
          const xCenter = xPos(i);
          const x = xCenter - colWidth / 2;
          const barH = margin.top + innerH - y;
          return (
            <rect
              key={i}
              x={x}
              y={y}
              width={colWidth}
              height={barH}
              fill={COLORS[fuelKey]}
              rx="2"
              onMouseEnter={(e) => showTipAt(e, labels[i], v, xCenter)}
              onMouseMove={(e) => showTipAt(e, labels[i], v, xCenter)}
              onMouseLeave={hideTip}
            />
          );
        })}
      </svg>
    </div>
  );
}
