import { useState, useEffect } from 'react';
import { getDataFromAPI } from '../services/apiService';

export const useMapData = () => {
  const [markers, setMarkers] = useState([]);
  const [loading, setLoading] = useState(true);

  // Carga inicial desde API
  useEffect(() => {
    const fetchData = async () => {
      try {
        console.log('ðŸ“¡ Obteniendo datos desde API...');
        const response = await getDataFromAPI();
        
        // Tu API devuelve { data: [...] }
        const data = Array.isArray(response) ? response : (response.data || []);
        
        // Mapear datos de tu API al formato del mapa
        const formattedData = data.map(item => {
          // Convertir strings a nÃºmeros y asegurar que sean vÃ¡lidos
          const lat = parseFloat(item.latitud);
          const lng = parseFloat(item.longitud);

          return {
            id: item.id || item.pbl,
            pbl: item.pbl,
            eds: item.eds,
            // IMPORTANTE: [lat, lng] para Leaflet (invierte si es necesario)
            lat: isNaN(lat) ? -33.8688 : lat,
            lng: isNaN(lng) ? -70.9123 : lng,
            Marca: item.Marca || 'Unknown',
            nombre: item.nombre || item.eds || `EstaciÃ³n ${item.id}`,
            // Precios - manejar null
            precio_g93: item.G93 || 0,
            precio_g95: item.G95 || 0,
            precio_diesel: item.Diesel || 0,
            direccion: item.direccion || '',
            logo: item.logo || '',
            Region: item.Region || '',
            Comuna: item.Comuna || '',
            Guerra_Precio: item.Guerra_Precio || 'No',
            Actualizacion: item.Actualizacion || ''
          };
        }).filter(m => !isNaN(m.lat) && !isNaN(m.lng)); // Filtrar marcadores invÃ¡lidos

        console.log(`âœ… ${formattedData.length} marcadores cargados correctamente`);
        console.log('Primer marcador:', formattedData[0]);
        setMarkers(formattedData);
        setLoading(false);
      } catch (error) {
        console.error("Error loading data:", error);
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  // Polling cada 30 minutos para actualizar datos
  useEffect(() => {
    const interval = setInterval(async () => {
      try {
        console.log('ðŸ”„ Actualizando datos...', new Date().toLocaleTimeString());
        const response = await getDataFromAPI();
        
        const data = Array.isArray(response) ? response : (response.data || []);
        
        const formattedData = data.map(item => {
          const lat = parseFloat(item.latitud);
          const lng = parseFloat(item.longitud);

          return {
            id: item.id || item.pbl,
            pbl: item.pbl,
            eds: item.eds,
            lat: isNaN(lat) ? -33.8688 : lat,
            lng: isNaN(lng) ? -70.9123 : lng,
            Marca: item.Marca || 'Unknown',
            nombre: item.nombre || item.eds || `EstaciÃ³n ${item.id}`,
            precio_g93: item.G93 || 0,
            precio_g95: item.G95 || 0,
            precio_diesel: item.Diesel || 0,
            direccion: item.direccion || '',
            logo: item.logo || '',
            Region: item.Region || '',
            Comuna: item.Comuna || '',
            Guerra_Precio: item.Guerra_Precio || 'No',
            Actualizacion: item.Actualizacion || ''
          };
        }).filter(m => !isNaN(m.lat) && !isNaN(m.lng));

        setMarkers(formattedData);
        console.log('âœ… Datos actualizados:', formattedData.length);
      } catch (error) {
        console.error("Error updating data:", error);
      }
    }, 1800000); // 30 minutos

    return () => clearInterval(interval);
  }, []);

  return { markers, loading };
};
