import React, { useState, useEffect, useMemo } from 'react';
import { getMercadoAlerta, getHistoricoAlerta } from '../services/apiService';
import '../styles/AlertPanel.css';

// Agrupar alertas por estaciÃ³n
function groupAlertsByStation(alerts) {
  const grouped = {};
  alerts.forEach(alert => {
    const key = alert.Nom_Eds || 'Sin nombre';
    if (!grouped[key]) grouped[key] = [];
    grouped[key].push(alert);
  });
  return grouped;
}

// CONTADOR CORRECTO DE ESTACIONES ÃšNICAS
function getStationCount(alerts) {
  const estaciones = {};
  alerts.forEach(a => estaciones[a.Nom_Eds || 'Sin nombre'] = true);
  return Object.keys(estaciones).length;
}

// FunciÃ³n para parsear fechas en formato "DD-MM-YYYY HH:mm"
function parseFecha(fechaStr) {
  const [datePart, timePart] = fechaStr.split(' ');
  const [day, month, year] = datePart.split('-').map(Number);
  const [hours, minutes] = timePart ? timePart.split(':').map(Number) : [0, 0];
  return new Date(year, month - 1, day, hours, minutes);
}

const ITEMS_PER_PAGE = 50;

const AlertPanel = ({ isVisible, onClose, alertasExternas }) => {
  const [activeTab, setActiveTab] = useState('alertas');
  const [alertas, setAlertas] = useState([]);
  const [historico, setHistorico] = useState([]);
  const [loading, setLoading] = useState(true);
  const [page, setPage] = useState(1);

  useEffect(() => {
    if (alertasExternas) {
      setAlertas(alertasExternas);
      setLoading(false);
    } else if (isVisible) {
      loadData();
    }
  }, [isVisible, alertasExternas]);

  const loadData = async () => {
    try {
      setLoading(true);
      const [alertasData, historicoData] = await Promise.all([
        getMercadoAlerta(),
        getHistoricoAlerta()
      ]);
      setAlertas(Array.isArray(alertasData) ? alertasData : []);
      setHistorico(Array.isArray(historicoData) ? historicoData : []);
    } catch {
      setAlertas([]);
      setHistorico([]);
    } finally {
      setLoading(false);
    }
  };

  const agrupadas = useMemo(() => groupAlertsByStation(alertas), [alertas]);
  const cantidadEstaciones = useMemo(() => getStationCount(alertas), [alertas]);

  // âœ… NUEVO: Filtrar PBL en guerra de precios
 // âœ… CORREGIDO: Filtrar PBL en guerra de precios (TODOS, sin lÃ­mite)
const pblsEnGuerra = useMemo(() => {
  console.log('ðŸ” Total registros histÃ³rico:', historico.length);
  
  const guerraMap = new Map();
  let contadorGuerra = 0;
  
  historico.forEach(item => {
    // âœ… Verificar que Guerra_Precio sea exactamente 'Si' (case-sensitive)
    const esGuerra = item.Guerra_Precio === 'Si' || item.Guerra_Precio === 'SI' || item.Guerra_Precio === 'si';
    
    // âœ… Filtrar solo marcas propias
    const esMarcaPropia = item.marca === 'Aramco' || item.marca === 'Petrobras' || 
                          item.marca === 'ARAMCO' || item.marca === 'PETROBRAS';
    
    if (esGuerra && esMarcaPropia) {
      contadorGuerra++;
      const key = item.pbl;
      
      if (!guerraMap.has(key)) {
        guerraMap.set(key, {
          pbl: item.pbl,
          estacion: item.Nom_Eds,
          marca: item.marca,
          cod_cne: item.cod_cne,
          combustibles: new Set(),
          fechaUltima: item.Fecha
        });
      }
      guerraMap.get(key).combustibles.add(item.Combustible);
      
      // Actualizar fecha si es mÃ¡s reciente
      if (parseFecha(item.Fecha) > parseFecha(guerraMap.get(key).fechaUltima)) {
        guerraMap.get(key).fechaUltima = item.Fecha;
      }
    }
  });
  
  console.log('ðŸ” Registros con Guerra_Precio="Si":', contadorGuerra);
  console.log('ðŸ” PBL Ãºnicos en guerra:', guerraMap.size);
  
  const resultado = Array.from(guerraMap.values()).sort((a, b) => 
    parseFecha(b.fechaUltima) - parseFecha(a.fechaUltima)
  );
  
  console.log('ðŸ” PBL a mostrar:', resultado.length);
  
  return resultado;
}, [historico]);


  const totalPages = Math.max(1, Math.ceil(historico.length / ITEMS_PER_PAGE));

  // Ordenar historial por fecha descendente usando parseFecha
  const historicoSorted = useMemo(() => {
    return [...historico].sort((a, b) => parseFecha(b.Fecha) - parseFecha(a.Fecha));
  }, [historico]);

  const historicoPageSlice = historicoSorted.slice((page - 1) * ITEMS_PER_PAGE, page * ITEMS_PER_PAGE);

  const handlePrevPage = () => setPage(p => Math.max(p - 1, 1));
  const handleNextPage = () => setPage(p => Math.min(p + 1, totalPages));

  return (
    <aside className={`alert-panel${!isVisible ? ' hidden' : ''}`}>
      <div className="alert-panel-header">
        <h3 className="alert-panel-title">ðŸ“Š Alertas de Mercado</h3>
        <button className="alert-panel-close" onClick={onClose}>âœ•</button>
      </div>
      <div className="alert-tabs">
        <button
          className={`alert-tab ${activeTab === 'alertas' ? 'active' : ''}`}
          onClick={() => setActiveTab('alertas')}
        >
          Alertas Activas ({cantidadEstaciones})
        </button>
        <button
          className={`alert-tab ${activeTab === 'guerra' ? 'active' : ''}`}
          onClick={() => setActiveTab('guerra')}
        >
          âš¡ Guerra de Precios ({pblsEnGuerra.length})
        </button>
        <button
          className={`alert-tab ${activeTab === 'historico' ? 'active' : ''}`}
          onClick={() => setActiveTab('historico')}
        >
          HistÃ³rico ({historico.length})
        </button>
      </div>
      <div className="alert-panel-content">
        {loading ? (
          <div className="alert-loading">Cargando...</div>
        ) : activeTab === 'alertas' ? (
          <AlertasActivas agrupadas={agrupadas} />
        ) : activeTab === 'guerra' ? (
          <GuerraPreciosPanel data={pblsEnGuerra} />
        ) : (
          <>
            <HistoricoAlertas data={historicoPageSlice} />
            <div style={{ marginTop: 10, textAlign: 'center' }}>
              <button onClick={handlePrevPage} disabled={page === 1}>Prev</button>
              <span style={{ margin: '0 10px' }}>PÃ¡gina {page} de {totalPages}</span>
              <button onClick={handleNextPage} disabled={page === totalPages}>Siguiente</button>
            </div>
          </>
        )}
      </div>
    </aside>
  );
};

const AlertasActivas = ({ agrupadas }) => {
  const estaciones = Object.entries(agrupadas);
  if (estaciones.length === 0) return <div className="alert-empty">No hay alertas activas</div>;

  return (
    <div className="alerts-list">
      {estaciones.map(([station, alerts]) => {
        const anyWar = alerts.some(a => a.Guerra_Precio === 'Si');
        const stationAlert = alerts[0];
        return (
          <div key={station} className={`alert-card${anyWar ? ' guerra' : ''}`}>
            <div className="alert-card-header">
              <span className="alert-icon">{anyWar ? 'âš¡' : 'ðŸ”´'}</span>
              <div className="alert-card-info">
                <strong>{station}</strong>
                <span className="alert-marca">{stationAlert.marca} - {stationAlert.cod_cne}</span>
              </div>
            </div>
            <table className="alert-combs-table">
              <thead>
                <tr>
                  <th>Comb.</th>
                  <th>Actual</th>
                  <th>Anterior</th>
                  <th>Dif.</th>
                  <th>Hora</th>
                  <th>Tipo</th>
                </tr>
              </thead>
              <tbody>
                {alerts.map((a, i) => {
                  const dif = (a.Precio || 0) - (a.PrecioAnterior || 0);
                  return (
                    <tr key={i}>
                      <td>{a.Combustible}</td>
                      <td>${a.Precio}</td>
                      <td>${a.PrecioAnterior}</td>
                      <td className={dif < 0 ? 'negative' : 'positive'}>
                        {dif > 0 && "+"}{dif}
                      </td>
                      <td>{a.Horario}</td>
                      <td>{a.tipo_atencion}</td>
                    </tr>
                  );
                })}
              </tbody>
            </table>
            <div style={{ marginTop: '8px', color: '#999', fontSize: '12px' }}>
              {alerts.map(a =>
                <div key={a.Combustible}>
                  <span>Actual: {a.Fecha}, Anterior: {a.FechaAnterior}</span>
                  {a.Guerra_Precio === "Si" && <span style={{ marginLeft: 8, color: "#e67e22" }}>âš¡ GUERRA DE PRECIO</span>}
                </div>
              )}
            </div>
          </div>
        );
      })}
    </div>
  );
};

// âœ… NUEVO: Panel de Guerra de Precios
const GuerraPreciosPanel = ({ data }) => {
  console.log('ðŸ“Š GuerraPreciosPanel recibiÃ³:', data.length, 'estaciones');
  
  if (!Array.isArray(data) || data.length === 0) {
    return <div className="alert-empty">ðŸ“‹ No hay estaciones en guerra de precios</div>;
  }

  return (
    <div className="guerra-panel-wrapper">
      <div className="guerra-panel-header">
        <h4>âš¡ Estaciones con Guerra de Precios Activa</h4>
        <p className="guerra-subtitle">
          Total: {data.length} estaciones {data.length > 25 && `(mostrando todas)`}
        </p>
      </div>
      <div className="guerra-list">
        {data.map((item, idx) => {
          console.log(`Renderizando card ${idx + 1}/${data.length}:`, item.pbl, item.estacion);
          
          return (
            <div key={`guerra-${item.pbl}-${idx}`} className="guerra-card">
              <div className="guerra-card-header">
                <div className="guerra-badge">âš¡</div>
                <div className="guerra-info">
                  <strong className="guerra-estacion">{item.estacion}</strong>
                  <span className="guerra-marca">{item.marca}</span>
                </div>
                <div className="guerra-pbl">
                  <span className="pbl-label">PBL</span>
                  <span className="pbl-value">{item.pbl}</span>
                </div>
              </div>
              <div className="guerra-card-body">
                <div className="guerra-detail">
                  <span className="detail-label">CÃ³digo CNE:</span>
                  <span className="detail-value">{item.cod_cne}</span>
                </div>
                <div className="guerra-detail">
                  <span className="detail-label">Combustibles:</span>
                  <div className="combustibles-tags">
                    {Array.from(item.combustibles).map(comb => (
                      <span key={comb} className={`comb-tag comb-${comb.toLowerCase()}`}>
                        {comb}
                      </span>
                    ))}
                  </div>
                </div>
