  const [activeMercadoPbl, setActiveMercadoPbl] = useState(null);
  const competenciaMarkersRef = useRef([]);
  const polylineRef = useRef([]);
  const [debugTable, setDebugTable] = useState(null);
  const [maxHeight, setMaxHeight] = useState(500);
  const [activeTab, setActiveTab] = useState('precios');
  const [modoCorreccion, setModoCorreccion] = useState(null);

  const makeDraggable = useCallback((popup) => {
    if (!map || !popup || !popup._container) return;
    try {
      const pos = map.latLngToLayerPoint(popup.getLatLng());
      L.DomUtil.setPosition(popup._wrapper.parentNode, pos);
      const draggable = new L.Draggable(popup._container, popup._wrapper);
      draggable.enable();
    } catch (error) {
      console.error('Error draggable:', error);
    }
  }, [map]);

  const clearCompetenciaMarkers = useCallback(() => {
    competenciaMarkersRef.current.forEach(marker => {
      try {
        if (map && map.hasLayer(marker)) {
          map.removeLayer(marker);
        }
      } catch (e) {
        console.error('Error limpiando competencia:', e);
      }
    });
    competenciaMarkersRef.current = [];
  }, [map]);

  const clearPolylines = useCallback(() => {
    polylineRef.current.forEach(line => {
      try {
        if (map && map.hasLayer(line)) {
          map.removeLayer(line);
        }
      } catch (e) {
        console.error('Error limpiando polyline:', e);
      }
    });
    polylineRef.current = [];
  }, [map]);

  const clearGuerraMarkers = useCallback(() => {
    Object.values(guerraMarkersRef.current).forEach(marker => {
      try {
        if (map && map.hasLayer(marker)) {
          map.removeLayer(marker);
        }
      } catch (e) {
        console.error('Error limpiando guerra markers:', e);
      }
    });
    guerraMarkersRef.current = {};
  }, [map]);

  const generarTablaPrecios = useCallback((datos, variant = 'primary') => {
    if (!datos || datos.length === 0) {
      return '<div style="padding: 12px; text-align: center; font-size: 12px; color: #999; font-style: italic;">Sin datos disponibles</div>';
    }

    const variantClass = variant === 'primary' ? '' : 'secondary';

    let html = `<div class="popup-table-wrapper ${variantClass}">
      <table class="popup-table ${variantClass}">
        <thead>
          <tr>
            <th>Fecha</th>
            <th>G93</th>
            <th>G95</th>
            <th>G97</th>
            <th>Diesel</th>
            <th>Kero</th>
          </tr>
        </thead>
        <tbody>`;

    datos.forEach((fila) => {
      html += `
        <tr>
          <td>${fila.fecha}</td>
          <td>${fila.g93}</td>
          <td>${fila.g95}</td>
          <td>${fila.g97}</td>
          <td>${fila.diesel}</td>
          <td>${fila.kero}</td>
        </tr>`;
    });

    html += `
        </tbody>
      </table>
    </div>`;

    return html;
  }, []);

  const generarContenidoPopup = useCallback((marker) => {
    const tablaPreciosHtml = generarTablaPrecios(marker.precios_historicos || [], 'primary');

    const pbl = marker.pbl || marker.id;
    const eds = marker.eds || 'Sin nombre';
    const logoHtml = createBrandLogo(marker.Marca);

    const guerraActiva = marker.Guerra_Precio === 'Si';
    const guerraClass = guerraActiva ? 'guerra-active' : '';

    const content = `
    <div class="custom-popup-wrapper ${guerraClass}">
      <div class="popup-main-header">
        <div class="popup-logo-title">
          ${logoHtml}
          <div class="popup-title-text">
            <h3>${eds}</h3>
            <p class="popup-subtitle">PBL: ${pbl} | ${marker.Marca}</p>
          </div>
        </div>
        ${guerraActiva ? '<div class="guerra-badge">‚ö†Ô∏è Guerra de Precio</div>' : ''}
      </div>

      <div class="popup-region-info">
        <span><strong>Regi√≥n:</strong> ${marker.Region || '-'}</span>
        <span><strong>Comuna:</strong> ${marker.Comuna || '-'}</span>
      </div>

      <div class="popup-tabs-container">
        <div class="popup-tabs-header">
          <button class="popup-tab-button active" data-tab="precios">Precios</button>
          <button class="popup-tab-button" data-tab="coordenadas">Coordenadas</button>
          <button class="popup-tab-button" data-tab="informacion">Informaci√≥n</button>
        </div>

        <div class="popup-tabs-content">
          <div class="popup-tab-pane active" data-tab="precios">
            ${tablaPreciosHtml}
          </div>

          <div class="popup-tab-pane" data-tab="coordenadas">
            <div class="popup-info-box">
              <p><strong>Lat corregida:</strong> <span id="lat-actual-${marker.pbl || marker.id}">${marker.lat.toFixed(6)}</span></p>
              <p><strong>Lon corregida:</strong> <span id="lon-actual-${marker.pbl || marker.id}">${marker.lng.toFixed(6)}</span></p>
              <p><strong>Lat original:</strong> ${marker.lat_original ? marker.lat_original.toFixed(6) : 'N/A'}</p>
              <p><strong>Lon original:</strong> ${marker.lng_original ? marker.lng_original.toFixed(6) : 'N/A'}</p>
            </div>

            <div style="display: flex; gap: 8px; margin-top: 12px;">
              <button 
                style="flex: 1; padding: 8px; background: #007bff; color: white; border: none; border-radius: 4px; cursor: pointer; font-size: 11px; font-weight: 600;"
                onclick="window.activarModoCorreccion && window.activarModoCorreccion('${marker.pbl || marker.id}')"
              >
                Activar Correcci√≥n
              </button>
              
              <button 
                id="guardar-coord-${marker.pbl || marker.id}"
                style="flex: 1; padding: 8px; background: #28a745; color: white; border: none; border-radius: 4px; cursor: pointer; font-size: 11px; font-weight: 600; opacity: 0.5;"
                onclick="window.guardarCorreccionCoordenadas('${marker.pbl || ''}', '${marker.id || ''}', '${marker.eds}', '${marker.Marca}')"
                disabled
              >
                Guardar
              </button>
            </div>
          </div>

          <div class="popup-tab-pane" data-tab="informacion">
            <div class="popup-info-box">
              <p><strong>Direcci√≥n:</strong> ${marker.direccion || '-'}</p>
              <p><strong>Surtidores PES:</strong> ${marker.Surtidores_Autoservicio || '-'}</p>
              <p><strong>Posici√≥n Surtidor:</strong> ${marker.Posicion_Surtidor || '-'}</p>
              <p><strong>Tipo Isla:</strong> ${marker.Tipo_Isla || '-'}</p>
              ${marker.Guerra_Precio === 'Si' ? `<hr style="margin: 8px 0; border: none; border-top: 1px solid #dee2e6;"><p><strong style="color: #d9534f;">‚ö†Ô∏è Guerra de Precio Activa</strong></p>` : ''}
            </div>
          </div>
        </div>
      </div>
    </div>
  `;

  if (marker.pbl && (marker.Marca === 'Aramco' || marker.Marca === 'Petrobras')) {
    content += `<button class="popup-button-mercado" onclick="window.showAssociatedIds && window.showAssociatedIds(${marker.pbl}, ${marker.lat}, ${marker.lng}, '${marker.id}')">Mercado</button>`;
  }

  return content;
}, []);


  const updateIconsInViewport = useCallback((allMarkers, clearFirst = true) => {
    if (!map || updateInProgressRef.current) return;

    updateInProgressRef.current = true;
    const bounds = map.getBounds();
    const visibleMarkers = allMarkers.filter(marker => {
      return bounds.contains([marker.lat, marker.lng]);
    });

    if (clearFirst) {
      Object.values(markersRef.current).forEach(marker => {
        try {
          if (map.hasLayer(marker)) {
            map.removeLayer(marker);
          }
        } catch (error) {
          console.error('Error removiendo:', error);
        }
      });
      markersRef.current = {};
      clearGuerraMarkers();

      map.eachLayer((layer) => {
        if (layer instanceof L.Marker && !map.hasLayer(layer)) {
          try {
            map.removeLayer(layer);
          } catch (e) { }
        }
      });
    }

    if (visibleMarkers.length === 0) {
      updateInProgressRef.current = false;
      return;
    }

    const markersByBrand = {};
    visibleMarkers.forEach(marker => {
      if (!markersByBrand[marker.Marca]) {
        markersByBrand[marker.Marca] = [];
      }
      markersByBrand[marker.Marca].push(marker);
    });

    const brandOrder = ['Aramco', 'Petrobras', 'Copec', 'Shell', 'Blanco', 'Gulf', 'Petroprix'];
    let delayOffset = 0;
    const cacheKey = selectedRegion;
    let totalBrands = brandOrder.filter(b => markersByBrand[b]).length;
    let brandsProcessed = 0;

    brandOrder.forEach((brand) => {
      if (!markersByBrand[brand] || markersByBrand[brand].length === 0) return;

      const brandMarkers = markersByBrand[brand];

      setTimeout(() => {
        brandMarkers.forEach((marker) => {
          try {
            const markerId = `${cacheKey}-${marker.id}`;

            if (markersRef.current[markerId]) {
              return;
            }

            const lat = parseFloat(marker.lat);
            const lng = parseFloat(marker.lng);

            if (isNaN(lat) || isNaN(lng)) {
              console.error(`Coordenadas inv√°lidas para ${marker.eds}:`, marker);
              return;
            }

            const icon = createCustomIcon(marker.Marca, marker.Guerra_Precio);
            const leafletMarker = L.marker([lat, lng], { icon, draggable: false });

            const popupContent = generarContenidoPopup(marker);

            const popup = L.popup({
              maxWidth: 600,
              minWidth: 400,
              maxHeight: maxHeight,
              className: 'custom-popup',
              closeButton: true,
              autoClose: false,
              closeOnClick: false,
              autoPan: true
            }).setContent(popupContent);

            leafletMarker.bindPopup(popup);

            leafletMarker.on('popupopen', (e) => {
              const popup = e.popup;
              if (popup) {
                makeDraggable(popup);
                setTimeout(() => {
                  const popupContainer = popup._container;
                  const tabButtons = popupContainer.querySelectorAll('.popup-tab-button');
                  tabButtons.forEach(btn => {
                    btn.addEventListener('click', function (e) {
                      const tabName = this.getAttribute('data-tab');

                      popupContainer.querySelectorAll('.popup-tab-button').forEach(b => b.classList.remove('active'));
                      popupContainer.querySelectorAll('.popup-tab-pane').forEach(pane => {
                        pane.classList.remove('active');
                      });

                      this.classList.add('active');
                      popupContainer.querySelector(`.popup-tab-pane[data-tab="${tabName}"]`).classList.add('active');
                    });
                  });
                }, 0);
              }
            });

            leafletMarker.addTo(map);
            markersRef.current[markerId] = leafletMarker;

          } catch (error) {
            console.error(`Error creando marcador para ${marker.eds}:`, error);
          }
        });

        brandsProcessed++;
        if (brandsProcessed === totalBrands) {
          updateInProgressRef.current = false;
        }
      }, delayOffset);

      delayOffset += 50;
    });

  }, [map, selectedRegion, createCustomIcon, generarContenidoPopup, makeDraggable, maxHeight, clearGuerraMarkers]);

  const mostrarCompetencia = useCallback((markerPbl) => {
    clearCompetenciaMarkers();
    clearPolylines();

    const markerPrincipal = markers.find(m => m.pbl === markerPbl);
    if (!markerPrincipal) return;

    const competencia = baseCompData.filter(item => String(item.pbl).trim() === String(markerPbl).trim());

    if (competencia.length === 0) {
      console.log(`Sin competencia para PBL: ${markerPbl}`);
      return;
    }

    competencia.forEach(comp => {
      const lat = parseFloat(comp.lat_competencia);
      const lng = parseFloat(comp.lon_competencia);

      if (isNaN(lat) || isNaN(lng)) {
        console.error('Coordenadas inv√°lidas para competencia:', comp);
        return;
      }

      const icon = L.divIcon({
        className: 'competencia-marker',
        html: `
          <div style="
            background: #dc3545; 
            color: white; 
            padding: 8px 12px; 
            border-radius: 8px; 
            font-size: 11px; 
            font-weight: 600; 
            box-shadow: 0 2px 8px rgba(0,0,0,0.2);
            white-space: nowrap;
          ">
            ${comp.nombre_competencia || 'Competidor'}
          </div>
        `,
        iconSize: [100, 40],
        iconAnchor: [50, 20]
      });

      const compMarker = L.marker([lat, lng], { icon });
      compMarker.addTo(map);
      competenciaMarkersRef.current.push(compMarker);

      const latDist = markerPrincipal.lat - lat;
      const lngDist = markerPrincipal.lng - lng;
      const distanciaKm = Math.sqrt(latDist * latDist + lngDist * lngDist) * 111;

      const popupContent = `
        <div style="font-size: 11px; padding: 4px;">
          <strong>${comp.nombre_competencia || 'Sin nombre'}</strong><br>
          <strong>Marca:</strong> ${comp.marca_competencia || '-'}<br>
          <strong>Distancia:</strong> ${distanciaKm.toFixed(2)} km
        </div>
      `;

      compMarker.bindPopup(popupContent);

      const polyline = L.polyline(
        [[markerPrincipal.lat, markerPrincipal.lng], [lat, lng]],
        { color: 'red', weight: 2, opacity: 0.6, dashArray: '5, 10' }
      );
      polyline.addTo(map);
      polylineRef.current.push(polyline);
    });

  }, [markers, baseCompData, map, clearCompetenciaMarkers, clearPolylines]);

  const handleVerMercado = useCallback((pbl, lat, lng, id) => {
    console.log('üü¢ Llamando handleVerMercado con:', { pbl, lat, lng, id });

    if (showingAssociated && activeMercadoPbl === pbl) {
      console.log('‚ö†Ô∏è Cerrando vista de mercado');
      setShowingAssociated(false);
      setActiveMercadoPbl(null);
      setOriginalPopupMarker(null);

      clearGuerraMarkers();
      clearCompetenciaMarkers();
      clearPolylines();
      updateIconsInViewport(markers, false);
      return;
    }

    const originalMarker = markers.find(m => m.pbl === pbl);
    if (originalMarker) {
      setOriginalPopupMarker(originalMarker);
    }

    setShowingAssociated(true);
    setActiveMercadoPbl(pbl);

    console.log('üîµ markersForMercado disponible:', markersForMercado ? 'S√≠' : 'No');
    console.log('üîµ Cantidad de marcadores en markersForMercado:', markersForMercado ? markersForMercado.length : 0);

    if (markersForMercado && markersForMercado.length > 0) {
      const filteredByPbl = markersForMercado.filter(m => m.pbl === pbl);
      console.log(`üîç Marcadores encontrados para PBL ${pbl}:`, filteredByPbl.length);

      if (filteredByPbl.length > 0) {
        clearGuerraMarkers();

        filteredByPbl.forEach((marker) => {
          const guerraLat = parseFloat(marker.lat);
          const guerraLng = parseFloat(marker.lng);

          if (!isNaN(guerraLat) && !isNaN(guerraLng)) {
            const guerraIcon = L.divIcon({
              className: 'guerra-marker',
              html: `
                <div class="guerra-marker-content">
                  <span class="guerra-marker-nombre">${marker.Nombre_Guerra || 'Guerra'}</span>
                </div>
              `,
              iconSize: [120, 40],
              iconAnchor: [60, 20]
            });

            const guerraMarker = L.marker([guerraLat, guerraLng], { icon: guerraIcon });
            guerraMarker.addTo(map);

            const tablaPreciosHtml = generarTablaPrecios(marker.precios_historicos_guerra || [], 'secondary');

            const guerraPopupContent = `
              <div class="guerra-popup-wrapper">
                <div class="guerra-popup-header">
                  <h3>${marker.Nombre_Guerra || 'Estaci√≥n Guerra de Precio'}</h3>
                  <p class="guerra-popup-subtitle">Mercado: ${marker.Mercado || '-'} | PBL Base: ${marker.pbl || '-'}</p>
                </div>
                <div class="guerra-popup-body">
                  ${tablaPreciosHtml}
                </div>
              </div>
            `;

            const guerraPopup = L.popup({
              maxWidth: 550,
              minWidth: 350,
              maxHeight: maxHeight,
              className: 'custom-popup guerra-popup',
              closeButton: true,
              autoClose: false,
              closeOnClick: false,
              autoPan: true
            }).setContent(guerraPopupContent);

            guerraMarker.bindPopup(guerraPopup);

            guerraMarker.on('popupopen', (e) => {
              const popup = e.popup;
              if (popup) {
                makeDraggable(popup);
                setTimeout(() => {
                  const popupContainer = popup._container;
                  const tabButtons = popupContainer.querySelectorAll('.popup-tab-button');
                  tabButtons.forEach(btn => {
                    btn.addEventListener('click', function (e) {
                      const tabName = this.getAttribute('data-tab');

                      popupContainer.querySelectorAll('.popup-tab-button').forEach(b => b.classList.remove('active'));
                      popupContainer.querySelectorAll('.popup-tab-pane').forEach(pane => {
                        pane.classList.remove('active');
                      });

                      this.classList.add('active');
                      popupContainer.querySelector(`.popup-tab-pane[data-tab="${tabName}"]`).classList.add('active');
                    });
                  });
                }, 0);
              }
            });

            guerraMarkersRef.current[marker.id] = guerraMarker;
            console.log(`‚úÖ Marcador de guerra agregado: ${marker.Nombre_Guerra || marker.id}`);
          }
        });

        mostrarCompetencia(pbl);

        const bounds = L.latLngBounds([
          [lat, lng],
          ...filteredByPbl.map(m => [parseFloat(m.lat), parseFloat(m.lng)])
        ]);

        map.fitBounds(bounds, { padding: [50, 50] });
      }
    }
  }, [
    showingAssociated,
    activeMercadoPbl,
    markers,
    markersForMercado,
    map,
    clearGuerraMarkers,
    clearCompetenciaMarkers,
    clearPolylines,
    updateIconsInViewport,
    generarTablaPrecios,
    makeDraggable,
    maxHeight,
    mostrarCompetencia
  ]);

  useEffect(() => {
    if (typeof window !== 'undefined') {
      window.showAssociatedIds = handleVerMercado;
    }
  }, [handleVerMercado]);

  useEffect(() => {
    window.activarModoCorreccion = (pblOrId) => {
      console.log(`üîß Activando modo correcci√≥n para: ${pblOrId}`);
      setModoCorreccion(pblOrId);

      const markerId = Object.keys(markersRef.current).find(key => key.includes(pblOrId));
      if (!markerId) {
        console.error(`‚ùå No se encontr√≥ marcador para ${pblOrId}`);
        return;
      }

      const leafletMarker = markersRef.current[markerId];
      if (!leafletMarker) return;

      leafletMarker.dragging.enable();
      leafletMarker.setOpacity(0.6);

      const botonGuardar = document.getElementById(`guardar-coord-${pblOrId}`);
      if (botonGuardar) {
        botonGuardar.disabled = false;
        botonGuardar.style.opacity = '1';
      }

      leafletMarker.on('drag', (e) => {
        const newLatLng = e.target.getLatLng();
        const latDisplay = document.getElementById(`lat-actual-${pblOrId}`);
        const lonDisplay = document.getElementById(`lon-actual-${pblOrId}`);

        if (latDisplay) latDisplay.textContent = newLatLng.lat.toFixed(6);
        if (lonDisplay) lonDisplay.textContent = newLatLng.lng.toFixed(6);
      });
    };

    window.guardarCorreccionCoordenadas = async (pbl, id, eds, marca) => {
      try {
        const markerId = Object.keys(markersRef.current).find(key => key.includes(pbl || id));
        if (!markerId) {
          alert('‚ùå No se pudo encontrar el marcador');
          return;
        }

        const leafletMarker = markersRef.current[markerId];
        const newLatLng = leafletMarker.getLatLng();

        const resultado = await guardarCoordenadaCorregida({
          pbl: pbl || null,
          id: id || null,
          eds,
          marca,
          lat_corregida: newLatLng.lat,
          lon_corregida: newLatLng.lng
        });

        if (resultado.success) {
          alert(`‚úÖ Coordenadas guardadas correctamente para ${eds}`);

          leafletMarker.dragging.disable();
          leafletMarker.setOpacity(1);
          setModoCorreccion(null);

          const botonGuardar = document.getElementById(`guardar-coord-${pbl || id}`);
          if (botonGuardar) {
            botonGuardar.disabled = true;
            botonGuardar.style.opacity = '0.5';
          }
        } else {
          alert(`‚ùå Error al guardar: ${resultado.message}`);
        }
      } catch (error) {
        console.error('Error guardando coordenadas:', error);
        alert(`‚ùå Error: ${error.message}`);
      }
    };
  }, []);

  useEffect(() => {
    if (!map || !markers || markers.length === 0) return;

    clearGuerraMarkers();

    if (showingAssociated) {
      updateIconsInViewport([originalPopupMarker].filter(Boolean), false);
    } else {
      clearCompetenciaMarkers();
      clearPolylines();
      updateIconsInViewport(markers, true);
    }

    const handleZoomEnd = () => {
      if (!showingAssociated) {
        if (zoomUpdateRef.current) {
          clearTimeout(zoomUpdateRef.current);
        }
        zoomUpdateRef.current = setTimeout(() => {
          updateIconsInViewport(markers, false);
        }, 200);
      }
    };

    map.on('zoomend', handleZoomEnd);

    return () => {
      map.off('zoomend', handleZoomEnd);
      if (zoomUpdateRef.current) {
        clearTimeout(zoomUpdateRef.current);
      }
    };
  }, [markers, map, showingAssociated, originalPopupMarker, updateIconsInViewport, clearGuerraMarkers, clearCompetenciaMarkers, clearPolylines]);

  return debugTable;
};

export default IconMarkersLayer;
