import React, { useMemo, useState } from 'react';

const FilterPanel = ({ markers, onFiltersChange }) => {
  // Estados de filtros
  const [selectedComuna, setSelectedComuna] = useState('');
  const [selectedMarca, setSelectedMarca] = useState('');
  const [selectedJefeZona, setSelectedJefeZona] = useState('');
  const [selectedEds, setSelectedEds] = useState('');
  const [edsSearchTerm, setEdsSearchTerm] = useState('');

  // Obtener datos √∫nicos
  const comunas = useMemo(() => {
    return [...new Set(markers.map(m => m.Comuna))].sort();
  }, [markers]);

  const marcas = useMemo(() => {
    return [...new Set(markers.map(m => m.Marca))].sort();
  }, [markers]);

  const jefesZona = useMemo(() => {
    return [...new Set(
      markers
        .filter(m => m.nombre)
        .map(m => m.nombre)
    )].sort();
  }, [markers]);

  const allEds = useMemo(() => {
    return [...new Set(
      markers
        .filter(m => {
          const comunaMatch = !selectedComuna || m.Comuna === selectedComuna;
          const marcaMatch = !selectedMarca || m.Marca === selectedMarca;
          const jefeMatch = !selectedJefeZona || m.nombre === selectedJefeZona;
          return comunaMatch && marcaMatch && jefeMatch;
        })
        .map(m => m.eds)
        .filter(eds => eds)
    )].sort();
  }, [markers, selectedComuna, selectedMarca, selectedJefeZona]);

  const filteredEds = useMemo(() => {
    return allEds.filter(eds =>
      eds.toLowerCase().includes(edsSearchTerm.toLowerCase())
    );
  }, [allEds, edsSearchTerm]);

  // Filtrar marcadores seg√∫n filtros
  const filteredMarkers = useMemo(() => {
    return markers.filter(m => {
      const comunaMatch = !selectedComuna || m.Comuna === selectedComuna;
      const marcaMatch = !selectedMarca || m.Marca === selectedMarca;
      const jefeMatch = !selectedJefeZona || m.nombre === selectedJefeZona;
      const edsMatch = !selectedEds || m.eds === selectedEds;
      return comunaMatch && marcaMatch && jefeMatch && edsMatch;
    });
  }, [markers, selectedComuna, selectedMarca, selectedJefeZona, selectedEds]);

  // Notificar cambios al padre
  React.useEffect(() => {
    onFiltersChange(filteredMarkers);
  }, [filteredMarkers, onFiltersChange]);

  // Calcular estad√≠sticas
  const markersByBrand = useMemo(() => {
    const result = {};
    filteredMarkers.forEach(m => {
      result[m.Marca] = (result[m.Marca] || 0) + 1;
    });
    return result;
  }, [filteredMarkers]);

  const handleClearFilters = () => {
    setSelectedComuna('');
    setSelectedMarca('');
    setSelectedJefeZona('');
    setSelectedEds('');
    setEdsSearchTerm('');
  };

  return (
    <div className="filter-panel">
      <h3 className="filter-title">üìä Estad√≠sticas</h3>
      <div className="stats-mini">
        <div className="stat-item">
          <span className="stat-label">Estaciones</span>
          <span className="stat-value">{filteredMarkers.length}</span>
        </div>
        <div className="stat-item">
          <span className="stat-label">Marcas</span>
          <span className="stat-value">{Object.keys(markersByBrand).length}</span>
        </div>
      </div>

      <h3 className="filter-title">üè¢ Por Marca</h3>
      <div className="brands-mini">
        {Object.entries(markersByBrand)
          .sort((a, b) => b[1] - a[1])
          .map(([brand, count]) => (
            <div key={brand} className="brand-mini">
              <span>{brand}</span>
              <span className="count">{count}</span>
            </div>
          ))}
      </div>

      {/* Filtros */}
      <h3 className="filter-title">üîç Filtros Avanzados</h3>

      {/* Comuna */}
      <div className="filter-item">
        <label>Comuna</label>
        <select
          value={selectedComuna}
          onChange={(e) => {
            setSelectedComuna(e.target.value);
            setSelectedEds('');
            setEdsSearchTerm('');
          }}
        >
          <option value="">Todas</option>
          {comunas.map(c => (
            <option key={c} value={c}>{c}</option>
          ))}
        </select>
      </div>

      {/* Marca */}
      <div className="filter-item">
        <label>Marca</label>
        <select
          value={selectedMarca}
          onChange={(e) => setSelectedMarca(e.target.value)}
        >
          <option value="">Todas</option>
          {marcas.map(m => (
            <option key={m} value={m}>{m}</option>
          ))}
        </select>
      </div>

      {/* Jefe de Zona */}
      <div className="filter-item">
        <label>Jefe de Zona</label>
        <select
          value={selectedJefeZona}
          onChange={(e) => {
            setSelectedJefeZona(e.target.value);
            setSelectedEds('');
            setEdsSearchTerm('');
          }}
        >
          <option value="">Todos</option>
          {jefesZona.map(jefe => (
            <option key={jefe} value={jefe}>{jefe}</option>
          ))}
        </select>
      </div>

      {/* EDS Search */}
      <div className="filter-item">
        <label>Buscar EDS</label>
        <input
          type="text"
          placeholder="Escribe para buscar..."
          value={edsSearchTerm}
          onChange={(e) => {
            setEdsSearchTerm(e.target.value);
            setSelectedEds('');
          }}
        />
        
        {edsSearchTerm && filteredEds.length > 0 && (
          <div className="eds-dropdown">
            {filteredEds.slice(0, 8).map(eds => (
              <div
                key={eds}
                className="eds-option"
                onClick={() => {
                  setSelectedEds(eds);
                  setEdsSearchTerm('');
                }}
              >
                {eds}
              </div>
            ))}
          </div>
        )}

        {selectedEds && (
          <div className="selected-eds">
            <span>‚úì {selectedEds}</span>
            <button
              onClick={() => {
                setSelectedEds('');
                setEdsSearchTerm('');
              }}
            >
              ‚úï
            </button>
          </div>
        )}
      </div>

      {/* Bot√≥n Limpiar */}
      <button className="btn-clear" onClick={handleClearFilters}>
        üîÑ Limpiar Filtros
      </button>
    </div>
  );
};

export default FilterPanel;
